css:
@-moz-document domain("hepsiburada.com") {
body,
.header-wrapper,
.global-header__content, 
.navigation,
.navigationWrapper,
.voltran-container.home .navigationWrapper,
.new-voltran-body,
.footer-global,
.minimal-layout .container {
    background: #333 !important;
}

.login-box.box, .input {
    background: #444 !important;
}

.control-label {
    background: transparent !important;
}

.voltran-container .navigation-wrapper ul.menu li.menu-main-item {
    border-top-color: #333;
    border-bottom-color: #333;
}

.minimal-footer-wrapper {
    border-top-color: #555 !important;
}

.with-hover .not-fashion-flex {
    border-bottom-color: #555 !important;
}

.filter-box-container,
.CategoryTree.widget, .box-container, .dictionary, .dictionaryDetail, .related-searched-keywords,
.BannerHorizontal4Column.widget>.banner-container,
#productresults {
    border-color: #555 !important;
}

.filter-content .page-options .title-wrapper,
.box-container,
.owl-carousel .owl-stage-outer,
.last-price,
.BannerHorizontal4Column.widget>.banner-container .box.product,
#productresults,
.SearchListing.widget.desktop .search-item,
.box.product .info,
.CategoryTree.widget, .box-container, .dictionary, .dictionaryDetail, .related-searched-keywords{
    background: #000 !important;
}

.voltran-container .navigation-wrapper ul.menu li.menu-main-item > a,
.voltran-body,
.footer-global,
.footer-head a,
.footer-top span.title,
.footer-top span.content,
.footer-global a,
.account__info__name,
.nav__item .item__link,
.main__container__title h1,
.main__container__head .nav__item--active,
.filter-content .page-options .title-wrapper .search-results-title,
.FilterResultCount.widget.desktop .totalItems,
.categoriesCaption,
.CategorySelector.widget.desktop .categories .main-item a,
.filters-container .box-container h6,
.filters-container .box-container label span.filter-text,
.filters-container .box-container label,
.DealOfTheDay.widget.desktop .overlaping-carousel a,
.box.product .title,
.last-price .price-value{
    color: #FFF !important;
}

.voltran-container .navigation-wrapper ul.menu li.menu-main-item:hover>a {
    color: #000 !important;
}

.nav__item__dropdown li a:not(.item__link--active),
.main__container__head .nav__item:not(:first-child),
.order-block__date {
    color: #ddd !important;
}

.voltran-body b {
    color: rgb(237, 67, 62) !important; 
}

.HomePageTopBanner,
.footer-end-wrapper {
    display: none;
}

.owl-carousel .owl-item .owl-lazy {
    transition: 0s all !important;
}
}
