css:
@-moz-document domain(mokum.place) {
    body {
        background: rgb(21, 32, 43);
        color: #fff;
        font-family: 'Ubuntu', sans-serif !important;
    }

    .bem-signin-block_header {
        float: left;
    }
    .bem-post__text {
        color: #eee;
    }
    .box-body {
        background: rgb(21, 32, 43) !important;
    }
    .bem-post {
        background: rgb(21, 32, 43);
        font-family: 'Ubuntu', sans-serif !important;
    }
    .bem-post:hover {
        background: rgb(26, 40, 53);
    }
    .bem-page-post {
        border-bottom: 1px solid #555;
    }
    .bem-post__author-name {
        font-family: 'Ubuntu', sans-serif !important;
        color: #fff;
        font-weight: 700;
    }
    .bem-sidebar-userinfo__username a:hover {
        color: rgba(29, 161, 242, 1.00);
    }
    .bem-post__author-name:hover {
        color: rgba(29, 161, 242, 1.00);
    }
    .bem-post__comment-text {
        color: #eee;
    }
    .bem-post__likes-list {
        color: #9e9e9e;
    }
    a {
        color: rgba(29, 161, 242, 1.00);
    }
    a:hover {
        color: rgba(29, 161, 242, 1.00);
        text-decoration: underline;
    }
    a:visited {
        color: rgba(29, 161, 242, 1.00);
    }
    a:active {
        color: rgba(29, 161, 242, 1.00)
    }
    .bem-post__posted-to_liked a {
        color: #ddd;
    }
    .bem-post__button {
        padding: 0 3px;
        color: #eee;
    }
    .bem-sidebar-menu__items a {
        color: #eee;
        text-transform: capitalize;
        font-weight: 700;
    }
    .bem-sidebar-userinfo__display-name {
        color: #999;
    }
    .bem-sidebar-menu__title {
        color: rgba(29, 161, 242, 1.00);

    }
    .bem-sidebar-menu__show-button {
        color: #fff;
        margin-right: 4px;
        padding: 2;
        text-transform: capitalize;
    }
    .Select-control {
        background: rgb(21, 32, 43);
    }
    .value-label {
        color: #afaca7;
    }
    .bem-posting-form__pane {
        color: #afaca7;
    }
    .box-header-timeline {
        color: #8a8a8a !important
    }
    .btn-default {
        background: rgba(29, 161, 242, 1.00);
        color: #ddd
    }
    .search-input {
        color: #eee;
    }
    .bem-mokum-logo__link {
        color: #8a8a8a;
        font-size: 30px;
        text-transform: lowercase;
    }
    .bem-mokum-logo__link:hover {
        color: #ddd;
    }
    .bem-mokum-logo__logo > a:nth-child(1) > small:nth-child(1) {
        display: none;
    }
    .bem-page_logo-row {
	position:sticky;
        top: 0;
        z-index: 10;
        background-color: rgb(21, 32, 43);
        border-bottom: 1px solid #dddf;
    }
    .profile {
        color: #999;
    }
    .profile a {
        color: #fff;
    }
    .sentence {
        color: #999;
    }
    h2 {
        color: #777;
    }
    .box-footer {
        background: #555;
    }
    .tile-list a:hover {
        color: #ddd;
    }
    .bootstrap-switch-label {
        background: rgb(25, 38, 51) !important;
    }
    .bootstrap-switch-handle-on.bootstrap-switch-primary {
        background-color: rgb(25, 38, 51) !important;
    }
    .bem-sidebar .bem-live-selector .bootstrap-switch-handle-on {
        color: #ddd;
    }
    .bem-post__likes-counter-icon_liked {
        color: #b38e40;
    }
    .content .box-footer {
        background-color: rgb(21, 32, 43);
    }
    .bem-page__page-footer .bem-page-footer {
        color: #ddd !important;
    }
    .bem-post__posted-to_feeds {
        color: #9f9f9f;
    }
    .table-striped > tbody > tr:nth-of-type(2n+1) {
        background-color: rgb(7, 45, 68);
    }
    .bg-warning {
        background-color: #2d2d2d;
    }
    .bem-post__posted-to_direct-to {
        color: #e33535;
    }
    .bem-central-block_title {
        color: #ddd;
    }
    .bem-mokum__link-button_blue {
        color: #ddd;
    }
    .bem-page_central-block {
        background: rgb(21, 32, 43);
    }
    .bem-central-block__title {
        color: rgb(21, 32, 43);
    }
    i.fa {
        color: rgba(29, 161, 242, 1.00);
    }
    i.fa.fa-asterisk {
        color: #fff;
    }
    label {
        color: #fff;
    }
    select {
        background: rgb(21, 32, 43);
        color: #fff;
    }
    .modal-content {
        color: #333;
        border: 1px solid #fff;
        border-radius: 3px;
    }
    .form-control {
        color: #999;
    }
    .fa-arrow-circle-o-up:before {
        color: skyblue;
    }
    button.bem-post__button:hover {
        text-decoration: underline;
    }
    ul.dropdown-menu {
        background: rgb(21, 32, 43);
    }
    @media screen and (max-width: 600px) {
        .row {
            display: block;
        }
        Mokum div.row:nth-child(2) {
            display: block;
        }
        .bem-phone-navigation__button {
            color: #e89f22;
        }
        .bem-post__button {
            padding: 2px;
        }
    }
}
