css:
@-moz-document url-prefix("https://prodota.ru") {
  *,
  *::after,
  ::before {
    box-sizing: border-box;
    padding: 0;
    margin: 0;
  }
  .ipsApp {
    background-color: #bdbdbd;
    line-height: 1.5;
    font-size: 12px;
    color: #212121;
  }
  ul {
    list-style: none;
  }
  a {
    transition: color 0.2s ease;
    color: #b71c1c;
  }
  a:hover,
  a:focus {
    color: #1976d2;
  }
  a.ipsType_blendLinks:not(.ipsType_noUnderline):hover,
  .ipsType_blendLinks:not(.ipsType_noUnderline)
    a:not(.ipsType_noUnderline):hover {
    color: #1976d2;
  }
  a.cke_button {
    padding: 0px 8px;
  }
  a[data-mentionid] {
    background: #1976d2;
    border-radius: 2px;
    padding: 0px 4px;
    font-size: 0.9em;
  }
  a[data-mentionid]:hover {
    color: #fff;
  }
  .ipsType_medium[data-excludequote] {
    margin-top: 4px;
    display: block;
  }
  .ipsType_medium[data-excludequote] strong {
    font-weight: 400;
    font-size: 12px;
  }
  .cSubscriptionsHeader {
    min-height: 100px;
  }
  .ipsAreaBackground_dark {
    background: #424242;
    color: #fff;
  }
  .ipsBadge_positive,
  .ipsBadge_style4 {
    background: #388e3c;
  }
  .ipsBadge.ipsBadge_icon {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
    border-radius: 50%;
    line-height: 24px;
    height: 24px;
    width: 24px;
  }
  .ipsType_veryLarge {
    font-size: 32px;
    line-height: 1.5;
    font-weight: 300;
    color: #212121;
  }
  html[dir="ltr"] .ipsContained_container > span:not(.ipsHide) {
    padding-right: 4px;
  }
  .ipsComment[data-role="commentContent"] {
    margin-bottom: 24px;
  }
  .ipsComment_subComments .ipsComment {
    margin-bottom: 4px;
    padding: 8px;
  }
  .ipsPageHeader + .ipsClearfix + .cTopic {
    margin-top: 0;
  }
  .ipsPageHeader + .ipsClearfix + .ipsBox + br {
    display: none;
  }
  .ipsApp .ipsPageHeader + .ipsSpacer_top {
    margin-top: 0;
  }
  .ipsButton a:hover,
  .ipsButton a:focus {
    color: inherit;
  }
  html[dir="ltr"] .ipsMessage {
    padding: 16px 16px 16px 36px;
  }
  .ipsMessage {
    margin-bottom: 8px;
  }
  html[dir="ltr"] .ipsMessage:before {
    left: 16px;
  }
  .ipsMessage:before {
    line-height: 19px;
    font-size: 16px;
    top: 16px;
  }
  .cAnnouncementContentTop.ipsMessage {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
    margin-bottom: 16px;
  }
  .ipsType_sectionTitle.custom_poll_opacity {
    opacity: 1 !important;
    box-shadow: none;
  }
  .ipsType_sectionTitle.custom_poll_opacity[style*="opacity: 1"] {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
  }
  .ipsMessage_info,
  .ipsMessage_information {
    background-color: #1976d2;
  }
  .custom_poll_opacity > .ipsContained {
    justify-content: space-between;
    align-items: center;
    display: flex;
  }
  #elFlashMessage {
    background: rgba(0, 0, 0, 0.9);
    border-radius: 2px;
    font-size: 14px;
  }
  #elFlashMessage.cPostFlash {
    height: 50px;
  }
  #elFlashMessage.ipsFlashMsg_dismissable[data-action="dismissFlashMessage"] {
    border-radius: 50%;
  }
  #elStatusSubmit > .ipsPad {
    padding: 0;
  }
  #elStatusSubmit > ul.ipsToolList {
    padding: 8px 0;
  }
  .ipsType_pageTitle {
    line-height: 1.5;
    font-size: 24px;
    color: #212121;
  }
  .ipsType_pageTitle + br + br {
    display: none;
  }
  .ipsType_pageTitle span[data-role="editableTitle"]:hover,
  .ipsType_pageTitle span[data-role="editableTitle"]:focus,
  .ipsType_pageTitle[data-role="editableTitle"]:hover,
  .ipsType_pageTitle[data-role="editableTitle"]:focus,
  .ipsType_editable:hover {
    background: transparent;
    color: #212121;
  }
  .ipsApp .ipsSpacer_bottom.ipsSpacer_double,
  .ipsApp .ipsSpacer_both.ipsSpacer_double {
    margin-bottom: 16px;
  }
  .ipsStreamItem_time {
    background: #212121;
    border-radius: 12px;
    font-size: 12px;
    padding: 0 16px;
  }
  .cStreamFilter {
    font-size: 12px;
    padding: 8px;
  }
  .cStreamFilter > li > a {
    border-right: 1px solid #bdbdbd;
    padding: 0 8px;
  }
  .cStreamFilter p em {
    color: #757575;
    opacity: 1;
  }
  .cStreamFilter a {
    color: #212121;
  }
  .cStreamFilter > li > a.cStreamFilter_active {
    background: #212121;
    margin: -8px 0;
    padding: 8px;
  }
  html[dir="ltr"] .ipsButtonBar .ipsButtonRow {
    margin-left: 8px;
  }
  .ipsMenu_itemCount,
  .ipsSideMenu_itemCount {
    background: #eee;
    font-size: 12px;
    padding: 0 4px;
    color: #616161;
  }
  .cMessage .ipsType_medium {
    font-size: 12px;
  }
  .ipsApp .cMessage .ipsSpacer_top.ipsCommentCount {
    margin-top: 0;
  }
  .cMessage_members {
    margin-top: 8px;
  }
  .ipsApp .ipsButton:not(.ipsButton_noHover):hover {
    background-image: none;
  }
  .ipsEmpty {
    padding: 32px 0 0;
    font-size: 16px;
    color: #212121;
  }
  .ipsEmpty i {
    margin-bottom: 8px;
    font-size: 100px;
  }
  html[dir="ltr"] #elMessageViewer {
    padding-left: 0;
  }
  html[dir="ltr"] .cMessage_members > ol > li {
    border-right: 1px solid #bdbdbd;
    margin: 8px 16px 8px 0;
    padding-right: 16px;
  }
  html[dir="ltr"] .ipsPhotoPanel.ipsPhotoPanel_tiny > div {
    margin-left: 46px;
  }
  #elMessageFolders_menu {
    font-size: 12px;
  }
  #elMessageFolders_menu > .ipsPad_half {
    padding: 8px 16px;
  }
  #elMessageSidebar .ipsButtonBar .ipsButtonRow {
    margin-left: 0;
  }
  #elMessageSidebar + .ipsType_medium {
    padding: 8px;
  }
  #elMessageSearch {
    border-top: 1px solid #bdbdbd;
  }
  #elStreamTimePeriod_menu li:nth-child(5) {
    padding: 0 16px 8px;
  }
  #elStreamFilterForm ul[data-role="saveButtonContainer"] {
    border-top: 1px solid #bdbdbd;
    margin-top: 0;
  }
  #elStreamShowMe_menu .ipsHr {
    margin: 8px 0;
  }
  #elStreamShowMe_menu li:nth-child(4) {
    padding: 0 16px;
  }
  #elStreamShowMe_menu .ipsFieldRow_fullWidth .ipsType_small {
    margin-top: 4px;
  }
  #elSingleStatusUpdate {
    padding: 0;
  }
  #elProfileActivityOverview .ipsStreamItem,
  #elUserContent .ipsStreamItem,
  #elSingleStatusUpdate .ipsStreamItem {
    border-bottom: 1px solid #bdbdbd;
    padding: 12px 16px;
  }
  #elSingleStatusUpdate .ipsStreamItem {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
  }
  .avatar_indicator_top_left {
    left: -2px;
    top: -2px;
  }
  .avatar_indicator_online_color_top_left {
    border-left-color: #388e3c;
    border-top-color: #388e3c;
  }
  .avatar_indicator_offline_color_top_left {
    display: none;
  }
  .ipsCode {
    border-left: 4px solid #bdbdbd !important;
    padding: 12px !important;
    background: #eee;
    font-size: 12px;
  }
  html[dir="ltr"] .cTopicList .ipsDataItem_icon {
    min-width: 37px;
  }
  html[dir="ltr"]
    .ipsStream:not(.ipsStream_withTimeline)
    .ipsStreamItem_contentType {
    left: 8px;
  }
  html[dir] .ipsStream:not(.ipsStream_withTimeline) .ipsStreamItem_contentType {
    border-radius: 50%;
    top: 40px;
  }
  .ipsStreamItem_condensed.ipsStreamItem.ipsStreamItem_contentBlock {
    padding: 12px;
    line-height: 1.5;
    margin: 8px 0 8px 30px;
  }
  .ipsList_inline {
    flex-flow: row wrap;
    display: flex;
  }
  .ipsList_inline.ipsType_center {
    justify-content: center;
  }
  .ipsList_inline.ipsList_csv {
    display: block;
  }
  html[dir="ltr"] .ipsStreamItem_condensed .ipsPhotoPanel_mini > div {
    margin-left: 42px;
    margin-right: 200px;
  }
  html[dir="ltr"] .ipsStreamItem.ipsStreamItem_condensed .ipsStreamItem_stats {
    border-left: 1px solid #bdbdbd;
    padding-left: 16px;
    font-size: 12px;
  }
  .ipsStreamItem.ipsStreamItem_contentBlock {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
    border: none;
    padding: 16px;
    border-radius: 2px;
  }
  .ipsTable.ipsMatrix .ipsMatrix_subHeader {
    background: #f5f5f5;
    color: #212121;
    padding: 16px;
  }
  .ipsTable.ipsMatrix td {
    padding: 16px;
  }
  .ipsTable.ipsMatrix thead th {
    background: #212121;
  }
  .ipsTable th {
    padding: 16px 16px 16px 8px;
    background: #f5f5f5;
    font-size: 12px;
  }
  .ipsFieldRow_checkbox {
    margin-bottom: 4px;
  }
  .ipsFieldRow_desc {
    margin-top: 4px;
    color: #757575;
  }
  .ipsFieldRow_checkbox input[type="checkbox"],
  .ipsFieldRow_checkbox input[type="radio"],
  .ipsFieldRow_checkbox .ipsCustomInput {
    margin-top: 4px;
  }
  .ipsCustomInput,
  .ipsCustomInput > span {
    height: 16px;
    width: 16px;
  }
  .ipsCustomInput input {
    height: 16px;
    width: 16px;
  }
  .ipsCustomInput input[type="checkbox"]:checked + span:after {
    line-height: 16px;
    font-size: 12px;
    color: #1976d2;
  }
  .ipsCustomInput input[type="radio"]:checked + span:after {
    width: 6px;
    height: 6px;
    top: 4px;
    left: 4px;
    border-radius: 50%;
    background: #1976d2;
  }
  .ipsCustomInput input:checked + span {
    border-color: #1976d2;
  }
  .cNotificationBox .ipsPhotoPanel {
    padding: 8px 16px;
  }
  .ipsCommentCount {
    background: #212121;
    border-radius: 2px;
    margin-bottom: 4px;
    line-height: 1.5;
    padding: 2px 8px;
    font-size: 12px;
  }
  .ipsDataItem_unread {
    background-color: #f5f5f5;
  }
  .ipsDataItem_unread .ipsDataItem_title {
    font-weight: bold;
  }
  .custom_poll_opacity .custom_arrow {
    display: inline-flex;
    margin-left: 4px;
    font-weight: 400;
    height: 16px;
    width: 16px;
    opacity: 1;
  }
  .custom_poll_opacity .custom_arrow::after {
    font-size: 14px;
    line-height: 1;
  }
  .cPollVoteBar {
    background: #e0e0e0;
    border-radius: 2px;
    display: block;
    height: 20px;
  }
  .cPollVoteBar > span {
    background: #2962ff;
    border-radius: 2px;
    position: relative;
    display: block;
    height: 20px;
  }
  .cPollList > li {
    flex-flow: column nowrap;
    display: flex;
  }
  .cPollVoteBar > span:after {
    color: #424242;
  }
  .cPollList .ipsType_sectionHead {
    margin-bottom: 16px;
  }
  .cPollList_choices > li {
    align-items: center;
    margin-bottom: 8px;
    font-size: 12px;
    display: flex;
  }
  .cPollList_choices > li *[class*="ipsGrid_span"] {
    min-height: 0;
  }
  .cPollList > li:not(:last-child) {
    border-bottom: 1px solid #bdbdbd;
    padding-bottom: 16px;
    margin-bottom: 16px;
  }
  html[dir="ltr"] .cPollVoteBar > span.cPollVoteBar_inside:after {
    line-height: 20px;
  }
  html[dir="ltr"] .ipsCommentCount {
    margin-left: 8px;
  }
  html[dir="ltr"] .ipsCommentCount:after {
    right: 100%;
    border-color: transparent #212121 transparent transparent;
  }
  .ipsApp .ipsFollow.ipsButton.ipsButton_verySmall,
  .ipsApp .ipsFollow.ipsButton.ipsButton_verySmall:hover {
    background-color: #212121;
    align-items: center;
    padding: 6px 12px;
    display: flex;
    border: none;
    color: #fff;
  }
  .ipsApp .ipsSpacer_bottom.ipsSpacer_half,
  .ipsApp .ipsSpacer_both.ipsSpacer_half {
    margin-bottom: 8px;
  }
  .ipsType_medium:not(.ipsType_richText) {
    line-height: 1.5;
  }
  .ipsType_medium {
    font-size: 14px;
  }
  .ipsType_small {
    font-size: 12px;
  }
  .ipsType_light {
    color: #616161;
  }
  .ipsPad_double,
  .ipsApp ul.ipsPad_double,
  .ipsApp ol.ipsPad_double {
    padding: 24px;
  }
  .ipsApp .ipsSpacer_top.ipsSpacer_double,
  .ipsApp .ipsSpacer_both.ipsSpacer_double {
    margin-top: 24px;
  }
  .cSearchMainBar .ipsType_huge.fa {
    margin: 3px 8px 3px 0;
    font-size: 32px;
  }
  .ipsApp .ipsField_primary,
  .ipsApp .ipsField_primary.ipsField_autocomplete input,
  .ipsApp .ipsFieldRow_primary input {
    font-size: 16px;
  }
  html[dir="ltr"] .cSearchMainBar.ipsPhotoPanel.ipsPhotoPanel_mini > div {
    margin-left: 42px;
  }
  .cStaffDirectory_blocks .ipsUserPhoto {
    width: 54px;
    display: block;
    margin: 0 auto 8px auto;
  }
  #ipsLayout_header {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
    border-bottom: none;
  }
  #ipsLayout_sidebar
    li.ipsWidget.ipsWidget_vertical[data-blockid="app_core_newsletter_newsletter"]
    .ipsWidget_title {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
  }
  [data-blockid="app_core_newsletter_newsletter"]
    .ipsWidget_inner
    .ipsType_veryLarge {
    padding: 0 16px;
    line-height: 1;
  }
  [data-blockid="app_core_newsletter_newsletter"]
    .ipsWidget_inner
    .ipsButton_important {
    margin-top: 16px;
  }
  #elLogo img {
    margin: 14px 0 0;
    width: 80px;
  }
  .ipsNavBar_primary {
    padding-bottom: 34px;
    top: -34px;
  }
  html[dir="ltr"] .ipsNavBar_primary > ul > li > a {
    margin-right: 8px;
  }
  .ipsBox[data-controller="core.front.profile.main"] {
    box-shadow: none;
  }
  .ipsBox {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
    border-radius: 2px;
    border: none;
  }
  .ipsComment {
    margin-bottom: 16px;
  }
  .ipsComposeArea[data-ipsEditor] + [data-ipsEditor-toolList] {
    margin-bottom: 4px;
    margin-top: 8px;
    padding: 0 12px;
  }
  .ipsComposeArea_dropZone {
    padding: 16px;
    border: none;
  }
  .ipsAreaBackground_light {
    background: #eee;
  }
  .ipsComposeArea_attachments {
    border-top: 1px solid #bdbdbd;
  }
  html[dir="ltr"] .ipsFieldRow_inlineCheckbox .ipsCustomInput,
  html[dir="ltr"] .ipsToggle {
    margin-right: 4px;
  }
  .ipsToggle.ipsToggle_on {
    background: #388e3c;
  }
  .ipsComposeArea_editor {
    border-radius: 2px;
    background: #eee;
  }
  html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor:before {
    border-color: transparent #eee transparent transparent;
  }
  html[dir="ltr"] #elEditProfile {
    right: 8px;
  }
  #elEditProfile {
    top: 8px;
  }
  html[dir="ltr"] .cProfileHeader_name {
    margin-right: 16px;
    padding: 0;
  }
  .ipsPageHead_barText,
  .ipsPageHead_barText_small {
    background: rgba(0, 0, 0, 0.75);
    padding: 6px 12px;
  }
  #elProfileStats {
    padding: 8px 12px 8px 155px;
    background: #212121;
    font-size: 14px;
    height: auto;
  }
  #elProfileInfoColumn {
    background-color: transparent;
  }
  #elProfileInfoColumn + .ipsColumn {
    padding: 16px 16px 0 0;
  }
  #elProfileTabs {
    background-color: transparent;
    padding: 0;
  }
  #elProfileTabs_content {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
  }
  #elFollowers > .ipsPad_half {
    border-top: 1px solid #bdbdbd;
    padding: 8px 16px;
  }
  #ipsLayout_mainArea .ipsBox[data-controller="core.front.profile.main"] {
    background: transparent;
  }
  #modcp_menu {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
  }
  .ipsComment:not(.ipsModerated) .ipsComment_header {
    background: #f5f5f5;
    border: none;
  }
  .ipsItemStatus.ipsItemStatus_read:not(.ipsItemStatus_large) {
    color: #bdbdbd;
  }
  .ipsStreamItem_snippet {
    margin: 12px 0;
  }
  .ipsButtonBar {
    border-bottom: 1px solid #bdbdbd;
    background-color: #e0e0e0;
    box-shadow: none;
  }
  .ipsStreamItem_title {
    line-height: 1.5;
    font-size: 18px;
    color: #212121;
  }
  .ipsSideMenu_item:not(.ipsSideMenu_itemActive) a:hover,
  a.ipsSideMenu_item:not(.ipsSideMenu_itemActive):hover,
  span.ipsSideMenu_item:not(.ipsSideMenu_itemActive):hover {
    background: #fff;
    color: #212121;
  }
  .ipsSideMenu_itemActive:hover {
    color: #fff;
  }
  [data-role="profileContent"] {
    background-color: #bdbdbd;
  }
  [data-role="profileHeader"] {
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
    position: relative;
    z-index: 1;
  }
  .ipsType_minorHeading {
    font-weight: 400;
    font-size: 12px;
    color: #9e9e9e;
  }
  html[dir="ltr"] #elProfileStats > ul:last-child > li {
    border-right: 1px solid #bdbdbd;
    padding-right: 16px;
    margin-right: 16px;
  }
  .ipsNotificationCount {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
  }
  .ipsColumn {
    padding: 0 8px;
  }
  .ipsApp .ipsMenu {
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
    border-radius: 2px;
    padding: 8px 0;
    border: none;
  }
  .ipsApp .ipsMenu.ipsPad {
    padding: 16px;
  }
  .ipsApp .ipsMenu.ipsMenu_wide {
    padding: 0;
  }
  .ipsApp select:not([multiple]) {
    height: 35px;
    line-height: 1.5;
    padding: 8px 30px 8px 12px;
  }
  .cToken {
    margin: 0 4px 0 0 !important;
    background: #1976d2;
    border-radius: 12px;
    line-height: 21px;
    font-size: 12px;
    padding: 0 8px;
    height: 21px;
  }
  .ipsBreadcrumb.ipsBreadcrumb_top {
    margin: 0;
    padding: 16px 0;
    opacity: 1;
  }
  .ipsBreadcrumb > ul:last-child > li {
    color: #212121;
  }
  .ipsBreadcrumb > ul:last-child > li:last-child {
    opacity: 1;
  }
  .ipsBreadcrumb > ul:last-child > li:last-child:not(:first-child) {
    color: #616161;
  }
  .ipsBreadcrumb a[data-action="defaultStream"],
  .ipsBreadcrumb a[data-action="markSiteRead"] {
    line-height: 1.5;
    color: #212121;
  }
  .ipsMenu > .ipsMenu_sep {
    margin: 8px 16px;
  }
  .ipsMenu > .ipsPad_half:last-child {
    padding: 0 16px 8px;
  }
  .ipsMenu_item > a:not(.ipsMenu_itemInline),
  .ipsMenu_item > span:not(.ipsMenu_itemInline) {
    padding: 8px 16px;
    color: #212121;
  }
  .ipsMenu_title {
    background: #e0e0e0;
    padding: 8px 16px;
    font-weight: bold;
    font-size: 12px;
  }
  .ipsPagination.ipsPagination_mini {
    opacity: 1;
    transition: all 0.2s ease;
  }
  .ipsPagination.ipsPagination_mini a {
    background: #eee;
    padding: 0 4px;
    color: #424242;
