    border-color: #1976d2;
    border-style: dashed;
    margin: 16px 0;
  }
  html[dir="ltr"] .ipsToolList.ipsToolList_horizontal > li,
  html[dir="ltr"] .ipsToolList.ipsToolList_horizontal > li.ipsPos_right {
    margin-left: 16px;
  }
  .ipsComposeArea_editorPaste a {
    color: #212121;
  }
  .ipsComposeArea_editorPaste a:hover {
    color: #212121;
  }
  .ipsComposeArea_editorPaste .ipsComposeArea_editorPasteSwitch {
    box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 1px -1px,
      rgba(0, 0, 0, 0.14) 0px 1px 1px 0px, rgba(0, 0, 0, 0.12) 0px 1px 3px 0px;
    position: relative;
  }
  .ipsComposeArea_editorPaste .ipsComposeArea_editorPasteSwitch:hover {
    color: #fff;
  }
  [data-role="autoSaveRestoreMessage"] {
    border-top: 1px solid #bdbdbd;
  }
  .cke_wysiwyg_div {
    padding: 8px 12px !important;
  }
  .ipsDialog.ipsAnim.ipsAnim_in {
    animation-name: fadeInTop;
  }
  .ipsDialog.ipsAnim.ipsAnim_out {
    animation-name: fadeOutBottom;
  }
  .ipsAnim.ipsAnim_fast {
    animation-duration: 0.15s;
  }
  .ipsAnim_fade.ipsAnim_in {
    animation-duration: 0.15s;
  }
  .ipsAnim_fade.ipsAnim_out {
    animation-duration: 0.15s;
  }
  .ipsAnim {
    animation-duration: 1.15s;
    animation-fill-mode: both;
    animation-timing-function: ease-out;
    transform: translate3d(0, 0, 0);
  }
  @keyframes fadeInZoom {
    from {
      opacity: 0;
      transform: scale(0);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }
  @keyframes fadeOutZoom {
    from {
      opacity: 1;
      transform: scale(1);
    }
    to {
      opacity: 0;
      transform: scale(0);
    }
  }
  @keyframes fadeInTop {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
  @keyframes fadeOutBottom {
    from {
      opacity: 1;
      transform: translateY(0);
    }
    to {
      opacity: 0;
      transform: translateY(10px);
    }
  }
}
