css:
@-moz-document url(chrome://mozapps/content/extensions/extensions.xul), url(chrome://stylish/content/manage-standalone.xul) {

/* Removes icon and tags */
#styles *[anonid="status-icon"], #styles *[anonid="tags"] {

  display: none !important;
  }

/* Make the line height smaller */
#styles richlistitem {

  padding: 0 !important;
  min-height: 0 !important;
  border: 0 !important;
  }

/* Adds mouseover color */
#styles richlistitem:not([update="true"]):hover {

  background: #599EEF !important;
  }

/* Buttons in button row at the bottom of the manager window (thanks to FlunkLuc) */
   #update-all {
  list-style-image: url("data:image/png;base64,") !important;
  }

   #new-style {
  list-style-image: url("data:image/png;base64,") !important;
  }

#styles richlistitem button[anonid="update"] {
   	 list-style-image: url("data:image/png;base64,") !important;
   	}
	
#styles richlistitem button label,
#update-all label,
#new-style label {

  padding-left: 5px !important;
  }

/* Only show enable/disable button */
#styles richlistitem:not([update="true"]) *[anonid="actions"] {

  display: -moz-box !important;
  -moz-box-flex: 1 !important;
  }
#styles richlistitem:not([update="true"]) *[anonid="actions"] > *:not([anonid="edit-actions"]) {

  display: none !important;
  }
#styles richlistitem:not([update="true"]) > vbox {

  -moz-box-orient: horizontal !important;
  }

/* Removes the buttons */
#styles richlistitem:not([update="true"]) *[anonid="edit"], #styles richlistitem:not([update="true"]) *[anonid="delete"],
#copy-style-info {

  display: none !important;
  }

/* Non-checked items */
#styles richlistitem:not([update="true"]) *[anonid="enable"], #styles richlistitem:not([update="true"]) *[anonid="disable"] {

  -moz-appearance: none !important;
  border: none !important;
  -moz-border-top-colors: none !important;
  -moz-border-bottom-colors: none !important;
  -moz-border-left-colors: none !important;
  -moz-border-right-colors: none !important;
  background-color: transparent !important;
  min-width: 15px !important;
  min-height: 15px !important;
  list-style-image: url("data:image/gif;base64,R0lGODlhEAAOALMAAAAAAISChMTCxPz%2B%2FAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAAAAALAAAAAAQAA4AAwQXEMhJq704681zCF0wfFwgkCXYrWzrbhEAOw%3D%3D") !important;
  }

/* Checked items */
#styles richlistitem:not([update="true"]) *[anonid="disable"] {

  list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8%2F9hAAABGUlEQVR42mNkoBAwjhqA3YDcbR7snH%2Ble7n5uKNevX%2FDeOvSg00P3r9uvN1%2F%2ByVQ%2BitBA0q3JE31s3bO%2BsLyneHm24cMl27dYDi9%2BuKuy7NvlQCl7yEbgtWApkO5H0wN9fnf%2FfrEcO%2F1E4Zbz%2B4zXNx34%2BelluvZQOkdQPwUrwE5G2M%2Bqegr8n7%2B8ZXh%2BbtXDPdfPWW4vu%2F27weTnvQApZcB8RW8Brg2WS6VtZCJ%2Bs3yh%2BHNpw8MDx8%2BZbiz5P7jX6d%2FLyHKAIVEBWU%2BdvYZjKKMdh8%2FfWZ8dvzVi1%2FXfx9k%2BMywDyi9i6AXgIALiBWB2AyIlaFid4D4FBDfB%2BLvhAwAAU4gFgRiASj%2FAxC%2FA%2BIfBKORFDDwBgAAWtdxEQkZYoQAAAAASUVORK5CYII%3D") !important;
  }

/* Removes other stuff */
#styles richlistitem:not([update="true"]) *[anonid="enable"] .button-text, #styles richlistitem:not([update="true"]) *[anonid="disable"] .button-text{

  display: none !important;
  }
#styles richlistitem:not([update="true"]) *[anonid="enable"] > hbox, #styles richlistitem:not([update="true"]) *[anonid="disable"] > hbox {
  
  padding: 0 !important;
  }

/* Filterbox content change (thanks FlunkLuc) */
#styles-filter .textbox-input-box { 

  font-weight: bold !important; 
  color: #444444 !important; 
  }
}
