css:
@-moz-document domain("multitran.com") {
/* 
Main background color: #1e1e1e

Main text color: #d4d4d4
Text selection color: #ffffff
Text selection background color: #264f78

Headers background color: #191919
Search box and select box background color: #2e2e2e

Links color: #4e94ce
Visited links color: #295887
*/

a:link {
  color: #4e94ce !important;
}

a:visited {
  color: #295887 !important;
}

body {
  background: #1e1e1e;
  background-image: none !important;
}

body, td {
    color: #d4d4d4 !important;
}

div#start.container div.middle_col div.div700 div#textBox.expandable-input {
  background: #2e2e2e !important;
}

div#start.container div.middle_col table tbody tr td {
  background: #1e1e1e !important;
}

div#start.container div.middle_col table tbody tr td div.forumrules p {
  background: #1e1e1e !important;
}

div#start.container div.middle_col table tbody tr td font {
  background: #1e1e1e !important;
}

div#start.container div.middle_col table tr td div {
  background: #1e1e1e !important;
}

div#start.container div.middle_col table tr td div.nextline {
  background: #1e1e1e !important;
}

div#start.container div.middle_col table tr td p {
  background: #191919 !important;
}

div#start.container div.middle_col table tr td p.forum_tab {
  background: #1e1e1e !important;
}

div#start.container div.middle_col table tr td p.tab1 {
  background: #1e1e1e !important;
}

div#start.container div.middle_col table tr td u {
  background: #1e1e1e !important;
}


input {
  background: #2e2e2e;
  color: #d4d4d4;
}

select {
  background: #2e2e2e;
  color: #d4d4d4;
}

select:hover {
  background: #2e2e2e;
}

td.leftround {
  background: #191919 !important;
}

td.plaingray {
  background: #191919 !important;
}

td.rightround {
  background: #191919 !important;
}

textarea {
  background: #2e2e2e;
  color: #d4d4d4;
}

.fulltable {
  background: #1e1e1e;
}

.fulltextarea {
  color: #d4d4d4;
  background: #2e2e2e;
}

.grayline {
  background: #191919;
}

.middle_col a.menu {
  color: #d4d4d4 !important;
}

.middle_col a.menu1 {
  color: #d4d4d4 !important;
}

#suggest {
  background: #2e2e2e !important;
}

#suggest div {
  background: #2e2e2e !important;
  color: #d4d4d4 !important;
}

#suggest div:hover {
  background: #295887 !important;
}

#suggest div.over{
  color: #ffffff !important;
  background: #264f78 !important;
}

#sugodiv {
 	background:#2e2e2e !important;
}
}
