css:
@-moz-document url-prefix("https://play.google.com/music/listen") {
html,
#material-app-bar,
#music-content,
#player,
#main,
.material-container,
.material-container table .material-container tbody,
.material-container tr,
.material-container td,
.song-table table,
.song-table tbody,
.song-table tr,
.song-table td,
.songlist-container,
.details .title,
.material-card,
.gpm-vertical-list,
gpm-detail-page-header div.gpm-detail-page-header > [slot],
#descriptionWrapper.gpm-detail-page-header:hover,
.style-scope.gpm-vertical-list a, .song-table.mini.tight {
    background-color: #282828!important;
    color: #f8e6cb!important;
}
sj-home[selected] .home-palette-id-1,
sj-home[selected] .home-palette-id-2,
gpm-card-grid > [slot],
sj-card #details.sj-card > [slot],
.cluster .header .cluster-title,
.cluster .header .subtitle,
.song-table th, .song-details-wrapper .song-title {
    color: #f8e6cb!important;
}
sj-home[selected] .home-palette-id-3,
sj-home[selected] .home-palette-id-2:hover,
gpm-quick-nav .items.gpm-quick-nav > [selected],
.paper-icon-button:hover,
gpm-detail-page-header div.gpm-detail-page-header > [slot="buttons"]:hover {
    color: #ff5722!important;
}
gpm-now-card-grid .buttons.gpm-now-card-grid paper-icon-button.gpm-now-card-grid,
#player .now-playing-actions paper-icon-button {
    color: #f8e6cb;
}
.music-logo {
    filter: hue-rotate(900deg) invert(1);
}
#material-app-bar,
.top-tracks {
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.6);
}
.song-row.selected-song-row td,
.song-row.currently-playing td,
.song-row:hover,
.song-row:hover td {
    background-color: #3c3c3c!important;
}
.details .title::after,
.fade-out::after {
    background: transparent!important;
}
#material-app-bar,
.more-songs-container {
    border-color: transparent!important;
}
gpm-row-item:hover .gpm-row-item,
gpm-card-grid:not(.mini) div.gpm-card-grid > sj-card {
    background: rgba(255, 255, 255, 0.1);
}
gpm-detail-page-header div.gpm-detail-page-header > [slot="description"] {
    color: #fff!important;
}
sj-card .details.sj-card {
    padding-left: 8px;
}
}
