css:
@-moz-document domain("nowtoronto.com") {
/**
* NOW Magazine Dark Night Mode
*
* A dark style for NOW Magazine (NOWToronto).
* 
* By pkiff
* Version 1.0
* Last update: 2019-12-28
* Initial launch: 2019-06-20
*
* 2019-12-28: Version 1.0
* - Initial launch
* - Replace colors
* - Add visible hover and focus values for improved #a11y
*
*/

/* Colors 
*
* black: #111 (background)
* very dark grey: #333 (offset backgrounds)
* dark grey: #666 (side headings, some links)
* red: #ce2027 (NOW red)
* light grey: #999 (body text)
* white: #ddd (main headings)
*
*/

html {
  background-color: #111;
  color: #999;
}

#bottom {
  background-color: #333;
}


/* Menu */

#topnav ul li a {
  color: #666;
}

#topnav ul.subnav_nested {
  border-color: #666;
}

#topnav .mainnav .subnav_nested a {
  color: #666;
}

.subnav_nested {
  background-color: #000;
}

#topnav .mainnav .selected a, 
#topnav .mainnav li > a:hover, 
#topnav .mainnav li > a:focus, 
#topnav .mainnav .subnav_nested a:hover, 
#topnav .mainnav .subnav_nested a:focus, 
#topnav .mainnav .subnav_nested .selected a {
  background-color: #000;
}

#topnav.active {
  background-color: #000;
}

#topnav .mainnav li > a:hover, 
#topnav .mainnav li > a:focus,
#topnav .mainnav .subnav_nested a:hover, 
#topnav .mainnav .subnav_nested a:focus, 
#topnav ul li a:hover, 
#topnav ul li a:focus,
#breadcrumbs a:hover, 
#breadcrumbs a:focus {
  color: #ce2027;
}

/* Global */

a:hover, 
a:focus {
  color: #ddd;
  text-decoration: underline;
} 

h3, h4, h5, h6 {
  color: #666;
}

h1, 
h2 {
  color: #ddd;
}

h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
  color: #ddd;
}

h3 a:hover,
h3 a:focus {
  text-decoration: underline;
  color: #ce2027;
}

p, 
pre, 
.content {
  color: #999; 
}

strong {
  color: #ddd;
}

/* Specific IDs and Classes */

#pagetype_article #title h1 {
  color: #ddd;
}

.feats, 
.cats {
  color: #666;  
}

.pageinfos {
  border-color: #666;
}

.pageinfos .author a {
  color: #ddd;
  text-decoration: underline;
}
.pageinfos .author a:hover,
.pageinfos .author a:focus {
  color: #ce2027;
  text-decoration: none;
}

.mp-html a img {
  box-shadow: none !important;
}

.mp-html h2,
.mp-html h3, 
.mp-html div strong {
  color: #666 !important;
}

.mp-title h2, 
p.mp-infos.mp-tags, 
p.feats, 
.eventinfos h5 {
  color: #666;
}

.mp-title h2 {
  border-top-color: #666;
}

.event_info h4 + a, 
.loc_info h4 + p {
  color: #666;
}

#side .mp-title h2 {
  color: #666;
}

.mp-view-more {
  border-color: #666;  
}

.mp-classiclist .mp-list-item {
  border-color: #666;
}

.mp-filterbar-container {
  background-color: #333;
}

.mp-filterbar-textbar.mp-has-filters::before {
  background-color: #333;
}

.mp-filterbar-textbar input[type="text"].mp-filterbar-input {
  background-color: #333
}

.mp-filterbar-menu ul {
  background-color: #000;
  box-shadow: none;
  border: 1px solid #333;
}

.mp-view-more:hover, 
.mp-view-more:focus { 
  color: #000;
}

.tags h4,
#comments h3, 
.mentions_list h3, 
#related h3 {
  color: #666;
  border-color: #666;
}

.mp-tagcloud-list a,
.tag-cloud-list a,
.tags a {
  color: #000;
}


.mp-tagcloud-list a:hover, 
.mp-tagcloud-list a:focus, 
.tag-cloud-list a:hover, 
.tag-cloud-list a:focus, 
.tags a:hover,
.tags a:focus {
  color: #ddd;
}

.event_result, 
.location_result, 
.location_listing_result {
  border-color: #666;
}

.mentions_list li, 
#related li {
  border-color: #666;
}

#textnav ul li a:hover {
  opacity: 1;
  color: #000;
}

.pagination ul {
  color: #666;
}

.pagination a {
  background-color: #ce2027;
  color: #000;
  border-color: #ce2027;
}

.pagination a:hover, 
.pagination a.ord:hover {
  background-color: #111;
  color: #ddd;
}

.sidebar-title {
  color: #666;
}

/* Comments */

#comments form {
  background-color: #333;
}

/* Search */

input {
  background-color: #333;
  color: #ddd;
}

button {
  padding: .4em;
  background-color: #ce2027;
  color: #000;
  font-weight: bold;
  border: 1px solid #ce2027;
}
button:hover {
  background-color: #111;
  color: #fff !important;
  border: 1px solid #ce2027;
}

.mp-type-search #main .sectiontitle {
  margin-bottom: 2em;
}

#CSE .search.submit, 
#mp-search-bar .search.submit {
  border-color: #111;
}

#CSE .search.submit:hover,
#CSE .search.submit:focus,
#mp-search-bar .search.submit:hover,
#mp-search-bar .search.submit:focus {
  border-color: #ce2027;
}

/* Carousel */ 

.carousel.media {
  background-color: #111;
}


/* Tabs */

.ui-tabs .ui-state-default {
  background-color: #111;
  color: #999;
}

.ui-tabs .ui-state-default.ui-state-active {
  background-color: #ddd;
  color: #000;
}

.ui-tabs .ui-state-default:hover,
.ui-tabs .ui-state-default:focus {
  background-color: #ce2027;
  color: #000;
  cursor: pointer;
}

.ui-tabs .ui-state-default.ui-state-active:hover,
.ui-tabs .ui-state-default.ui-state-active:focus {
  background-color: #ddd;
  color: #000;
  cursor: default;
}


/* Calendar */

.mp-cal {
  background-color: #333;
  border-color: #666;
}

.flatpickr-prev-month svg,
.flatpickr-next-month svg {
  fill: #ddd;
}

.flatpickr-month { /* month header */
  background-color: #333;
}

.flatpickr-current-month { /* year */
  color: #ddd;
}

.flatpickr-current-month span.cur-month { /* month */
  color: #ddd;
}

.flatpickr-innerContainer {
  background-color: #333;
}

.flatpickr-day {
  color: #ddd;
}

.flatpickr-day.prevMonthDay,
.flatpickr-day.nextMonthDay {
  color: #666;
}

.flatpickr-day:hover, 
.flatpickr-day.prevMonthDay:hover, 
.flatpickr-day.nextMonthDay:hover, 
.flatpickr-day:focus, 
.flatpickr-day.prevMonthDay:focus, 
.flatpickr-day.nextMonthDay:focus {
  border-color: #ce2027;
}

.submit_event, 
.mp-goto-search {
  background-color: #333;
}

.submit_event:hover, 
.mp-goto-search:hover {
  background-color: #333;
  color: #ddd;
  text-decoration: underline;
}

.mp-cal .mp-event-item,
.mp-cal .mp-event-item:first-child {
  background-color: #333;
  border-color: #666;
}

#event_ft_reset_date, 
.mp-view-more {
  background-color: #ce2027;
  color: #000;
  border-color: #ce2027;
}

#event_ft_reset_date:hover, 
.mp-view-more:hover {
  background-color: #111;
  color: #ddd;
  border-color: #666;
}

.search_calendar {
  background-color: #333;
}
}
