@-moz-document domain("koreus.com") {
:root {
        --textcolor: hsl(240, 2%, 84%); /* Choix de la couleur du texte */
        --textcolor2: hsl(0, 39%, 61%); /* Choix couleur texte normalement rouge */
    
        --hs: 240;  /* Choix de la teinte (en degré) */
        --addl: 0%; /* Augmenter/Baisser la luminosité (en %)*/
        --adds: 0%; /* Augmenter/Baisser le contraste (en %)*/
    
    
        --color2: hsl(var(--hs), calc(10% + var(--adds)), calc(19% + var(--addl)));
        --color3: hsl(var(--hs), calc(13% + var(--adds)), calc(16% + var(--addl)));
        --color4: hsl(var(--hs), calc(11% + var(--adds)), calc(17% + var(--addl)));
        --color5: hsl(var(--hs), calc(6% + var(--adds)), calc(20% + var(--addl)));
        --color6: hsl(var(--hs), calc(12% + var(--adds)), calc(15% + var(--addl)));
        --color7: hsl(var(--hs), calc(10% + var(--adds)), calc(30% + var(--addl)));
        --color8: hsl(var(--hs), calc(14% + var(--adds)), calc(31% + var(--addl)));
        --color9: hsl(var(--hs), calc(14% + var(--adds)), calc(41% + var(--addl)));
        --color10: hsl(var(--hs), calc(12% + var(--adds)), calc(27% + var(--addl)));
        --color11: hsl(var(--hs), calc(10% + var(--adds)), calc(26% + var(--addl)));
        --color12: hsl(var(--hs), calc(37% + var(--adds)), calc(35% + var(--addl)));
        --color13: hsl(var(--hs), calc(9% + var(--adds)), calc(44% + var(--addl)));
        --color14: hsl(var(--hs), calc(8% + var(--adds)), calc(30% + var(--addl)));
        --color15: hsl(var(--hs), calc(11% + var(--adds)), calc(11% + var(--addl)));

        --color16: hsl(var(--hs), calc(11% + var(--adds)), calc(29% + var(--addl)));
        --color17: hsl(var(--hs), calc(12% + var(--adds)), calc(19% + var(--addl)));
        --color18: hsl(var(--hs), calc(12% + var(--adds)), calc(34% + var(--addl)));
        --color19: hsl(var(--hs), calc(14% + var(--adds)), calc(25% + var(--addl)));
        --color20: hsl(var(--hs), calc(14% + var(--adds)), calc(35% + var(--addl)));
        --color21: hsl(var(--hs), calc(12% + var(--adds)), calc(28% + var(--addl)));

        --color22: hsl(var(--hs), calc(0% + var(--adds)), calc(31% + var(--addl)));
        --color23: hsl(var(--hs), calc(13% + var(--adds)), calc(27% + var(--addl)));
    }

    body div {
        border: 0 !important;
    }
    body div table[style="background-color: #BEBCC9;"] {
        background-color: var(--color2) !important;
    }
    body div table[style="background-color: #BEBCC9;"] img {
        background: url(https://k.img.mu/FeTPDK.jpg) left top no-repeat;
        box-sizing: border-box;
        padding-left: 322px;
        width: 322px;
        height: 80px;
    }
    table {
        background-color: var(--color3);
    }
    a {
        color: var(--textcolor) !important;
    }
    li {
        margin-left: 2px;
        list-style: square inside;
        color: var(--textcolor)
    }
    .itemText {
        color: var(--textcolor);
    }
    .itemInfo {
        text-align: right;
        padding: 3px;
        background-color: #9A9ACE
    }
    .itemTitle,
    .itemTitle a {
        font-weight: bold;
        color: #330066;
        font-size: 14px;
        text-decoration: none;
    }
    td#leftcolumn div.blockContent {
        background-color: var(--color2);
    }
    .itemHead {
        background: var(--color4);
        border-top: 0;
        border: black;
        border-radius: 10px;
        padding: 5px;
        margin: 2px;
    }
    .itemPoster {
        font-size: 90%;
        font-style: italic;
        background-color: var(--color2);
    }
    table {
        color: var(--textcolor);
    }
    .item {
        border: 1px solid var(--color2);
        background-color: var(--color2);
        border-radius: 10px;
    }
    .itemFoot {
        background-color: var(--color4);
        border-radius: 5px;
    }
    .itemInfo {
        background: 0;
    }
    td#leftcolumn {
        color: var(--textcolor);
    }
    b {
        color: var(--textcolor2);
    }
    tr.even td {
        background-color: var(--color11);
        padding: 5px;
    }
    tr.odd td {
        background-color: var(--color7);
        padding: 5px;
    }
    td#mainmenu a.menuMain {
        border-right: 0;
        border-bottom: 0;
    }
    td#mainmenu a:hover {
        background-color: var(--color7);
    }
    td#mainmenu a.menuSub {
        border-right: 0;
        border-bottom: 0;
        border-left: 0;
    }
    td#usermenu a:hover {
        background-color: var(--color7);
    }
    td#leftcolumn th {
        background-color: var(--color2);
        color: var(--textcolor);
        vertical-align: middle;
    }
    td#leftcolumn div.blockTitle {
        padding: 3px;
        background-color: var(--color4);
        color: var(--textcolor);
        background-image: none;
    }
    td#leftcolumn {
        background-color: var(--color3);
        border-right: 1px solid var(--color2);
    }
    form input {
        background: var(--color2);
        border-color: var(--color10);
        color: var(--textcolor);
    }
    .footer,
    .footer a {
        background: var(--color3);
    }
    body {
        background-color: var(--color3) !important;
    }
    .comUserRankImg {
        border: 0;
        filter: invert(75%);
    }
    .small,
    .small a {
        color: var(--textcolor);
    }
}

@-moz-document domain("koreus.com") {
h2,
    h2 a {
        margin-top: 0px;
        margin-bottom: 0px;
        font-weight: bold;
        color: var(--textcolor);
        background-color: transparent;
        font-size: 16px;
        text-decoration: none;
    }
    .odd {
        background-color: var(--color5);
        padding: 5px;
        border-right: solid var(--color8) 2px;
    }
    .foot {
        background-color: var(--color2);
    }
    td#centercolumn th {
        background-color: var(--color8);
    }
    td#centercolumn th[width="20%"] {
        background-color: var(--color9);
    }
    .comTitle {
        margin-left: 5px;
    }
    .itemTitle,
    .itemTitle a {
        color: var(--textcolor);
    }
    .even {
        background-color: var(--color4);
        border-radius: 10px;
    }
    .outer {
        border: 0;
    }
    .head {
        background-color: var(--color14);
    }
    .comUserStat {
        font-size: 10px;
        color: var(--textcolor);
        font-weight: bold;
        border: 1px solid var(--color22);
        background-color: var(--color23);
        margin: 2px;
        padding: 2px;
        border-radius: 5px;
    }
    div.xoopsQuote {
        background: var(--color21);
        border: solid var(--color20) 2px !important;
        border-radius: 5px;
        font-family: Verdana, Arial, Helvetica, sans-serif;
        padding: 0px 6px 6px 6px;
        font-size: 12px;
    }
    div.xoopsQuote .xoopsQuote {
        background: var(--color18);
        border: solid var(--color19) 2px !important;
    }
    .odd[align="left"] {
        background: var(--color17) !important;
    }
    form select {
        background: var(--color16);
        border-color: var(--color5);
        color: var(--textcolor);
    }
    div select {
        background: var(--color16);
        border-color: var(--color5);
        color: var(--textcolor);
    }
}

@-moz-document domain("koreus.com") {
td#usermenu a {
        border-right: 0;
        border-bottom: 0;
    }
    td#centerCcolumn legend.blockTitle {
        color: var(--textcolor);
    }
    td#centerCcolumn div.blockContent {
        border-left: 0;
        border-right: 0;
        border-bottom: 0;
    }
    td#rightcolumn th {
        background-color: var(--color2);
        color: #FFFFFF;
        vertical-align: middle;
    }
    td#rightcolumn div.blockTitle {
        padding: 3px;
        background-color: var(--color4);
        color: var(--textcolor);
        background-image: none;
    }
    td#rightcolumn {
        background-color: var(--color3);
        border-left: 1px solid var(--color2);
    }
    td#rightcolumn div.blockContent {
        background-color: var(--color2);
    }
    h1,
    h1 a {
        color: var(--textcolor);
    }
    td textarea {
        background-color: var(--color2);
        border-color: var(--color10);
        color: var(--textcolor);
    }
    th div select {
        -webkit-appearance: none;
        background-color: var(--color9);
        color: white;
        transition: all 250ms;
    }
    th div select:hover {
        background-color: #37374b;
        transition: all 250ms;
    }
    #index_forum .item {
        background: none;
    }
    .comUserRankText img {
        filter: invert(80%);
    }
}

@-moz-document url-prefix("https://www.koreus.com/video/") {
.h2 {
        color: var(--textcolor);
    }
    .container {
        background-color: var(--color2) !important;
        border: 0;
        border-radius: 10px;
    }
    .navbar-default {
        background-color: var(--color2);
        border: 0;
    }
    .btn {
        background: var(--color4) !important;
        border-color: var(--color10) !important;
        color: var(--textcolor) !important;
    }
    .breadcrumb {
        background-color: var(--color6);
    }
    p {
        color: var(--textcolor);
    }
    span input {
        background: var(--color4) !important;
        border-color: var(--color10) !important;
    }
}

@-moz-document domain("koreus.com") {
fieldset {
        border-color: var(--color13);
        border-radius: 10px;
        background: var(--color2);
    }
    fieldset table {
        background-color: var(--color2) !important;
    }
    fieldset .even {
        background-color: var(--color2);
        border-radius: 10px;
    }
    #mainmenu a.menuTop {
        padding-left: 3px;
        color: blue;
        background: var(--color15);
        text-align: center;
    }
}

@-moz-document domain("koreus.com") {
img[src="//koreus.cdn.li/media/uploads/cavt54ef87738e1fc.jpg"]:hover {
        box-shadow: #f7ff00 0px 0px 5px;
        transform: rotate(360deg);
        transition: all 250ms;
        width: 500px;
        height: 1000px;
    }
    img[src="//koreus.cdn.li/media/uploads/cavt54ef87738e1fc.jpg"] {
        transition: all 250ms;
    }
    a[href="https://www.koreus.com/membre/wasab_ii"],
    a[href="https://www.koreus.com/membre150568.html"],
    a[href="/membre/wasab_ii"] {
        background-image: repeating-linear-gradient(45deg, violet, indigo, blue, green, yellow, orange, red, violet);
        text-align: center;
        background-size: 800% 800%;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        animation: rainbow 8s ease infinite;
    }
    a[href="https://www.koreus.com/membre/lefreund"],
    a[href="https://www.koreus.com/membre127277.html"],
    a[href="/membre/lefreund"] {
        background-image: repeating-linear-gradient(45deg, #d5d5d7, #74beb9, #5ffefc);
        text-align: center;
        background-size: 800% 800%;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        animation: rainbow 8s ease infinite;
    }
    @keyframes rainbow {
        0% {
            background-position: 0% 50%
        }
        50% {
            background-position: 100% 25%
        }
        100% {
            background-position: 0% 50%
        }
    }
}

@-moz-document url-prefix("https://appli.koreus.com/") {
.jumbotron,
    .sidenav {
        background-color: var(--color5);
    }
    body {
        color: var(--textcolor);
    }
    .bg-k {
        background-color: var(--color2);
    }
    li {
        list-style: none;
    }
    .dropdown-item {
        background-color: var(--color2);
    }
    .dropdown-menu {
        background-color: var(--color2);
    }
    .dropdown-item:hover,
    .dropdown-item:focus {
        background-color: var(--color14);
    }
    .text-k-purple {
        color: var(--textcolor);
    }
    .bg-light {
        background-color: var(--color5) !important;
    }
    .img-thumbnail {
        background-color: var(--color5);
        border: 1px solid var(--color5);
    }
    .form-control {
        color: var(--textcolor);
        background-color: var(--color3);
        background-clip: padding-box;
        border: 1px solid #414151;
    }
    .form-control:focus {
        border-color: var(--color12);
        box-shadow: 0 0 0 0.2rem rgba(70, 92, 115, 0.25);
        color: var(--textcolor);
        background-color: var(--color3);
    }
    .table-bordered th,
    .table-bordered td {
        border: 1px solid var(--color13);
    }
    .modal-content {
        background-color: var(--color5);
    }
    p.newsdate {
        color: var(--textcolor);
    }
}

@-moz-document domain("koreus.com") {
body div[style="text-align:center; background-color: #EBEBEB; border-top: 1px solid #FFFFFF; border-left: 1px solid #FFFFFF; border-right: 1px solid #AAAAAA; border-bottom: 1px solid #AAAAAA; font-weight : bold;"] {
        text-align: center;
        background-color: var(--color2) !important;
        font-weight: bold !important;
        color: var(--textcolor) !important;
        width: 70% !important;
        margin: auto !important;
        margin-top: auto !important;
        border: solid #3f3f4b 2px !important;
        font-family: Verdana, Arial, Helvetica, sans-serif !important;
        font-size: 15px !important;
        border-radius: 5px !important;
        margin-top: 50px !important;
    }
    body div[style="text-align:center; background-color: #EBEBEB; border-top: 1px solid #FFFFFF; border-left: 1px solid #FFFFFF; border-right: 1px solid #AAAAAA; border-bottom: 1px solid #AAAAAA; font-weight : bold;"] a {
        color: var(--textcolor2) !important;
    }
}
