css:
@-moz-document domain(dev.to) {
.container {
	width: auto;
}

.container .title {
	width: calc(82% - 0.4em);
	max-width: initial;
	margin-bottom: 2em;
}

.container .body {
	font-family: "Noto Sans", sans-serif;
}

#article-show-primary-sticky-nav, #article-reaction-actions, nav > * {
	display: none;
}

nav #nav-search-form-root {
	display: block !important;
}

.top-bar {
	background: none;
    border: none;
    box-shadow: none;
	position: absolute;
}

nav #nav-search-form-root form #nav-search {
	background: #dde3e9;
}

footer .container .inner-footer-container {
	padding: 10px 0;
	width: 100%;
	max-width: 100%;
	margin: 0;
	text-align: center;
	border: none;
	background: #e7e9ec;
}

footer .container, footer .container.centered-footer {
	width: 100%;
	max-width: 100%;
	margin: 0;
}

.container {
	border: none;
	box-shadow: none;
}

body, .stories-show {
    background: #ffffff;
}

.single-comment-node .reaction-button .reactions-count, .single-comment-node .dropbtn .reactions-count {
	top: 0;
	border: none;
	background: none;
	line-height: 25px;
}

.single-comment-node .reaction-button.reaction-button, .single-comment-node .dropbtn.reaction-button {
    padding-top: 0;
}

.show-page-content-display .content-classification .content-classification-text {
	background: none;
}

.single-comment-node .inner-comment {
	border: none;
}

.comments-container form {
	border: none;
	background: none;
}

.comments-container form textarea {
	border: none;
	background: #e7e9ec;
	color: #111111;
}

.home {
	max-width: 1412px;
}

.home .side-bar {
    margin: 0;
}

.home .side-bar .widget {
	margin-bottom: 50px;
	border: none;
	background: none;
	box-shadow: none;
}

.home .articles-list .single-article {
	border: none;
	background: none;
	box-shadow: none;
}

@media screen and (min-width: 1240px) {
	.home .articles-list {
		margin: 0 50px;
	}
}
}
