css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

	*::-moz-selection{

	background:#333 !important;

	color:#fff !important;

}

*{

	font-family:"Trebuchet MS","Segoe UI","Arial","Serif" !important;

	color:#333 !important;

}

body{background:none !important; background-color:#fff !important;}

#body{max-width:5000px !important; background-color:#fff !important;}

.bar {border:none !important; background:none !important; background-color:#F0F0F0 !important;}

.subscribebar, .subscribebar a:link, .subscribebar a:visited { color:#fff !important; }

.control, .control a:link,.control a:visited  {color:#1e529d !important;}



.main {background: no-repeat bottom !important; }

.box .main {border-bottom: none !important; }
.box .box-bar.groups, .box .box-bar.groups .box-corner, .box .box-bar.search, .box .box-bar.search .box-corner { border:none !important; background: none !important; background-color: #F0F0F0 !important; }
    .box .box-bottom,.box .box-corner { background: none !important; }
    .box .box-bar.friends, .box .box-bar.friends .box-corner {border:none !important; background:none !important;background-color:#F0F0F0 !important;}
    .box-body { background-color: #fff !important; padding-bottom: 10px !important; }
.box .box-bar.home, .box .box-bar.home .box-corner {border:none !important; background:none !important;background-color:#F0F0F0 !important;}




a,a:link,a:visited{

	text-decoration:none !important;

	color: #1e529d !important;

}

a:hover{

	color:#1e529d !important;

	border-bottom:1px dotted #333333 !important;

}

a:active{

	text-decoration:none !important;

	color:#f4a460 !important;

}

.entry .body

{

	background: #F0F0F0;

       

        padding-bottom: 10px !important;

}



.entry .title .text 

{

padding-left: 66px !important;

padding-bottom: 10px !important;

}





#footer a,#footer a:visited,.feed .entry .info a,.feed .entry .info a:visited,.feed .entry .likes a,.feed .entry .likes a:visited,.feed .entry .comment a,.feed .entry .comment a:visited,.feed .entry .expandcomment a,.feed .entry .expandcomment a:visited,.feed .entry.deleted a,.feed .entry.deleted a:visited,a.fl,a.fl:visited,.usersubgrid .lists a,.usersubgrid .lists a:visited{color:#333333 !important;}

#logo a:hover{

	border-bottom:none !important;

}





#logo {background: url(http://friendfeed.com/static/images/logo-small.png) no-repeat !important; }



#logo a img {display: none;}



#logo a {display: block; width: 200px; height: 40px; background: none;}







.cluster{

	border-bottom:1px dotted #708090 !important; /*Separates each posts*/

	padding-bottom:1em !important;

}

.info,.likes,.comment,#footer,.pager,.bottom,.hidetoggle { font-size: 12px !important; }

.title {font-size: 14px !important;color:#F0F8FF !important; }

.lbody {font-style:italic !important; }

.main ul li a, .section ul li a { font-size: 11px !important; }

.sharebox, .entry, .pager.top { border-bottom:1px dotted #bbb !important; margin-bottom:25px !important;padding-bottom:25px !important; }

.searchbold { background-color:#CCFFE6; }

}
