css:
@-moz-document domain("douban.com") {
html,
body {
    background-color: #333;
}

div,
ul,
li,
span,
p,
blockquote,
figure {
    color: white !important;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #29a19c !important;
}

div.content,
div.cover,
div.block,
#g-side-info .ft,
td.tablelc,
td.tablecc,
td.tablerc,
p:hover {
    background-color: #333 !important;
    color: white !important;
}

.rating-stars {
    color: #e6a157 !important;
}

div.cover:hover {
    background-color: transparent !important;
}


span.react-text,
.action-btn span,
.channel-item .likes {
    color: #0fabbc !important;
}


/*个人信息左右角落圆角*/
div.ex1,
div.ex2 {
    visibility: hidden;
}

/**/
.indentpop1 {
    background: #333;
    color: #29a19c;
}
span.tagbtn{
    color:#333 !important;
}

/*优选评论背景图*/
li.comment-item h3,
div.meta-header,
div.nlst {
    background-color: #333 !important;
}

a.collect_btn span {
    color: #29a19c !important;
    background: white;
    padding: 2px 2px 7px 5px !important;
}

a.colbutt {
    background: none !important;
}

.infobox .bd {
    padding: 10px;
    background-color: #333;
    border: 1px solid #aaa;
}

.infobox .ex2,
.infobox .ex1 {
    background: none;
}

#db-isay textarea {
    background-color: white !important;
}

#wrapper a,
#wrapper a:link,
#wrapper a:visited,
#wrapper a:active {
    color: #0fabbc !important;
}


.status-item .block {
    background: #333;
}

.mod {
    background: #333;
    padding: 10px;
}


.stream-items .status-wrapper {
    border-bottom: 1px solid #555;
}

.topics .topic-item .abstract {
    color: #ccc;
}

#interest_sectl .rating_num {
    color: rgb(224, 144, 21);
}

.gray_ad {
    background-color: #333;
}

.tag,
.tag:link,
.tag:visited {
    background-color: #333 !important;
}

.tag:hover {
    background-color: white !important;
}

/*豆瓣电影清单*/
.doulist-item {
    border-bottom: 1px dashed #aaa;
}
.doulist-item .mod,
.doulist-item .doulist-subject {
    background-color: #333 !important;
}

#wrapper a:visited,
#wrapper a:active {
    color: white !important;
}

.user-group-list {
    background: white !important;
}
.user-group-list a {
    color: #333 !important;
}

a.bn-big-action,
a.bn-big-action:link,
a.bn-big-action:hover,
a.bn-big-action:visited {
    background: #555 !important;
}

.select .select-item {
    background-color: transparent !important;
}

.dropdown-menu {
    background-color: #333 !important;
}

.picker-wrap {
    color: #333 !important;
    background-color: transparent !important;
}

.topic-reply li {
    background-color: transparent !important;
}

.topic-reply li .reply-doc h4 {
    background-color: #333 !important;
    border-bottom: 1px dashed #aaa;
}

div#top-nav-notimenu * {
    color: #3e3e3e !important;
    background: white !important;
}
}

@-moz-document domain("movie.douban.com") {
.tag-list label {
    color: #f0ad6e !important;
}

.gallery-frame .gallery-detail {
    background: none !important;
}
}

@-moz-document domain("music.douban.com") {
div.inner {
    background: #333 !important;
}

.section-switcher .header {
    background-color: #333 !important;
}

.popular-artists.section .artists,
.popular-artists.section .new-artists {
    background-color: #333 !important;
}
}
