css:
@-moz-document domain("okoun.cz") {
.item .meta .user {
    color: green;
}

.item .content {
    color: gray;
}

.item .meta {
    padding-right: 0em;
}

textarea {
    /* padding: 2px; */
    /* min-width: 15em; */
    width: 100%;
    height: 7em;
}

input[type=checkbox] {
  transform: scale(2);
}

.nobr {
    white-space: unset;
}

.expbutton .hd {
    font-size: 20pt;
    font-family: sans-serif;
    padding: 0;
}
.pager ul li {
    font-size: 18pt;
}


.yui-button button {
    font-size: 25pt !important;
    line-height: 1.5em !important;
 }

.pager-top {
    right: unset;
    top: 16px;
}
input.text {
    font-size: 25pt;
      width: 180pt !important;
}

body {
    padding: 0;
    min-width: auto;
}
#body {
    padding: 0;
    margin: 0;
}
.head .user {
    font-size: 22pt;
}
  /* reklamy fuc */
  #adwrapper-lb, #adwrapper-bs {    visibility: hidden; display:none !important}

  /* rozdeleni sloupcu */
.main .ctx {
    visibility: hidden;
    height: 0;
    display: none;
}
.main .main {
    width: 100%;
}

  /* ramecek prispevku */
.listing .item {
     padding-bottom: 0; 
}

.listing {     padding-top: 16px; 
}
/*pryc kategorie*/
    
    h3 {
        position: absolute;
        z-index: -11;
        visibility: hidden ;
        display: none;
    }

h2 {
     font-family: sans-serif; 
     font-size: 2.5em;
}
.welcome {
    height: 0;
    visibility: hidden;
    display: none;
}
div.content {
    font-size: 25pt;
}

/* popisek audirka*/
.descr {
    position: absolute;
    right: 3%;
    display: none;
     visibility: hidden;
   }

/* button s oblidenosti klubu*/
.yui-button.top {
    float: inherit;
    margin-bottom: 0.5em;
}
.content, .item .meta, body.userlist .main .main, body.boardlist .main .main {
    font-size: 25pt;
        overflow-wrap: break-word;
    word-wrap: break-word;
}
.head .menu, .board .main .settings {
    font-size: 25pt;
}
.head .menu {
    margin-left: 0em;
}

a {
    overflow-wrap: break-word;
    word-wrap: break-word;
}


/* dark colors */


.head .user .login input.text {
    width: 9em;
    border: 0;
    height: 24px;
}


#body {

    margin: 0px;
    padding: 3px;
	background-color: #222;
}

body {
   padding: 0;
}

body,
.item.hover .actions span.prev {
	color: #aaa;
}

.room .item.new {
    background-color: #233;
}


a, h2 a:visited {
    color: #59a;
}
a:visited {
    color: #995566;
}

.welcome, .item .content .confirm.reply, .item .actions .share:after, .item.new .actions .share:after {
     background-color: transparent;
}

.room .item.new, .room .item  {
     border: 0px; 
     border-bottom: 1px solid; 
}



.item .actions, .item.new .actions  {
    border-top: 1px solid #555;
}

html {
    background: #000;
}

.head h1 a {
     background-image: none;
}


.item .date, .item .permalink a, .item .meta a {
    color: #555;
} 


input, textarea {
    background-color: #111;
    color: #888;
}

.yui-button button {
    background-color: #555;
}

.main {
    margin-left: 10px;
}

.pager ul li, .pager ul li.older, .pager ul li.oldest, .pager ul li.newer, .pager ul li.newest, .index .news .yui-g.first .yui-u  {
    background-color: unset; 
    background-image: none;
}

.search input.text, .board .main .search input.text.authors {
    border: 0;
    height: 22px;
    padding-top: 2px;
    padding-bottom: 0px;
}

a {
    text-decoration: none;
}
.item .date, .item .permalink a, .item .meta a {
    color: #777;
}

.item .actions.replyForm a.reply, .expbutton .post.content, .actions.replyForm .post.content {
    background-color: #223334;
}

}
