css:
@-moz-document domain("zimuzu.tv"),
domain("xiazai003.com"),
domain("zmz2019.com"),
domain("got001.com"),
domain("rrys2019.com") {

    .menu-box a,
    .top24 li a,
    h2.it,
    .user-release dt,
    h3.f16 {
        font-weight: normal;
    }

    #xianliaome_window {
        display: none;
    }

    body {
        background: #eeeeee;
    }

    /* header */
    div[style="text-align:center; margin:10px 0 15px 0;"],
    #help {
        display: none;
    }

    div [style="text-align:center; margin:0 0 15px 0;"] {
        display: none;
    }

    div [style="text-align:center; margin:20px 0;"] {
        display: none;
    }

    .top-box,
    .ui-header {
        background-color: #ffffff;
        box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
        -webkit-box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
    }

    span[style="position: relative; font-size: 16px; font-weight: bold; left: 20px; top: 15px;"] a {
        cursor: pointer;
        min-width: 145px;
        min-height: 44px;
        border-radius: 4px;
        border: solid 1px #3498db;
        color: #3498db !important;
        padding: 5px;
        font-weight: 400;
    }

    .header {
        padding-top: 5px;
        border-bottom: 0;
        background: transparent;
    }

    .menu-box {
        background: transparent;
    }

    .menu-box a {
        height: 38px;
        line-height: 20px;
        color: #333333;
        margin: 2px 2px;
        transition: all 0.1s linear;
    }

    .menu-box a.cur,
    .menu-box a:hover {
        background: transparent;
        color: #3498db !important;
        border-bottom: 3px solid #3498db;
    }

    .menu-box a:active {
        background: transparent;
        color: #3498db !important;
        border-bottom: 3px solid #3498db;
    }

    .notice {
        background: transparent;
    }

    .notice .w a {
        color: red;
    }

    /* main */
    h2.it,
    .top24 h2.it,
    .today-play h2.it {
        height: 40px;
        line-height: 40px;
        font-size: 20px;
        border-top-left-radius: 2px;
        border-top-right-radius: 2px;
    }

    .middle-box .box,
    .resource-showlist li,
    .day_data,
    .qd-list li {
        box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
        -webkit-box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
        border-radius: 2px;
        margin-bottom: 12px;
    }

    .today-play .box,
    .box.out-resource {
        margin: 0;
    }

    .today-play .jobs-hr {
        margin-top: 8px;
    }

    .today-play .jobs-hr {
        height: 157px;
    }

    .friendlinks {
        margin-top: 0;
    }

    .resource-imgs .box {
        box-shadow: none;
        border-radius: 0;
    }

    .top24 .box {
        height: 100%;
    }

    .top-update li p {
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
    }

    .user-menu {
        height: auto;
    }

    .found-box .it {
        padding-bottom: 6px;
        padding-top: 6px;
    }

    .box.xy-list {
        margin: 10px 0;
    }

    .xy-list .title,
    .xy-tags {
        box-shadow: none;
    }

    .day_data {
        border-top: none;
        border-top-left-radius: 0;
        border-top-right-radius: 0;
        border-bottom: none;
    }

    .qd-list li {
        margin: 8px 0;
    }

    div [style="width:1038px;margin:10px auto;background-color:#fff"] {
        display: none;
    }

    div [style="text-align:center; margin:10px 0;"] {
        display: none;
    }

    div [style="margin:15px 0 0 0;text-align:center;"] {
        display: none;
    }

    div [style="text-align:center; margin:15px 0;"] {
        display: none;
    }

    div [style="text-align:center;margin-top:10px"] {
        display: none;
    }

    /* download */
    .ui-header .ui-limit .view-count {
        background-color: #eeeeee;
        border: 0px;
    }

    .ui-header .ui-limit .view-count .count {
        color: #4d4d4d;
    }

    .ui-header .ui-limit .count-down .minfo .count {
        color: #4d4d4d;
        background-color: #eeeeee;
    }

    .ui-header .ui-limit .film-title {
        color: #4d4d4d;
    }

    .ui-content .row-content .col-box.col-sidebar .sidebar-warpper {
        background-color: white;
        box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
        -webkit-box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
    }

    .ui-content .row-content .col-box.col-sidebar .sidebar-warpper .tab-side>li>a {
        background-color: white;
    }

    .ui-content .row-content .col-box.col-sidebar .sidebar-warpper .tab-side>li.active>a {
        background-color: #3f6ecf;
    }

    .ui-content .row-content .col-box.col-sidebar .sidebar-warpper .tab-side>li.active>a {
        color: white;
    }

    .tab-content .tab-pane>.tab-header {
        height: 40px;
        background: white;
    }

    .tab-content .tab-pane>.tab-header>li {
        padding: 10px 10px 6px 10px;
    }

    .tab-content .tab-pane>.tab-header>li a {
        padding: 0;
    }

    ul+.tab-content .tab-pane {
        box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
        -webkit-box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
        border-radius: 2px;
        background: white;
        margin-bottom: 10px;
    }

    .down-popup {
        background-color: #ffffff;
    }

    .down-popup .panel-inner-down .panel-body .btn {
        background-color: #3f6ecf;
        border: 0px solid #0d1926;
        color: #ffffff;
    }

    .ui-footer.ui-footer-simple .copyright {
        background-color: white;
        box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
        -webkit-box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.2), 0px 4px 5px 0px rgba(0, 0, 0, 0.14), 0px 1px 10px 0px rgba(0, 0, 0, 0.12);
    }

    .ui-footer .copyright {
        color: #4d4d4d;
    }
}
