css:
@-moz-document regexp("https://cssbattle.dev/((battle/)\\d+)?") {
body,
.header { min-width: auto }

.Homepage_halfAndHalf__35wAK > div { --easing: cubic-bezier(.76,0,.38,1.02) }
.Homepage_halfAndHalf__35wAK > div:after { display: none }
.Homepage_halfAndHalf__35wAK:focus-within > div,
.Homepage_halfAndHalf__35wAK:hover        > div {
    max-height: 100vh;
    overflow: auto;
}
.Homepage_halfAndHalf__35wAK:focus-within > div:not(:focus-within), 
.Homepage_halfAndHalf__35wAK:hover        > div:not(:hover) {
    max-height: 0;
}
.container__item--choose-leaderboard,
.container__item--sticky,
.container__item--user-info {
    flex-basis: 0;
    min-width: 392px;
}

.item__header {
    position: sticky;
    top: 0;
    z-index: 1;
}
}
