css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.tvguide.com/Listings/Default.aspx),
url(http://www.tvguide.com/listings/default.aspx),
url(http://www.tvguide.com/listings/),
url(http://www.tvguide.com/Listings/),
url(http://www.tvguide.com/Listings/default.aspx){

HTML
{padding-bottom:0 !important}

BODY
{margin-top:-50px !important;
 overflow-x:hidden !important;
 background: #DDDDD3 url(http://i50.photobucket.com/albums/f329/Webbpa/TV_bg.gif) fixed repeat}

/* Time grid */
.timebarRow>.timebarCell:first-child
{padding-right:15px !important}
.timebarCell+.timebarCell
{border-right-width:1px !important;
 border-right-color:white !important;
 border-right-style:solid !important}

.timebarCell IMG{margin-top:-2px !important}
.timebarCell{text-align:center !important}
.timebarCell:first-child+TD{padding-left:4% !important}

#body,#l-w,#l-l-w,#l-l-c,.listings-w,
.listings-w>DIV,.listings-w>DIV>TABLE,
#TvGridDiv,#gridTable,.gridtabs,
.timebarTable,.ch>TABLE
{width:100% !important;
 background-image:none !important}

#gridTable
{border-top-width:0 !important;
 margin-top:-2px !important}

#l-l-w
{margin-left:-10px !important}

.gridLegend TABLE
{margin-left:auto !important;
 margin-right:auto !important}

.pc2height,.nch,.nch B,.timebarCell
{font-size:13px !important;
 font-weight:bold !important}

.par
{font-size:13px !important;
 font-weight:lighter !important}

.pc2height:hover,.nch:hover
{text-decoration:none !important}

.newSelectStyle,#ID_foLayerCell,#ID_foLayerCell *
{font-size:14px !important}

IMG[src$="/flyover_close.gif"]
{height:15px !important;
 width:15px !important}

/* Date and time boxes */
#l-l-c>DIV:first-child+DIV+DIV>DIV>TABLE>TBODY>TR:last-child>TD:first-child
{position:absolute !important;
 left:600px !important;
 top:10px !important}

.gridDiv
{height:auto !important;
 overflow:hidden !important}

.nch B
{padding-right:5px !important}

#gridLegendDiv
{margin-left:auto !important;
 margin-right:auto !important}

#divWelcome,#ad-728x90-top0,BODY>A,.nch BR,
BODY>#body>DIV:not(#l-w),#ID_foLayerCell>FONT:last-child,
#l-w>DIV:not(#l-l-w),.ph-w,#amg_target_e5eb,
.listings-bottom-ad-w,.gridAdRow,.cc,
#printlocalrow,#searchboxrow,#meebo,#facebox,
TD[colspan="37"][onclick="javascript:gotoMagicUrl()"],
/* Search, etc */
#l-l-c>DIV:first-child+DIV>DIV>TABLE>TBODY>TR:first-child,
#l-l-c>DIV:first-child+DIV>DIV>TABLE>TBODY>TR:first-child+TR,
#l-l-c>DIV:first-child+DIV>DIV>TABLE>TBODY>TR:first-child+TR+TR,
#l-l-c>DIV:first-child+DIV>DIV>TABLE>TBODY>TR:first-child+TR+TR+TR,
#l-l-c>DIV:first-child+DIV+DIV>DIV>TABLE>TBODY>TR:last-child>TD:last-child,
#l-l-c>DIV:first-child+DIV+DIV>DIV>TABLE>TBODY>TR:first-child+SCRIPT+TR,
#l-l-c>DIV:first-child+DIV+DIV>DIV>TABLE>TBODY>TR:first-child+SCRIPT+TR+TR,
/* HD Only box */
#gridtabdiv>TABLE>TBODY>TR>TD:last-child
/* May be needed for Lifetime ads
,#gr0,TD[style*="25px"]*/
{display:none !important}}
