css:
/* i really want this to be global */

:root {
--main-bg: #222;    
    --main-body: #ddd;
    --alt-bg: #444444;
    --main-border: #e5e5e5;
    --active-element:  #0783ac;
}


body,
.containerSite,
.Select-control,
.cbm-select--1,
.cbm-select--2,
.cbm-input--1,
.multiSelectFieldContentContainer .search > input,
input::placeholder,
.cbm-mainInfos,
#cbm-carousel .cbm-photoGallery,
.CBM_page_listing .adLineContainer .adContainer .linkAd.linkAd--colored,
.Checkbox .Checkbox__checkmark,
.containerFooter .line4 {
    background: var(--main-bg) !important;
    color: var(--main-body)  !important;
}

.CBM_page_listing .Select:not(.is-disabled) .Select-placeholder,
.cbm-select--1, .cbm-select--2,
.cbm-select--1 select, .cbm-select--2 select,
.cbm-input--1,
.cbm-photoGallery__thumbnail--image {
    border-color: var(--active-element) !important;
    border-radius: 0!important;
    color: var(--main-body)!important;
}

.CBM_page_listing .adLineContainer .adContainer > a,
.CBM_page_listing .adLineContainer .adContainer
 {
    border-color: var(--active-element);
}

.CBM_page_listing .adLineContainer .adContainer .linkAd:active,
.CBM_page_listing .adLineContainer .adContainer .linkAd:focus,
.CBM_page_listing .adLineContainer .adContainer .linkAd:hover {
    border-color: transparent;
    background: #333;
}

.cbm-txt--default,
.cbm-goodDeal .cbm-goodDeal__label,
.vehicleCont,
.dptCont,
.cbm-consultedSearch__content,
.cbm-consultedSearch__content a,
.brandModel,
.DropdownMenu__labelText,
.titleContainer,
.optionLabel > *,
.cbm-title--section,
.cbm-list--1 .cbm-list__title, .cbm-list--2 .cbm-list__title, .cbm-list--3 .cbm-list__title,
.cbm-list--1 li, .cbm-list--2 li, .cbm-list--3 li {
    color: var(--main-body) !important;
}


.navMobile {
    background-color: var(--main-bg);
}

.containerContent {
    background-color: var(--main-bg);
    color: var(--main-body);
}
