css:
@-moz-document url-prefix("https://www.iliad.it/account/") {
#navbar {
   background-color: #303030;
   box-shadow: 0 1px 8px 0 black;
}

#page-container, #container {
   background-color: #202020;
}

nav.nav-menu ul.menu > li:nth-child(2n+1) {
   background-color: #272727;
   color: #a0a0a0;
}

nav.nav-menu, .page__title {
   background-color: #373737;
   color: white;
}

nav.nav-menu ul.menu > li {
   color: #a0a0a0;
}

.menu .selected {
   color: white !important;
}

body, a, .mdc-radio__label {
   color: white;
}

.page > p {
   color: white !important;
}

.p-conso h2,
.p-conso .table-montant {
   background-color: #373737;
}

.bt-label.bt-white span { color: white; }
.bt-label.bt-white {
   background-color: #373737;
   border: 1px solid #3e3e3e;
}

.bt-label.bt-white:hover {
   background-color: #575757;
   border: 1px solid #3e3e3e;
}

.p-conso .conso__icon {
   background-image: url('data:image/png;base64,');
}

#conso-progress svg path:first-child {
	display: none;
	/*stroke: #222222;*/
}


.rechargement .table-rechargements {
   background-color: #272727;
}

.p-conso .table-details {
   background-color: transparent;
}

.p-conso .table-details .preheader {
   background-color: #3D2929;
}

.as__item__name .icon.bt-edit.grey {
   background-color: #3c3a39;
}

.acc__commandes .title, .p-activation .title {
   background-color: #373737;
}
}
