@-moz-document domain("forums.linuxmint.com") {
/* Basics 
---------------------------------------- */
body {
    background-color: #5e7553;
    font-family: Helvetica;
    font-size: 12px;
    line-height: normal;
    margin: 0;
}

div.page-body {
    margin-top: -20px;
    background-color: #424242;
}

.wrap {
    background-color: #5e7553;
    border-style: none;
    border-radius: 0px;
}

h1 {
    /* Forum name */
    font-family: Helvetica;
    font-weight: bold;
    font-size: 2.5em;
}

h2 {
    /* Forum header titles */
    font-family: Helvetica;
    font-weight: normal;
    background-color: #424242;
    color: #ffffff;
    padding: 5px;
    padding-top: 10px;
    padding-left: 10px;
    font-size: 2em;
}

h2.topic-title a {
    color: #ffffff;
}

h2.posting-title a {
    color: #ffffff;
}

h3 {
    /* Sub-headers (also used as post headers, but defined later) */
    font-family: Helvetica;
    font-weight: bold;
    text-transform: uppercase;
    border-bottom: 1px solid transparent;
    margin-bottom: 3px;
    padding-bottom: 2px;
    font-size: 1.05em;
    margin-top: 20px;
}

h4 {
    /* Forum and topic list titles */
    font-family: Helvetica;
    font-size: 1.3em;
}

/* header section 
---------------------------------------- */
.site_logo {
    background-image: url(https://i.imgur.com/3MiIGFZ.png);
    margin-top: -10px;
    width: 76px;
    height: 71px;
}

div.headerbar {
    padding: 15px;
    border-style: none;
    background-color: #424242;
    background-image: none;
    border-radius: 30px 30px 0px 0px;
}

div.site-description p {
    font-size: 14px;
    font-family: helvetica;
}

ul.nav-main.linklist {
    border-style: none;
}

ul.nav-breadcrumbs.linklist.navlinks {
    border-style: none;
}

div.navbar {
    background-color: #fff;
    margin-bottom: -15px;
    margin-top: -4px;
    border-style: none;
    border-radius: 0px;
}

ul.nav-main.linklist {
    font-family: helvetica;
    font-size: 12px;
}

ul.nav-breadcrumbs.linklist.navlinks {
    font-size: 12px;
}


/* footer */
div.stat-block.online-list p {
    color: #ffffff;
    padding-left: 10px;
    padding-right: 10px;
    padding-bottom: 10px;
    border-style: none;
}

a.left-box.arrow-left span {
    color: #ffffff;
}

div.stat-block.online-list h3 a {
    color: #2e8f45;
    padding-left: 10px;
}

a.username {
    color: #339e2f;
}

div.stat-block.online-list h3 {
    border-style: none;
}

/* Global edits */
/* CP tabbed menu
----------------------------------------*/
.tabs .tab > a {
    background: #E0EDD3;
    color: #333333;
}

.tabs .tab > a:hover {
    background: #ECEFE9;
    color: #3bbc2a;
}

.tabs .activetab > a,
.tabs .activetab > a:hover {
    background-color: #E0EDD3;
    background-image: none;
    border-color: #E0EDD3;
    box-shadow: none;
    color: #333333;
}

.tabs .activetab > a:hover {
    color: #000000;
}

/* Mini tabbed menu used in MCP
----------------------------------------*/
.minitabs .tab > a {
    background-color: #E0EDD3;
}

.minitabs .activetab > a,
.minitabs .activetab > a:hover {
    background-color: #E0EDD3;
    color: #3bbc2a;
}

/* Responsive tabs
----------------------------------------*/
.responsive-tab .responsive-tab-link:before {
    border-color: #536482;
}

.responsive-tab .responsive-tab-link:hover:before {
    border-color: #3bbc2a;
}

/* misc */
i.icon.fa-chevron-circle-up.fa-fw.icon-gray {
    font-size: 2.5em;
    color: #c8c8c8;
    padding-bottom: 10px;
    padding-left: 5px;
    padding-right: 5px;
}

i.icon.fa-chevron-circle-up.fa-fw.icon-gray:hover {
    font-size: 2.5em;
    color: #3bbc2a;
    padding-bottom: 10px;
    padding-left: 5px;
}


a.top span {
    display: none;
}

/* THREADS 
---------------------------------------- */
/* thread page header */
div.rules.rules-link {
    display: none;
}

div.action-bar.bar-top {
    padding-left: 10px;
    padding-right: 10px;
}

li.active span {
    color: #ffffff;
    background-color: #42b854;
}

/*buttons*/
.button {
    border: none;
    background-color: #dbdbdb;
    background-image: none;
    box-shadow: none;
    color: #424242;
    border-radius: 0px;
}

.button:hover,
.button:focus {
    border: none;
    background-color: #fff;
    background-image: none;
    text-shadow: none;
    color: #6daf25;
    border-radius: 0px;
}

.button .icon,
.button-secondary {
    color: #424242;
    border-radius: 0px;
}

.button-secondary:focus,
.button-secondary:hover {
    background-color: #fff;
    border-radius: 0px;
}

.button:focus .icon,
.button:hover .icon {
    color: #6daf25;
    border-radius: 0px;
}

input.inputbox.search.tiny {
    border-style: none;
    border-radius: 0px;
}

/* Pagination*/
a.mark {
    color: #fff;
}

div.pagination {
    color: #fff;
}

.pagination li a {
    background: #ECEDEE;
    filter: none;
    border: none;
    box-shadow: none;
    -webkit-box-shadow: none;
    color: #424242;
}

.pagination li.ellipsis span {
    background: transparent;
    color: #000000;
}

.pagination li.active span {
    background: #6DAF25;
    border: none;
    color: #FFFFFF;
}
.pagination li a:hover,
.pagination li a:hover .icon,
.pagination .dropdown-visible a.dropdown-trigger,
.nojs .pagination .dropdown-container:hover a.dropdown-trigger {
    background: #6DAF25;
    border: none;
    filter: none;
    color: #FFFFFF;
    text-shadow: none;
}

.row .pagination li a,
.row .pagination li span {
    border-radius: 0px;
}

/* Board Index */
div.stat-block.birthday-list h3 {
    color: #17bb59;
    padding-left: 10px;
}

div.stat-block.birthday-list p {
    color: #ffffff;
    padding-left: 10px;
    padding-right: 10px;
}

div.stat-block.statistics h3 {
    color: #17bb59;
    padding-left: 10px;
}

div.stat-block.statistics p {
    color: #ffffff;
    padding-left: 10px;
    padding-bottom: 10px;
}

div.stat-block.online-list h3 a {
    color: #17bb59;
}


/* Forum index */
.forabg {
    /* added selector: , .forumbg */
    background-color: #59674B;
    background-image: none;
    border-style: none;
    padding: 10px;
}

.forumbg {
    /* added selector: , .forumbg */
    background-color: #59674B;
    background-image: none;
    margin-top: 10px;
    padding: 10px;
}

a.mark-read.rightside {
    color: #ffffff;
    padding-right: 10px;
    float: right;
}

div.action-bar.actions-jump {
    padding-left: 5px;
    padding-right: 5px;
}

div.action-bar.bar-bottom {
    padding-top: 5px;
}

div.action-bar.compact {
    margin-top: -20px;
}

p.responsive-center.time {
    color: #ffffff;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 10px;
}

div.topic-poster.responsive-hide.left-box {
    font-size: .8em;
}

a.topictitle {
    font-size: 1.2em;
}

h2.forum-title a {
    color: #ffffff;
}

list-inner {
    padding-top: 5px;
    padding-bottom: 5px;
    margin-left: 5px;
}

ul.topiclist li {
    color: #000;
    padding-top: 5px;
    padding-bottom: 5px;
}

ul.topiclist dd {
    border: none;
}


li.row:hover {
    background-color: #bbd9ac;
}

i.icon.fa-file.fa-fw.icon-red.icon-md {
    display: none;
}

/* original selector: ul.forums */
ul.forums > li.row:first-child {
    background-color: #FEFFFC;
    /* Old browsers */
    /* FF3.6+ */
    background-image: none;
}

/* added */
ul.forums > li.row:first-child:hover {
    background-color: #bbd9ac;
    background-image: none;
}

i.icon.fa-angle-left.fa-fw.icon-black {
    color: #ffffff;
}

div.stat-block.permissions p {
    color: #ffffff;
    padding-left: 10px;
    padding-bottom: 10px;
}

div.stat-block.permissions h3 {
    padding-left: 10px;
    color: #ffffff;
}

.row .pagination li a,
.row .pagination li span {
    font-size: 1.0em;
}

/* thread mods */
.postbody .content {
    font-size: 1.2em;
}

div.content {
    font-size: 1.4px;
}


dd.profile-rank {
    margin-top: 10px;
    margin-bottom: 10px;
}

div.notice {
    font-style: italic;
}

div.action-bar.bar-bottom {
    padding-left: 10px;
    padding-right: 10px;
}

/* Poster profile block */
.postprofile {
    color: #424242;
    border-color: #dadada;
    font-size: 1.1em;
}

.pm .postprofile {
    border-color: #dadada;
}

.postprofile strong {
    color: #000000;
}

/* Quote block */
blockquote {
    background-color: #ffffff;
    border-color: #424242;
    border-style: dashed;
}

blockquote blockquote {
    background-color: #fdfdfd;
    border-color: #424242;
    border-style: dashed;
}
blockquote blockquote blockquote {
    background-color: #dbdbdb;
    border-color: #424242;
    border-style: dashed;
}

/* Code block */
.codebox {
    background-color: #FFFFFF;
    border-color: #d70b29;
    margin-left: 25px;
}

.codebox p {
    border-bottom: none;
}

.codebox code {
    color: #2E8B57;
}

/* images*/
img.postimage {
    border: none;
    margin-top: 10px;
    margin-bottom: 10px;
    padding: 0;
}


/* reply page */
div.panel {
    background-color: #e0edd3;
}

h3.review {
    padding-left: 10px;
    padding-right: 10px;
    color: #ffffff;
}

span.right-box a {
    color: #ffffff;
}

a.top span {
    color: #ffffff;
}

div.panel.bg2 {
    margin-top: -5px;
}

#postform hr {
    display: none;
}


/* Form button styles
---------------------------------------- */
a.button1,
input.button1,
input.button3,
a.button2,
input.button2 {
    border: none;
    color: #fff;
    background-color: #5e7553;
    /* Old browsers */
    /* FF3.6+ */
    background-image: none;
    padding: 5px;
}

a.button1,
input.button1 {
    border: none;
}

input.button3 {
    background-image: none;
}

/* Alternative button */
a.button2,
input.button2,
input.button3 {
    border: none;
}

/* <a> button in the style of the form buttons */
a.button1,
a.button2 {
    color: #000000;
}

/* Hover states */
a.button1:hover,
input.button1:hover,
a.button2:hover,
input.button2:hover,
input.button3:hover {
    border: none;
    color: #5e7553;
    background-color: #ffffff;
    background-image: none;
}

/* Focus states */
input.button1:focus,
input.button2:focus,
input.button3:focus {
    border: none;
    color: #6DAF25;
}

input.disabled {
    color: #666666;
}

/* remove curved corners */
.forabg {
    margin-bottom: 4px;
    padding: 5px;
    clear: both;
    border-radius: 0px;
}

.forumbg {
    margin-bottom: 4px;
    padding: 5px;
    clear: both;
    border-radius: 0px;
}

.panel {
    margin-bottom: 4px;
    padding: 5px 10px;
    border-radius: 0px;
}


.rowbg {
    margin: 5px 5px 2px 5px;
}

.post {
    border-radius: 0px;
    margin-bottom: 15px;
    margin-top: 15px;
    padding: 15px;
}

rules.rules-link {
    border-radius: 0px;
}

div.rules {
    border-radius: 0px;
}

.loading_indicator,
.dropdown,
.dropdown .dropdown-contents,
.dropdown-extended .header {
    border-radius: 0px;
}

