css:
@-moz-document domain("facebook.com") {
/* chat box size correction */
div.fbNubFlyoutOuter > div.fbNubFlyoutInner > div:nth-child(2),
._s0f ._2sz2 {
	height: 428px!important;
}
div.fbNub._50mz.opened {
    width: 324px!important;
}
/* text bubble size correction */
div._5w1r,
div._5w1r._3,
div._4gx_ {
    width: 224px!important;
 	max-width: 224px!important;
}
/* text bubble content size correction */
div._4gx_ > div > div,
div._4gx_ > div > div > span {
    max-width: 211px!important;
 	width: 211px!important;
}
}
