css:
@-moz-document domain(vkontakte.ru) {

#pageLayout {padding-top: 25px !important}
#pageHeader {background-image: none !important; width: 100% !important; height: 25px !important; position: fixed !important; top: 0 !important; opacity: .75 !important; z-index: 3 !important}
   #pageHeader #home {display: none}

#header span {display: none}
#quickquery {width: 175px !important; margin-left: 0px !important; position: relative; top: -1px !important}

.topNav a {text-transform: capitalize; height:21px !important}
.topNav a:hover {background: #90ABCB; text-decoration: none !important}
ul.topNav li {padding-top: 0px !important; background: none !important; width: 10% !important; border-left: 1px solid #3e6b95 !important; height: 25px !important}
#quickSearch {padding-top: 0px !important}

#pageLayout, #pageBody, #rightColumn, .dataWrap {width: auto !important; float: none !important}
#rightColumn {position: relative }

#pageBody {margin-left: 118px !important}
   #pageBody #header {padding: 4px 10px !important}

#sideBar {margin-left: 0px !important; background: #6587A7 !important; position: fixed !important; top: 20px;}
   
   #sideBar #nav li {background: #6587A7 !important; border: none !important;}
   #sideBar #nav li a {background: #6587A7 !important; border: none !important; color: #fff !important;}
      #sideBar #nav li a:hover {background: #90ABCB !important}
   #sideBar #nav ol li {background: #6587A7 !important; border: none !important;}

   .leftAd {border-top: 1px solid #fff !important; border-bottom: 1px solid #fff !important; margin-left: -6px !important; background: #6587A7 !important;}
      .leftAd a {color: #fff !important;}
      .leftAd a:hover {text-decoration: underline !important;}

#banner2, #banner1, .ad_box {display: none !important;}

.newRow {background: #E5E9EB !important}

}
