css:
@-moz-document domain("skidrowcodex.net") {
div.blog-content > h2 > a {
    font-size: 17px;
    font-weight: normal;
    font-family: sans-serif;
    text-transform: none;
}
div.psearch-content {
    font-size: 0px;
}
div.psearch-content {
    display: block;
    float: left;
    width: 100%;
    overflow: hidden;
    height: 15px;
    border: none;
    box-shadow: none;
    border-bottom: 1px solid #484848;
    
    height: auto;
    width: 99%;
    padding: 0;
    margin: 0;
    max-height: 34px;
    min-height: 33px;
}
.blog-content.wcontainer h2 {
    font-size: 26px;
    padding-left: 10px;
}
div.container.blog > div.row > div  {
    border-top: 4px solid #366182;
    padding-top: 1px;
}
}
