css:
@-moz-document regexp("http://211.86.224.21/")/*该网址仅用于过审*/ {
    /*全局变量*/
    * {
        /*设置全局字体*/
        font-family:"Microsoft Yahei UI", Verdana, Simsun, "Segoe UI", -apple-system, BlinkMacSystemFont, Roboto, "Helvetica Neue", sans-serif;
      }
    /*body*/
    .login_bg {
	    background: url(http://www.pptbz.com/d/file/p/201708/a1d07b6201af8f574b6539cb724bbc16.png) repeat-x left top!important;
        /*设置页面背景图片*/
    }
    .login_logo {
	    background: no-repeat right top;
	    /*删除方正Logo*/
    }
    .login_left .login_pic {
	    display: none;
	    /*删除左登陆栏的图片*/
    }
    .login_left {
	    width: 190px;
	    /*调整左登陆栏大小使右登陆栏具中*/
	    background: no-repeat;
	    /*删除左登陆栏图片*/
    }
    .login_right {
	    height: 350px;
	    width: 515px;
	    background: no-repeat;
	    background-color: white;
	    box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
	    -webkit-box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
	    border-radius: 2px;
	    margin-bottom: 12px;
    }
    .login_right DL {
	    MARGIN: 0px 0px 0px 133px;
	    MIN-HEIGHT: 0px;
	    WIDTH: 285px;
    }
    dd, th {
	    background-color: white;
    }
    td, th {
	    background-color: white;
    }
    p, th {
	    background-color: white;
    }
    #RadioButtonList1 {
	    background-color: white;
    }
    #lbYhm {
	    background-color: white;
	    color: black;
    }
    #txtUserName {
	    background-color: white;
    }
    .login_right .uesr {
        background: no-repeat left 0;
        text-indent: 0em;
    }
    .login_right .passw {
        background: no-repeat left 0;
        text-indent: 0em;
    }
    .login_right .yzm {
        background: no-repeat left 0;
        text-indent: 0em;
    }
    #lbMm {
	    background-color: white;
	    color: black;
    }
    #TextBox2 {
	    background-color: white;
    }
    #lbYzm {
	    background-color: white;
	    color: black;
    }
    #txtSecretCode {
	    background-color: white;
    }
    #icodems {
	    background-color: white;
    }
    label {
	    background-color: white;
	    color: black;
    }
    .login_copyright span {
        color: #616161;
    }
}
