css:
@-moz-document domain("/*[[adgh]]*/") {
body {
    background-color: #18191a;
    color: #b5b2ac
}
a {
    color: #6e94bf
}
.header {
    background-color: #242526;
    color: #eee;
    border-bottom-color: #005eeb1f;
    border-bottom: 1px solid #ababab1f
}
.form-control--search {
    color: #eee;
    box-shadow: #2b2f31 0px 1px 0px;
}
.logs__table .rt-thead,
.logs__table .rt-tbody {
    min-width: 100% !important;
    background-color: #17191a;
}
.dropdown-menu {
    background-color: #121213;
    color: #eee;
}
.icon--green {
    color: #72bb7e;
}
.dropdown-item:hover,
.dropdown-item:focus {
    color: #dfdcd8;
    text-decoration-color: currentcolor;
    background-color: #151516;
}
.dropdown-item.active,
.dropdown-item:active {
    background-color: #151516;
}
.custom-pagination .-btn:enabled:hover {
    background-color: #18191A !important;
}
.pagination-bottom {
    background-color: #121213;
}
.rt-tr-group.green {
    background-color: #3f7d5a1a;
}
.rt-tr-group.red {
    background-color: #b22d0e0d;
}
.card {
    background-color: #151516
}
.logs__table .rt-tr-group:not(:first-child) .rt-tr::before {
	content: "";
	position: absolute;
	left: 0rem;
	right: 1.5rem;
	top: 0;
	width: calc(100%);
	height: 1px;
	background-color: #464140;
}
.logs__table .rt-thead, .logs__table .rt-tbody {
    min-width: 100% !important;
    background-color: #121213;
}
.tooltip-custom__container {
    padding: 1rem 1.5rem 1.25rem 1.5rem;
    font-size: 16px !important;
    -webkit-box-shadow: 2px 4px 8px rgba(0, 0, 0, 0.2);
    box-shadow: 2px 4px 8px rgba(0, 0, 0, 0.2);
    border-radius: 4px !important;
    pointer-events: auto !important;
    background-color: #18191A !important;
    z-index: 102;
    overflow-y: scroll;
    max-height: 100%;
}
.icon--selected {
    background-color: #1f2123;
    border-color: #3b4042;
}
.tooltip-container {
    background-color: #121213;
}
.logs__text,
.logs__row .list-unstyled li {
    color: #B3B0AA;
}
.footer {
    background-color: #18191a;
    border-bottom: 1px solid #ababab1f
}
.footer a:not(.btn) {
    color: #66b574
}
div.footer {
    background-color: #18191a
}
div.header__column.mobile-menu {
    background-color: inherit
}
.nav-tabs .nav-item.show {
    background-color: #343;
}
.nav-tabs .nav-item:not(.show) .nav-link {
    color: #467fcf;
}
.nav-tabs .nav-item.show {
    background-color: #242526
}
.nav-tabs .nav-item.show .nav-link {
    color: #467fcf;
    border-bottom-color: #467fcf;
    background-color: inherit
}
.nav-tabs .nav-item.show .dropdown-menu.show {
    background-color: #181a1b;
    color: #66b574
}
.nav-tabs .nav-item.show .dropdown-menu.show .dropdown-item {
    background-color: #181a1b;
    color: #b9b5ab
}
.nav-tabs .nav-item.show .dropdown-menu.show .dropdown-item.active,
.nav-tabs .nav-item.show .dropdown-menu.show .dropdown-item:hover {
    background-color: #181a1b;
    color: #467fcf
}
.nav-tabs .nav-item .nav-link.active {
    color: #467fcf;
    border-color: #467fcf
}
.header-toggler.d-lg-none.ml-lg-0.collapsed {
    color: #5eba00
}
div.header__column.mobile-menu.mobile-menu--active {
    background-color: #222
}
div.header__column.mobile-menu.mobile-menu--active .nav-item.border-bottom.d-lg-none {
    border-bottom: 1px solid #363!important
}
div.header__column.mobile-menu.mobile-menu--active .nav-tabs .nav-item .nav-link:not(.active) {
    color: #363
}
div.header__column.mobile-menu.mobile-menu--active .nav-tabs .nav-item .nav-link.active {
    text-decoration: underline
}
.badge-success {
    background-color: #5ba167
}
.col-lg-3 {
    -ms-flex: 0 0 25%;
    flex: 0 0 25%;
    max-width: 25%
}
.card-table tr td,
.card-table tr th {
    border-color: #30363d
}
.rt-tr.red {
    background-color: #4f3333
}
.rt-tr.green {
    background-color: #343
}
.rt-tr.-even {
    filter: brightness(85%)
}
.service {
    border-color: #363636
}
.service__icon {
    color: #b6b6b6
}
.ReactModal__Overlay.ReactModal__Overlay--after-open {
    background-color: #000000BF!important
}
.modal-content,
.tab__control {
    color: #eee
}
.overlay.overlay--visible {
    background-color: #000C!important;
    color: #363
}
.ReactTable .-loading,
.card-body--loading::before,
.loading::before {
    background-color: #0009!important
}
.card {
    position: relative;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-clip: border-box;
    border: 1px solid #ababab1f!important;
    border-radius: 3px
}
.text-red {
    color: #cd201f!important
}
.alert-info {
    background-color: #132948;
    border-color: #0c192d;
    color: #467fcf
}
.alert-info .container a {
    color: #66b574
}
.ReactTable .-pagination .-btn,
.ReactTable .-pagination input,
.ReactTable .-pagination select,
.ReactTable .rt-thead.-filters input,
.ReactTable .rt-thead.-filters select,
.btn-secondary,
.custom-select,
.custom-switch-indicator,
.form-control,
.form-control--textarea,
.form-control:disabled,
.form-control:focus,
.form-control[readonly],
.select.select--language {
    background-color: #222;
    color: #eee
}
.btn-secondary {
    background-color: #363
}
.btn-success {
    color: #fff;
    background-color: #363;
    border-color: #363
}
.btn-success:disabled {
    color: #fff;
    background-color: #363;
    border-color: #363
}
.checkbox__label-subtitle,
.form__desc {
    color: #aaa
}
.logs__input-wrap .tooltip-custom--logs {
    filter: invert(.9) saturate(5) hue-rotate(50deg)
}
.rt-td .logs__action {
    background-color: #222
}
.rt-tr-group .red {
    background-color: #482020
}
.rt-tr-group .green {
    background-color: #254425
}
.ReactTable .rt-noData {
    background-image: initial;
    background-color: rgba(24, 26, 27, .8);
    color: rgba(232, 230, 227, .5)
}
.modal-content {
    background-color: #181a1b;
    outline-color: initial;
    border-color: rgba(102, 102, 102, .2)
}
.btn-gray {
    color: #fff;
    background-color: #3b3e41;
    border-color: #464b51
}
.toast {
    color: #fff;
    background-color: rgba(157, 15, 15, .75)
}
.toast--success {
    background-color: #5aad63bf
}
.red.p-3.mb-0.card {
    background-color: #242526
}
.css-yk16xz-control {
    background-color: #242526;
}
.css-yk16xz-control.select__control {
    background-color: #242526;
}
.css-1pahdxg-control {
    background-color: #242526;
}

.basic-multi-select .select__control {
    background-color: #242526;
}

.basic-multi-select .select__control:hover {
    background-color: #242526;
}

.basic-multi-select .select__control--is-focused,
.basic-multi-select .select__control--is-focused:hover {
    background-color: #242526;
}

.basic-multi-select .select__placeholder {
    background-color: #242526;
}

.basic-multi-select .select__menu {
    background-color: #242526;
}
:root {
    scrollbar-color: #242526 #18191a !important;
    scrollbar-width: thin !important;
}
::-webkit-scrollbar {
    background-color: #18191a !important;
}
::-webkit-scrollbar-thumb {
    background-color: #242526 !important;
}
.btn-secondary:hover {
    color: #ffffff;
    background-color: #4b9400;
    border-color: #0014311f;
}
.btn-outline-danger {
    color: #F00;
    background-color: #242526;
    border-color: #cd201f;
}
.header-brand-img {
    position: relative;
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
.h-6 {
    position: relative;
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
}
