css:
@-moz-document url-prefix("https://www.schul-netz.com/altekanti/") {
/* Variables for values and stuff */
:root {
    --bg: #121212;

    --elevation: rgba(255, 255, 255, .05);
    --elevation-hv: rgba(255, 255, 255, .025);
    --elevation-sd: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.16);

    --elevation-sd-h: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);

    --divider: rgba(255, 255, 255, .05);

    --edge: 5px;

    --p: #303f9f;
    --p-l: #666ad1;
    --p-d: #001970;

    --t: #ffffff;
    --t-p: #ffffff;
    --t-warn: #d05353;
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/") {
/* cleaning elements */
/* Reseting styles */
* {
    color: initial !important;
    background: initial !important;
    box-shadow: initial !important;
}

/* hiding global elements */
.mdl-tooltip,
.cls-page--header-row--logo-container,
#circle-progress {
    display: none !important;
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/") {
/* Adding global styles */
/* applies default font config */
* {
    color: var(--t) !important;
}

/* applies elevated */
.mdl-button,
#outer-div-Lehrerliste,
.mdl-card,
.cls-page--header,
.mdl-data-table,
#nav-drawer-content,
.mdl-menu__container,
#timer,
.dhx_cal_light,
.dhx_btn_set > *,
#buttons-container {
    background-color: var(--elevation) !important;
    box-shadow: var(--elevation-sd) !important;

    transition: background-color 0.25s ease-out !important;
}

/* applies more depth */
.mdl-menu__container,
.cls-page--header {
    box-shadow: var(--elevation-sd-h) !important;
}

/* applies a hover effect to elevated elements */
.mdl-button:hover,
.dhx_btn_set > *:hover {
    background-color: var(--elevation-hv) !important;
}

/* applies bg */
body,
.mdl-menu__container,
.dhx_cal_light,
#buttons-container {
    background: var(--bg) !important;
}

/* applies warn color */
#standardformular > div > .mdl-cell--12-col {
    color: var(--t-warn) !important;
}

/* applies divider */
hr {
    background-color: var(--divider) !important;
}

/* alternative divider */
.mdl-data-table > tbody > tr > td,
.dhx_wrap_section {
    border-color: var(--divider) !important;
    border-width: 2px !important;
}

/* applies primary */
#mainnav-small,
.dhx_btn_set > *,
.cls-page--header {
    background-color: var(--p) !important;
    color: var(--t-p) !important;
}

/* applies border radius */
.mdl-menu__container,
.mdl-card,
.mdl-data-table,
.dhx_cal_light,
#navigation-list > *:not(hr),
.dhx_btn_set > * {
    border-radius: var(--edge) !important;
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/") {
/* specific fixes for global elements  */
/* various drawer fixes */
.cls-page--navlist-lvl1,
.cls-page--navlist-lvl2 {
    padding: initial !important;
    line-height: initial !important;
    font-size: initial !important;
    font-weight: initial !important;
    color: var(--t) !important;
}

#navigation-list {
    border: none !important;
}

#navigation-list > *:not(hr) {
    display: block !important;
    padding: 5px !important;

    transition: background-color 0.25s ease-out !important;
}

#navigation-list > *:not(hr):hover {
    display: block !important;

    background-color: var(--elevation-hv) !important;
}

#navigation-list > #mainnav-small:hover {
    background-color: var(--p) !important;
}

#mainnav-small > button {
    display: none !important;
}

.mdl-layout--fixed-drawer > .mdl-layout__content {
    margin: 0 !important;
}

#nav-drawer-content {
    transition: transform 0.25s ease-out !important;
    background: var(--bg) !important;
}

.mdl-button {
    margin: 5px !important;
}


.mdl-layout--fixed-drawer .tbl-header-line {
    max-width: initial !important;
}

.cls-page--navlist {
    padding: 5% !important;
}

.cls-page--navlist > * {
    margin-bottom: 5% !important;
}

.cls-page--navlist-lvl2 {
    font-size: 90% !important;
    opacity: 0.5 !important;
}

/* various fixes to tables */
.cls-table--icons > div:not(.mdl-menu__container) {
    display: none !important;
}

#tbl-header-line > .cls-table--icons > .cls-table--count {
    display: inline-block !important;
}

.mdl-data-table__cell--non-numeric {
    border-color: var(--divider) !important;
}

.mdl-layout--fixed-drawer .tbl-header-line {
    max-width: initial !important;
}

/* popup fixes */
.mdl-menu__container {
    transform: translateY(10px) !important;
}

/* changes to timer */
#timer {
    transform: scale(1.35) translateY(0.5px) !important;
    border-radius: 100% !important;
}

/* timetable fix */
.dhx_cal_header > .dhx_scale_bar {
    background: none !important;
}

#nav-main-menu,
#menu-scroll-left,
#menu-scroll-right {
    display: none !important;
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/loginto.php") {
/* individual fixes */
/* removing unnessecary elements from frontpage */
.login-header,
.login-footer {
    display: none !important;
}

.loginform::before {
    content: "Willkommen beim Schulnetz der alten Kanti";
    text-align: center;
    padding-top: calc((100vh - 185px) / 20);
    font-size: calc((100vh - 185px) / 20);
    line-height: calc((100vh - 185px) / 16);
    height: calc((100vh - 185px) /2);
    display: inline-block;
}

.login-panel {
    vertical-align: initial !important;
}
}
