css:
@-moz-document domain("lemediatv.fr") {
body,
#panel {
    background-color: #1e1c1c;
}

.panel,
.posts,
.well,
select,
input,
.bootstrap-tagsinput,
.ui-menu-item,
.modal-body,
.modal-footer,
textarea,
.sc-htpNat,
.sc-cJSrbW,
fieldset,
.elementor-element,
.composer,
#redactor-uuid-0,
.title,
#redactor-uuid-1 {
    background-color: #262424 !important;
    color: #eaeaea !important;
}

.category .topic-list,
.category-children {
    background-color: #171616 !important;
}

.btn,
.dropdown-menu,
.tag,
.user-meta,
.panel-heading,
.modal-header,
.list-group-item,
.sc-kgAjT,
#page-loader-pro,
.re-button,
#redactor-toolbar-0,
#redactor-toolbar-1 {
    background-color: black !important;
    color: #eaeaea !important;
}

.panel a,
.posts a,
.category .topic-list a,
.breadcrumb a,
.tags a span,
.alert a {
    color: #d5c2f9 !important;
}

h1,
h2,
h3,
h4,
h5,
h6,
span,
.close,
p,
.progression-blog-title a,
.blog-meta-category-list a {
    color: #eaeaea !important;
}

.elementor-button-text,
.elementor-button-icon {
    color: black !important;
}
}
