css:
/* :::::::::::::::::::::::::::::::::::::Do Not Remove This Line:::::::::::::::::::::::::::::::::::::::: */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* :::::::::::::::::::::::::::::::::Bookmarks Toolbar with Text Only::::::::::::::::::::::::::::::::::::*/

#bookmarksBarContent .toolbarbutton-text
{
margin: 0px 2px 0px 2px !important;
}

#bookmarksBarContent toolbarbutton[container="true"] .toolbarbutton-icon
{ 
display: none !important; 
}

#bookmarksBarContent toolbarbutton[container="true"]  .toolbarbutton-menu-dropmarker 
{
display: -moz-box !important;
padding-right: 2px !important;
padding-left: 2px !important;
}


/* ::::::::::::::::::::::::::::::::::::::::::::Findbar::::::::::::::::::::::::::::::::::::::::::::::::::*/

#FindToolbar > *,
.findbar-container > * 
{
display:-moz-box !important;
}

#FindToolbar 
{ 
background: rgb(175, 181, 194) !important;
border: 0px !important;
}


/*::::::::::::::::::::::::::::::::::Background color of loading page::::::::::::::::::::::::::::::::::::*/

tabbrowser tabpanels 
{
background-color: rgb(255, 255, 255) !important; 
}


/*::::::::::::::::::::::::::::::::::::::::::::::::Tabs::::::::::::::::::::::::::::::::::::::::::::::::::*/

.tabbrowser-tabs
{
-moz-appearance: none !important;
background: black !important;
}

.scrollbutton-up,
.scrollbutton-down,
.tabs-alltabs-button,
.tabs-newtab-button,
.tabbrowser-tab:not([selected="true"]),
.tabbrowser-tab:not([selected="true"]):hover
{
padding-top:2px !important;
-moz-appearance: tab !important;
color: black !important;

}

.tabbrowser-tab {
margin-right:-1px !important;
-moz-border-radius-topleft:2px !important;
-moz-border-radius-topright:2px !important;
}


.tabbrowser-tab:not([selected="true"])
{
opacity: 0.999 !important;
}

.tabbrowser-tab:not([selected="true"]):hover
{
opacity: 0.999 !important;
padding-left:0px !important;
margin-top:3px !important;
}

.tabbrowser-tab[selected="true"]
{
min-width: 150px !important;
margin-top:-0px !important;
-moz-appearance: tab !important;
opacity: .99 !important;
}

.tabs-bottom
{
border-bottom: none !important;
}


/*:::::::::::::::::::::::::::::::::::::::::: Toolbars ::::::::::::::::::::::::::::::::::::::::::::::::::*/

#navigator-toolbox,
#navigator-toolbox #toolbar-menubar,
#navigator-toolbox #nav-bar,
#navigator-toolbox #PersonalToolbar
{
-moz-appearance: none !important;
border: 0 !important;
}

#navigator-toolbox
{
-moz-appearance: -moz-win-browsertabbar-toolbox !important;

border: none !important;
padding: 0px 0px 2px 0px !important;
}

#navigator-toolbox,
#navigator-toolbox .toolbarbutton-text,
#navigator-toolbox #PersonalToolbar .toolbarbutton-text,
#navigator-toolbox #toolbar-menubar .menubar-text
{
color: black !important;
}

#placesToolbar
{
-moz-appearance: none !important;
/*background: #ffffff url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAABRCAIAAACc%2BVspAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAB3RJTUUH2AkVABMcLYDs4wAAAAd0RVh0QXV0aG9yAKmuzEgAAAAMdEVYdERlc2NyaXB0aW9uABMJISMAAAAKdEVYdENvcHlyaWdodACsD8w6AAAADnRFWHRDcmVhdGlvbiB0aW1lADX3DwkAAAAJdEVYdFNvZnR3YXJlAF1w%2FzoAAAALdEVYdERpc2NsYWltZXIAt8C0jwAAAAh0RVh0V2FybmluZwDAG%2BaHAAAAB3RFWHRTb3VyY2UA9f%2BD6wAAAAh0RVh0Q29tbWVudAD2zJa%2FAAAABnRFWHRUaXRsZQCo7tInAAAAS0lEQVQYla2OMQ6AQAgE5%2Fb%2Fz7WxGhskeMbOgoSdZQGO0wgBo0SIEiBqazHImKlceZ0pBo7%2B5jtz8985xm763tBYv072R62nXp%2BzF7naOVmec%2FPYAAAAAElFTkSuQmCC") repeat-x top !important;*/
border-bottom: 1px solid #A9B7C9 !important;
}

#placesToolbar .menubar-text,
#placesToolbar .toolbarbutton-text
{
color: black !important
}

#organizerScopeBar .toolbarbutton-text,
#organizerScopeBar,
sidebarheader
{
color: black !important
}

#organizerScopeBar
{
background: white !important;
}

sidebarheader
{
-moz-appearance: none !important;
/*background: rgb(231,237,245) !important;*/
border: 0 !important;
}

#history-panel > hbox,
#bookmarksPanel > hbox
{
-moz-appearance: none !important;
/*background: rgb(231,237,245) !important;*/
border: 0 !important;
}

#bookmarks-view,
#historyTree 
{
-moz-appearance: none !important;
border: none !important;
/*background: rgb(231,237,245) !important;*/
}

#placesView tree
{
background-color: rgb(255,255,255) !important;
}

#infoPaneBox
{
background-color: rgb(245,245,245) !important;
}

#splitter,
#contentSplitter,
#sidebar-splitter 
{

}

#contentSplitter grippy,
#splitter grippy 
{
visibility: hidden !important;
}


/*:::::::::::::::::::::::::::::::::::::: Status Bar Style ::::::::::::::::::::::::::::::::::::::::::::::*/

statusbarpanel
{
-moz-appearance  : none !important;
border: none !important;
}


/*:::::::::::::::::::::::::::::::::::::::: Tab Bar on Top ::::::::::::::::::::::::::::::::::::::::::::::*/

@-moz-document url("chrome://browser/content/browser.xul")

{
.tabbrowser-strip
{
position:fixed;
top:0px;
left:-4px;
right:-1px;
z-index:0;
min-width:100%!important;
}

tab
{
-moz-user-select:-moz-none!important;
z-index:0!important;
-moz-border-radius-bottomleft: 0px!important;
}

.tabbrowser-strip .tabbrowser-tabs
{
width:100%!important;
}

.tab-drop-indicator-bar
{
position:fixed!important;
top:0px;
left:0px;
width:100%!important;
z-index:1;
}

window
{
padding-top:29px;
}
}
