    margin: 7px 0 !important;
}

.Header-menu-link>div>a {
    padding: 0 !important;
    margin: 7px 0 !important;
}

.Search {
    margin: 7px 16px 0 0 !important;
}

.public-DropMenu-link {
    padding: 6px 0 2px 0 !important;
}

.UserInfo.public-DropMenu {
    padding: 5px 0 !important;
}

.public-DropMenu-drop {
