}

.layout-Module-head.is-fixed {
    top: 50px !important;
}

.layout-Header .Header-wrap .Header {
    width: 90% !important;
}

.wm-general {
    margin-top: 70px !important;
    height: 0 !important;
}

.layout-Main {
    max-width: 100% !important;
    width: 91% !important;
    margin: 0 auto !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
}
