css:
@-moz-document domain("play.pokemonshowdown.com") {
html,
* {
    font-family: Calibri, FontAwesome !important;
}
* {
    border-radius: 0px !important;
}

body {
    background-color: #222 !important;
}

.header {
    background: none !important;
    background-color: #222 !important;
    border: 0 !important;
}
.ps-popup {
    background: #1a1a1a !important;
}
.roomtab {
    background-color: #3d3d3d !important;
    border: 1px solid #333 !important;
    border-bottom: 0px !important;
    box-shadow: none !important;
    padding-bottom: 10px !important;
}
.notifying {
    background-color: #3a0000 !important;
}
.cur {
    background-color: #444 !important;
}
.maintabbarbottom {
    background-color: #444 !important;
    border: none !important;
    box-shadow: 0px 5px 2px !important;
}
.ps-room {
    background-color: #1a1a1a !important;
    border: 1px solid #222 !important;
}
.ps-room-light {
    background: #262626 !important;
}
.ilink {
    color: lightgray !important;
    border: 1px solid #4f4f4f !important;
    background-color: #444 !important;
}
.pm-window > h3 {
    background: rgba(32, 32, 32, 1) !important;
    border: 1px solid #222 !important;
}
.pm-log {
    background-color: #333 !important;
    border: 1px solid #222 !important;
    border-bottom-left-radius: 6px !important;
    border-bottom-right-radius: 6px !important;
}
.newsentry {
    border-bottom: 1px solid #222 !important;
    color: #c1c1c1 !important;
}
.unread {
    background: none !important;
}
.select {
    background: rgba(112, 112, 112, 1) !important;
    color: #e1e1e1 !important;
}

a {
    text-decoration: none !important;
    color: #d1d1d1 !important;
}
h3 {
    color: #e1e1e1 !important;
}
.mainmenuwrapper {
    background: url(https://image.freepik.com/free-photo/dark-seamless-black-square-grid-pattern-art-design-wall-background_25920-1451.jpg) !important;
    background-attachment: scroll;
}
.icon {
    border: none !important;
}

.pad > .button,
td > .button,
.pad > p > .button,
.controls > p > .button,
.controls > p > span > .button {
    border: none !important;
}

.button.mainmenu1,
.button.mainmenu2,
.button.mainmenu3,
.button.mainmenu4,
.button.mainmenu5 {
    background: #404040 !important;
    border: 1px #000;
}

.button.mainmenu1:hover,
.button.mainmenu2:hover,
.button.mainmenu3:hover,
.button.mainmenu4:hover,
.button.mainmenu5:hover {
    background: #808080 !important;
    border: 1px #000;
}

.select > strong {
    color: white !important;
}

.battle,
.innerbattle {
    border: none !important;
}

.leftbar,
.rightbar {
    background: #000000e6 !important;
    border: none !important;
}

.battle-controls,
.controls {
    background-color: #1a1a1a !important;
}

.battle-log {
    background-color: #222 !important;
    border: none !important;
}

.userlist {
    background-color: #2d2d2d !important;
    border: none !important;
}

.battle-history {
    border: none !important;
    padding: 2px !important;
}

.inner > h2 {
    background-color: #2a2a2a !important;
    padding-left: 10px !important;
}

.inner > .mine {
    background-color: #222 !important;
}

.battle-log-add {
    background-color: #2d2d2d !important;
    border: none !important;
}

.text-area {
    border-color: #555 !important;
}

.span {
    text-shadow: none !important;
}

.tabbar.maintabbar {
    margin-bottom: -1px !important;
}

#header,
#wrapper .header,
.pfx-topbar,
.pfx-topbar.header,
.ps-header {
    background: #0D0D0D;
    border: 0
}
#header,
#wrapper .header {
    height: 45px
}
.pfx-topbar,
.pfx-topbar.header {
    height: 60px
}
.logo {
    display: none !important;
}
#header:after,
.nav-first img,
.ps-nav li a img {
    content: url("http://i.imgur.com/1nGotRD.png");
    padding-left: 50px;
    z-index: 1;
    position: relative;
}
.userbar {
    top: 10px;
}
.maintabbarbottom {
    background: #4675aa;
    border: none;
    bottom: -10px;
    height: 10px;
}
.tabbar a.button {
    height: 40px;
    cursor: pointer;
    text-align: center;
    color: #38342A;
    background: #000;
}
.tabbar a.button:hover {
    background: #9ABEE8;
    color: #FFF;
}
.tabbar a.button:active {
    background: #478BD9;
    border-radius: 0;
    height: 40px;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
    color: #FFF;
    background: #478BD9;
}
.siderooms li {
    z-index: 1;
    margin-right: 1px;
}
.tabbar li:first-child a.button,
.tabbar li:last-child a.button {
    border-radius: 0;
}
.closebutton,
.minimizebutton {
    color: #38342A;
}
.closebutton:hover,
.minimizebutton:hover,
.closebutton:active,
.minimizebutton:active {
    color: #DDD;
}
.button.notifying {
    border-color: #AA8866!important;
    background: #e3c3a3!important;
}
.button.notifying:hover {
    border-color: #604020!important;
    background: #cfaf8f!important;
}
.button.cur,
.button.cur:hover {
    color: #FFF!important;
    background: #478BD9!important;
    box-shadow: none;
    border-color: #AAAAAA;
    cursor: default;
}
.button.subtle-notifying {
    color: #AA6600;
}
.button.subtle-notifying .icon-comment-alt:before,
.button.notifying .icon-comment-alt:before {
    content: "\f0e6";
}

.ps-room.ps-room-light.scrollable ul li button {
    border-radius: 3px;
    border: 1px solid #000;
    margin-bottom: 2px;
    background: #000;
    color: #F2F2F2;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
}

.ps-room.ps-room-light.scrollable ul li button:hover,
.ps-room.ps-room-light.scrollable ul li button:focus {
    color: #0094FF;
}

button[name="edit"],
button[name="delete"] {
    margin-left: 2px;
}

.teamchartbox ::-moz-selection {
    background: #000 !important;
    color: #e7f5ff !important;
}

.teambuilder-results h3:after,
.teambuilder-results .utilichart h3:after,
.setchart-nickname:after,
.statform:after,
.detailsform:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/wleX36y.png);
    width: 45px;
    height: 7px;
    position: absolute;
    left: -1px;
    top: -7px;
}

.teambuilder-results h3,
.teambuilder-results .utilichart h3,
.statform,
.detailsform {
    position: relative;
    border-top-left-radius: 0px;
    margin-top: 5px;
}

.setchart-nickname {
    border-top-left-radius: 0px;
    border-top-right-radius: 2px;
    margin-top: 2px;
}

.teamchartbox {
    border-top: none;
}

.teambuilder-pokemon-import .textbox {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.teambuilder-pokemon-import .textbox:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.teambuilder-pokemon-import .textbox:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.teambuilder-results h3,
.teambuilder-results .utilichart h3 {
    border-color: #181818;
    background: #181818;
    color: #FFF;
    text-shadow: 1px 1px #000;
    box-shadow: none;
    margin: 10px 0px 5px 0px;
}

.setchart {
    border-color: #000;
    background: #181818;
    background-repeat: no-repeat;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
    margin-top: 9px;
    border-radius: 0px;
}

.setchart label,
.setchart-nickname label {
    color: #FFF;
    text-shadow: 1px 1px #000;
}

.setchart input,
.setchart-nickname input,
.setdetails,
.setstats,
.statform input.inputform {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.setchart input:hover,
.setchart-nickname input:hover,
.setdetails:hover,
.setstats:hover,
.statform input.inputform:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.setchart input:focus,
.setchart-nickname input:focus,
.setdetails:focus,
.setstats:focus,
.statform input.inputform:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.setdetails .detailcell {
    border-left: 1px solid #000;
}

.setchart-nickname {
    border-color: #181818;
    background: #181818;
    box-shadow: none;
    border-radius: 0px;
    border-top-left-radius: 0px;
}

.teambuilder-results .firstresult a {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.2);
}

.teambuilder-results .firstresult a:hover {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.25) !important;
}

.teambuilder-results .result a:hover {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.2);
}

.utilichart b {
    color: #000;
}

.utilichart .numcol {
    color: #713624;
}

.utilichart .typecol img {
    opacity: 0.85;
}

.setstats .statrow-head em {
    color: #EEE;
}

.teambar button {
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
    overflow-x: hidden;
    background: #181818;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    border-color: #000;
}

.teambar button:hover {
    background: #303030;
    color: #F2F2F2;
    border-color: #000;
}

.teambar button:disabled,
.teambar button:disabled:hover,
.teambar button:disabled:active {
    background: rgba(0, 0, 0, 0.75);
    opacity: 1;
    border-color: #000;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.setmenu {
    top: 3px;
}

.setmenu button:hover {
    background: rgba(0, 0, 0, 0.4);
    border-color: #000;
    color: #000;
    text-shadow: none;
}

.utilichart .labelcol em,
.utilichart .widelabelcol em,
.utilichart .pplabelcol em,
.utilichart .statcol em,
.utilichart .bstcol em {
    color: #555;
    font-size: 10px;
}

.utilichart .itemdesccol,
.utilichart .abilitydesccol,
.utilichart .movedesccol {
    color: #555;
}

.utilichart .col {
    color: #222;
}

div[style="position: absolute; margin: 0px; top: 123px; left: 495.667px;"] {
    border: 1px solid #000;
    color: #FFF;
    background: #181818;
    box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.2) inset, 0px 0px 0px rgba(228, 228, 228, 0.1) inset, 0px 0px 3px rgba(0, 0, 0, 0.5);
}

div[style="position: absolute; margin: 0px; top: 123px; left: 495.667px;"] .popupmenu button {
    color: #FFF;
    padding: 1px 4px;
}

div[style="position: absolute; margin: 0px; top: 123px; left: 495.667px;"] .popupmenu button:hover {
    border-color: #000;
    color: #FFF;
    background: #101010;
    padding: 0px 3px;
}

.statform {
    border-color: #000;
    background: #181818;
    border: 1px solid #000;
    margin-top: 15px;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    border-radius: 0px;
    border-top-left-radius: 0px;
}

.statform button {
    background: rgba(0, 0, 0, 0.5);
    color: #F2F2F2;
    border: 1px solid #000;
    border-radius: 0px;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
}

.statform button:hover {
    color: #0094FF;
}

.jslider .jslider-value {
    border: 1px solid #000;
    background: #151515;
}

.jslider .jslider-label {
    color: #FFF;
    opacity: 0.75;
}

button[name="back"],
button[name="import"],
.majorbutton,
button[name="saveImport"],
button[name="undeleteSet"],
button[name="savePokemonImport"],
button[name="closePokemonImport"],
button[name="backup"],
button[name="saveBackup"],
button[name="validate"],
.teampane .button,
.format-select .select,
.format-select .button,
.teamchart .button {
    background: #181818;
    color: #F2F2F2;
    border: 1px solid #000;
    border-radius: 0px;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
}

.teampane .button,
.format-select .button,
.teamchart .button {
    box-shadow: 0 -1px 2px #000 inset;
    padding: 5px 9px;
}

.format-select .select {
    box-shadow: none;
    border-top-left-radius: 0px;
}

button[name="back"]:hover,
button[name="import"]:hover,
.majorbutton:hover,
.majorbutton:focus,
button[name="saveImport"]:hover,
button[name="saveImport"]:focus,
button[name="undeleteSet"]:hover,
button[name="undeleteSet"]:focus,
button[name="savePokemonImport"]:hover,
button[name="savePokemonImport"]:focus,
button[name="closePokemonImport"]:hover,
button[name="closePokemonImport"]:focus,
button[name="backup"]:hover,
button[name="backup"]:focus,
button[name="saveBackup"]:hover,
button[name="saveBackup"]:focus,
button[name="validate"]:hover,
button[name="validate"]:focus,
.teampane .button:hover,
.teampane .button:focus,
.format-select .select:hover,
.format-select .select:focus,
.format-select .button:hover,
.format-select .button:focus,
.teamchart .button:hover,
.teamchart .button:focus {
    color: #d0d0d0;
}

div[style="position: absolute; margin: 0px; top: 176px; left: 7px;"].ps-popup {
    background: #333;
    border: 1px solid #000;
    color: #EEE;
    box-shadow: inset 1px 1px rgba(255, 255, 255, 0.3), inset -1px -1px rgba(0, 0, 0, 0.2), 2px 2px 3px rgba(0, 0, 0, .2);
