}

.textbox.teamnameedit {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.textbox.teamnameedit:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.textbox.teamnameedit:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.detailsform {
    border-color: #000;
    background: #181818;
    border: 1px solid #000;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
