}

.detailsform .textbox {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
}

.detailsform .textbox:hover {
    box-shadow: none;
    border-color: #000;
