css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hognation.proboards.com") {
.welcomebg img {
     display: none !important;
}
#google_ads_iframe_728x90_ROS, #google_ads_div_728x90_ROS {
     display: none !important;
}
a:visited, a:link, a:visited {
     color:#802323 !important;
     text-decoration:none !important;
     font-weight:normal;
}
a:hover {
     color:#8A0B0C !important;
     text-decoration:underline overline !important;
     font-weight:normal;
}
body {
     background:#666666!important;
}
.windowbg {
     background:#b4b4b4 !important;
}
.windowbg2 {
     background:#959595 !important;
}
td {
     color:#000000 !important;
}
.welcomebg {
     background:#000000!important;
     color:#ffffff !important;
     width:50%;
}
.welcomebg a {

     text-decoration: underline !important;
     font-weight: bold !important;
}
.catbg {
     background:#999999 !important;
}
.menubg {
     background:#999999 !important;
}
.bordercolor{
     border: 1px solid #000000!important;
}
.nav, .group0, .group1 {
     font-weight:bold !important;
}
.titlebg, .newstitlebg {
     background:#666666 !important;
}
.quote {
     background: #888888 !important;
     color:#333333 !important;
     font-size:8.5pt !important;
}
body center font, body center a, body center iframe {
     display:none !important;
}
.nav {
     font-size:8pt !important;
}
#fscroller {font-size:14pt !important; height:20px !important;}
}
