css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .news-teaser
  {
    color: #000;
  }

  .flash--beta
  {
    display: none;
  }

  .theme--pDefault .profile-bg
  {
    background: url("http://fullhdwall.com/wp-content/uploads/2017/10/Blue-Shapes-Wallpaper.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #fffbfb;
  }
  
  .theme--pDefault .profile-body
  {
    color: #0a0a0a;
  }
  .theme--pDefault .profile-bgHead
  {
    background: url("http://fullhdwall.com/wp-content/uploads/2017/10/Blue-Shapes-Wallpaper.jpg") top left repeat, linear-gradient(0deg, #2fffbfb 10%, #ffffff 100%);
  }

  .card--a
  {
    background-color: #fff0;
  }

  .card--shadow-l
  {
    box-shadow: none;
  }

  html
  {
    background: fixed url("https://serfob.s3.amazonaws.com/media/flat-blue-background-with-geometric-shapes4d9-6a6a-48c9-a088-26763b305390.png") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
  }

  .structure-ad
  {
    display: none;
  }

  .ad
  {
    display: none;
    position: relative;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
  }

  .ad--sq
  {
    display: none;
    width: 336px;
    height: 280px;
    background: #f2f2f2;
  }

  .ad--ldrbrd
  {
    display: none;
    width: 728px;
    height: 90px;
    background: #f2f2f2;
  }

  .ad--ldrbrd_l
  {
    display: none;
    width: 970px;
    height: 90px;
  }

  .ad--fixed
  {
    display: none;
    position: fixed;
    top: 30px;
  }

  .structure-header
  {
    margin-left: auto;
    margin-right: auto;
    max-width: 1020px;
    padding-left: 0px;
    padding-right: 0px;
  }

  .card--grit
  {
    background-image: url("http://fullhdwall.com/wp-content/uploads/2017/10/Blue-Shapes-Wallpaper.jpg");
  }

  .table--b .table-cell
  {
    border-bottom: none;
  }

  .tc-ts
  {
    color: #fffbfb;
  }

  .card--b
  {
    color: #fffbfb;
  }

  .tc-emerald
  {
    color: #09a94d;
  }
  .tc-i {
    color: #f9f3f3;
  }

  .tc-i
  {
    color: #f6f1f1;
  }

  .tc-dodger
  {
    color: #f12c0b;
  }

  .tc-lemon
  {
    color: #d2f907;
  }

  .news--solo .news-body p
  {
    color: #000;
  }

  .news--solo .news-body .yellow
  {
    color: #a28100;
  }

  .link--nav
  {
    color: #000000ba;
  }

  .link--nav.is-current
  {
    color: #000;
  }

  .link--nav:hover
  {
    color: #000;
  }

  .bg--gradient
  {
    background: #00000000;
  }
  .btn--a {
    background: linear-gradient(to right, #172fde, #07278e);
  }
  .btn--a {
    background: linear-gradient(to right, #1122ec, #f9f3f3);
    border: 1px solid #fbfafa;
  
}
  path:nth-child(2)
  {
    fill: #f3eded;
  }

  path:nth-child(3)
  {
    fill: #f3eded;
  }

  path:nth-child(4)
  {
    fill: #f3eded;
  }

  path:nth-child(5)
  {
    fill: #f3eded;
  }

  path:nth-child(6)
  {
    fill: #f3eded;
  }

  path:nth-child(8)
  {
    fill: #f3eded;
  }
}

@-moz-document url-prefix("https://www.nitrotype.com/api")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  html
  {
    background: fixed url("https://imgur.com/AQlOcaA.png") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #000;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
  }
}
@-moz-document domain(nitrotype.com)
{

  .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #2eb0e8;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }
}
