css:
@-moz-document url-prefix("https://mp3.pm") {
header {
    background-image: linear-gradient(to bottom, #000, #F0F) !important;
    box-shadow: inset 0 -1px 0 0 #F0F, inset 0 -2px 0 0 #F0F !important;
}
body {
    color: #FFF !important;
}
.onesongblock h3.onesongblock-title i {
    color: #F5F !important;
}
#banner-top {
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important;
}
#banner-bottom {
    border-top: 1px solid #F0F !important;
    border-bottom: 1px solid #F0F !important;
}
.ng-scope {
    background-color: #333 !important;
}
#search_bl {
    background-color: #555 !important;
    box-shadow: inset 0 -1px 0 0 #555, inset 0 -2px 0 0 #555 !important;
}
#search_bl h5 {
    color: #FFF !important;
}
#search_bl em a {
    background-image: linear-gradient(to bottom, #F0F, #000) !important;
}
#search_bl-s {
    background-image: linear-gradient(to bottom, #888, #555) !important;
    box-shadow: 0 0 0 1px #888, 0 0 0 2px #888, inset 0 1px 0 0 #888 !important;
}
#search_bl-s i input[type="text"] {
    color: #FFF !important;
}
#xx1 {
    background-color: #444 !important;
}
.onesongblock {
    background-color: #555 !important;
    box-shadow: inset 0 -1px 0 0 #555 !important;
    border-bottom: 1px solid #555 !important;
}
ul.mp3list li {
    background-color: #444 !important;
    box-shadow: inset 0 1px 0 0 #444, inset 0 -1px 0 0 #444 !important;
    border-bottom: 1px solid #F0F !important;
}
ul.mp3list li h4 i {
    color: #FFF !important;
}
ul.mp3list li em {
    color: #CCC !important;
}
span#fixplayer-progress_bar-now {
    background-color: #A0A !important;
}
.listalka em.listalka-back {
    background-color: #666 !important;
}
}
