@namespace url(http://www.w3.org/1999/xhtml);

/* ==== GOOGLE - RESULTATS RECHERCHE PERSONNALISÉS - OK FIREFOX 71  : DÉBUT ==== */
@-moz-document url-prefix(http://www.google),
                                 url-prefix(https://www.google)
{
	/* Définition des cartouches */
    .g,
    .g-blk,
    g-section-with-header
    {
        min-height: 200px !important;
        max-height: auto !important;
        min-width: 550px !important;
        max-auto: auto !important;
        padding-top: 32px !important;
        padding-left: 32px !important;
        padding-right: 32px !important;
        margin:20px 00px 40px -35px !important;
        background: LightBlue !important;
        border-top: Green 6px double !important;
        border-bottom: Red 6px double !important;
        border-left: Gold 6px double !important;
        border-right: Blue 6px double !important;
        border-radius: 20px !important;
        font-weight: bold !important;
    }
      g-scrolling-carousel
    {
        min-width: 200px !important;
        max-width: auto !important;
        padding-left: 10px !important;
        background: Transparent !important;
        font-weight: bold !important;
    }
    .vk_c .rllt__link
    {
        background: Transparent !important;
    }
/* -------- */
	/* Définition des cartouches imbriquées  dans celles de gauche */
    .vk_c
    {
        min-width: 300px !important;
        max-width: auto !important;
        padding-top: 32px !important;
        padding-left: 32px !important;
        margin:10px -10px 40px 00px !important;
        background: Transparent !important;
        font-weight: bold !important;   
    }
    g-inner-card
    {
        background: Transparent !important;
    }
/* -------- */
	/* Définition des cartouches imbriquées isolées */
    .mnr-c div
    {
        max-width: auto !important;
        font-weight: bold !important;
    }
/* -------- */
                   /* Définition cartouche "définition dictionnaire" */
    .lW8rQd,
    .Uekwlc
    {       
        font-weight: bold !important;
        color: Blue !important;
    }
/* -------- */
	/* Définition des cartouches menu-images */
    .lU8dJf
    {
        background: Transparent !important;
        font-weight: bold !important;
    }
/* -------- */
	/* Définition des cartouches droites */
    .mnr-c div,
    .mnr-a div,
    .mod
    {
        background: LightBlue !important;
    }
/* -------- */
	/* Définition des cartes de géographie */
#lu_map
{
        min-height: 250px !important;
        max-height: auto !important;
        min-width: 300px !important;
        max-width: 550px !important;
        margin-left:-25px !important;
        background-color: Transparent !important;
   }
/* -------- */
                   /* Titres des cartouches */
    h2,
    h3
    {       
        font-weight: bold !important;
    }
/* -------- */
                   /* Texte explicatif des cartouches gauches */
    .a,
    .s
    {
        font-weight: bold !important;
        font-size: 16px !important;
    }
    .st
    {
        font-weight: bold !important;
        font-size: 16px !important;
        color: DarkMagenta !important;
    }
                   /* Mots saisis pour la recherche (cartouches gauches) */
    .st em
    {
        font-weight: bold !important;
        font-size: 18px !important;
        color: Red !important;
        text-decoration: underline !important;
    }
                   /* Liens http des cartouches gauches */
    cite
    {
        font-weight: bold !important;
        font-size: 18px !important;
    }
/* -------- */
                   /* Police Menu-Google */
    #hdtbSum
    {
        font-size: 20px !important;
    }
/* -------- */
                   /* Bouton Menu-Google */
    #gbwa
    {
        background: DeepSkyBlue !important;
    }
/* -------- */
}
/* ==== GOOGLE - RESULTATS RECHERCHE PERSONNALISÉS : FIN ==== */
