css:
@-moz-document url-prefix("https://nekto.me/chat") {
body, .chat_container, .window_chat_dialog_new {
    color: silver;
    background-image: url('https://bipbap.ru/wp-content/uploads/2018/04/light_6_990x660-640x427.jpg') !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed;
    background-size: cover;
    background-color: hsla(70, 8%, 15%, 1);
}
.window_chat_ava, .tri::after, .tri::before {
    display:none;
}
.chat-box, .chat-box .header_chat, .lb_search, .navbar-brand, .navbar-inverse, .navbar-header, .collapse, .window_chat_statuss, .emojionearea, .box_message.message_box_1 .emojionearea, .window_chat_message, .message_box_smile_panel, .window_chat_dialog_write, .emojionearea .emojionearea-editor {
    color: hsla(50, 80%, 53%, 0.7);
    background-color: hsla(70, 8%, 15%, 1);
    border-color: hsla(70, 8%, 15%, 0.5);
}
.main_step .btn-default {
    color: hsla(50, 80%, 53%, 1);
    background-color: hsla(70, 8%, 15%, 1);
    border-color: hsla(50, 80%, 53%, 0.5);
}
button, .main_step .btn-default.checked, .smile_panel_button_send button {
    color: hsla(70, 8%, 15%, 1);
    background-color: hsla(50, 80%, 53%, 1);
    border-color: hsla(50, 80%, 53%, 0.5);
}
.nekto .window_chat_dialog_text {
    color: hsla(190, 81%, 53%, 1);
    background-color: hsla(70, 8%, 15%, 1);
    border-color: hsla(190, 81%, 53%, 0.5);
}
.mess_block.self .window_chat_dialog_text {
    color: hsla(80, 76%, 53%, 1);
    background-color: hsla(70, 8%, 15%, 1);
    border-color: hsla(80, 76%, 53%, 0.5);
}
.window_chat_dialog_time, .mess_block.self .window_chat_dialog_time {
    color: hsla(50, 80%, 53%, 0.3);
}
}
