css:
@-moz-document domain("braacket.com") {
/* Braacket.com dark theme
by mawk (mawwwk#1068)
*/

body {
    --main-bg-color: hsl(240, 11%, 13%);
    --panel-color: hsl(240, 11%, 18%);
    --panel-hover-color: hsl(240, 13%, 20%);
    --panel-mid-color: hsl(240, 9%, 25%);
    --panel-light-color: hsl(240, 8%, 20%);
    --panel-border-color: hsl(219, 3%, 29%);
    --button-color: hsl(240, 8%, 21%);
    --button-light-color: hsl(240, 8%, 27%);
    --link-color: hsl(205, 66%, 61%);
    --link-color-hover:hsl(205, 66%, 73%);
    --text-sub-color: hsl(0, 0%, 61%);
    --text-light: hsl(0, 0%, 73%);
    --text-light-2: hsl(0, 0%, 76%);
    --table-border-color: hsl(219, 3%, 24%);
}

html {
    background-color: hsl(240, 8%, 12%);
}

#content_body, .navbar-default, .navbar-default .navbar-collapse, .navbar-default .navbar-form {
    background-color: var(--main-bg-color);
    color: hsl(223, 22%, 83%);    
}

#content_header, #content_body, .my-navsub {
   box-shadow: 0 1px 3px 0 rgba(0, 0, 0, .56),
    0 1px 1px 0 rgba(52, 60, 88, .49),
    0 2px 10px 0 var(--main-bg-color);
}

.navbar-default, .navbar-default .navbar-collapse, .navbar-default .navbar-form, navbar:before {
    border: none;
}

.navbar-default .navbar-nav>li>a:hover, .navbar-default .navbar-brand:hover, .navbar-default .navbar-brand:focus, .navbar-default .navbar-nav>li>a:hover, .navbar-default .navbar-nav>li>a:focus {
    color:var(--text-light);
}

.navbar-custom div.dropdown-toggle:hover {
    color:var(--text-light-2);
}

.my-label-infos {
    color: var(--text-sub-color);
}

/* "Select all" in player list, other headers */
label {
    color:var(--text-light)
}

#footer {
    background-color: hsl(228, 12%, 12%);
    color: hsl(0, 0%, 45%);
}

/* Text form border */
.input-group-addon, .form-control {
    border-color:hsl(228, 12%, 30%);
}

.ads-desktop-bottom::before, .ads-desktop-bottom, .ads-desktop-bottom::after, #content {
    background-color:var(--main-bg-color);
    border-color: var(--table-border-color);
    border-width: 0;
}

.read-more-toggle {
    background: linear-gradient(
        hsla(240, 11%, 18%, .7),
        var(--panel-color)
    );
}

.modal-content {
    background-color:var(--main-bg-color);
}

/* Panels - league status, players, etc */
/* Header panels */
.table>thead:first-child>tr:first-child>thg, .table > thead > tr > th:not([class*="col-"]), .my-table-row-action, .my-panel-footer-action, .panel.my-panel-collapsed .panel-heading, .panel-default > .panel-heading {
    background-color:var(--panel-light-color);
    color:var(--text-light-2);
}

.panel-default>.panel-heading {
    border-color: var(--table-border-color)
}
/* Panel hover (account match history) */
.my-table-row-action > td > a:hover, .my-panel-footer-action > a:hover {
    background-color:var(--panel-hover-color);
}
.panel.my-panel-collapsed .panel-body .table, .panel-default, .panel-default > .panel-headin {
    color:var(--text-light-2);
    background-color:var(--panel-color);
    border-color:var(--panel-border-color);
}

.panel-footer, .panel.my-panel-collapsed .panel-heading+.panel-body, .panel.my-panel-collapsed .panel-body+.panel-body {
    border-color: var(--panel-border-color);
}

.table-responsive-overflow-right {
    background-image: linear-gradient(90deg, transparent, var(--main-bg-color));
}

/* Alert (i.e. ranking out of date) */
.alert-default {
    background-color: var(--panel-color);
}

/* Dashboard header */
/* Active icon */
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus {
    background-color:hsl(222, 9%, 27%);
    color:var(--text-light-2);
}

/* Inactive dashboard icons */
.navbar-default .navbar-nav>li>a {
    background-color:var(--main-bg-color);
}

/* Dashboard icon hover */
.my-navsub-ul > li {
    background-color:hsl(214, 7%, 22%);
}

.my-navsub-ul > li.active > a {
    border-bottom-color: hsl(215, 0%, 57%);
}

.my-navsub-ul::after {
    visibility: hidden;
}

/* Dashboard icon colors */
.my-navsub-icon {
    color:white;
}
.my-navsub-label {
    color:hsl(0, 0%, 86%);
}

/* Event name in leagues */
.my-panel-mosaic .panel-heading {
    background-color:var(--panel-light-color);

} 
.my-panel-mosaic .panel-heading td > a:not(.btn) {
    background-color:#fff0;
}

/* League owner namebadge */
a.badge-primary, .panel-default > .panel-heading .badge-primary {
    background-color: hsl(0, 0%, 17%);
}
/* League owner namebadge hover */
a.badge-primary:hover, a.badge-primary:focus, a.badge-primary:active, .panel-default > .panel-heading .badge-primary:hover, .panel-default > .panel-heading .badge-primary:focus, .panel-default > .panel-heading .badge-primary:active {
    background-color: hsl(0, 0%, 8%);
}
/* "Detail" bar and others */
.my-panel-footer-action > a {
    background-color: var(--panel-color);
    color: var(--link-color);
}

a:hover, a:focus {
    color:var(--link-color-hover);
}

a {
    color: var(--link-color)
}

/* Head to head page */
table tr > th, .table>tbody>tr.active>td {
    background-color: var(--panel-color);
}

/* League/ranking hover */
.table-hover>tbody>tr:hover, .table-hover > tbody > tr.active:hover > td {
    background-color: var(--panel-light-color);
}

/* Import league players from tournament - name field */
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: var(--panel-color);
}
/* H2H table cells */
.table>thead:first-child>tr:first-child>th, .table-bordered>thead>tr>th, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>td, .table > thead > tr > th:not([class*="col-"]), .table > tbody > tr > td:not([class*="col-"]), section:not(:last-child) {
    border-color:var(--table-border-color);
}

.alert-success {
    background-color: hsl(120, 17%, 17%) !important;
    color: hsl(102, 25%, 37%);
}
.alert-danger {
    background-color: hsl(355, 14%, 17%) !important;
    color: hsl(0, 28%, 39%);
}

.alert-warning {
    background-color: hsl(57, 20%, 14%) !important;
    color: hsl(56, 25%, 34%);
}

/* Forms, menus, buttons */
.search-box .input-group-addon:first-child, .search-box .form-control, .search-box .form-group.active .input-group-addon, .btn-default {
    background-color: hsl(240, 8%, 18%);
    border-color:var(--table-border-color);
    color:hsl(0, 0%, 82%);
}

/* "Admin" menu for league tournament */
.my-panel-mosaic .panel-heading a:not(.btn) {
    color: var(--text-light);
}
.search-box .form-group.active .form-control:not(:focus), .search-box .input-group-btn .btn, .search-box .btn-group .btn, .search-box .form-control, .input-group-addon+.input-group-addon, .input-group-addon:not(:first-child):not(:last-child), .input-group-btn:not(:first-child):not(:last-child), .input-group .form-control:not(:first-child):not(:last-child), .btn-sm, .btn-group-sm > .btn, .open>.dropdown-toggle.btn-default, .open>.dropdown-toggle.btn-default:hover {
    background-color: hsl(240, 8%, 20%);
    border-color:var(--table-border-color);
    color:var(--text-color-3);
}

/* Dropdown menus */
.dropdown-menu {
    background-color: var(--button-color);
}

.dropdown-menu>li>a, .dropdown-header {
    color:var(--text-light)
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
    background-color:var(--button-light-color);
    color: var(--text-light);
}

/* Typeable forms (league settings) */
.input-group-addon, .form-control, .disabled input.form-control, .disabled select.form-control {
    background-color: hsl(240, 8%, 12%);
    color:var(--text-color-3);
}

.search-box .form-group.active .input-group-addon, .search-box .form-group.active .form-control:not(:focus), .search-box .form-group.active .btn {
    border-color: hsl(240, 8%, 29%);
}
/* Buttons */
.btn-group>.btn-group:first-child:not(:last-child)>.btn:last-child, .btn-group>.btn-group:first-child:not(:last-child)>.dropdown-toggle, .btn-group>.btn-group:not(:first-child):not(:last-child)>.btn, .btn-group>.btn-group:last-child:not(:first-child)>.btn:first-child {
    background-color: var(--button-color);
    color:var(--text-color-3);
    border-color: var(--table-border-color);
}

/* Buttons hover */
.btn-group>.btn-group:last-child:not(:first-child)>.btn:first-child.btn:hover,.btn-group>.btn-group:not(:first-child):not(:last-child)>.btn:hover, .btn-group>.btn-group:first-child:not(:last-child)>.btn:last-child:hover, .btn-group-vertical>.btn:hover, .btn-group>.btn:focus, .btn-group-vertical>.btn:focus, .btn-group>.btn:active, .btn-group-vertical>.btn:active, .btn-group>.btn.active, .btn-group-vertical>.btn.active {
    background-color: var(--button-light-color) !important;
    color: var(--text-light-2);
    border-color: hsl(0, 0%, 36%);
}

.btn-default:hover {
    background-color: var(--button-light-color);
    color: var(--text-light-2);
}

/* Disabled buttons */
.input-group-addon:not(:first-child):not(:last-child) {
    background-color: hsl(240, 8%, 16%);
}
/* League stats chart, imported from google? hard to change eh */
rect {
    fill: hsl(0, 0%, 40%);
}

/* Misc text */
.text-success {
    color:hsl(120, 80%, 30%);
}
.text-danger {
    color:hsl(0, 80%, 41%);
}

/* "Text color black" - shows in League search players */
.text-color-black {
    color: var(--text-light-1) !important;
}

}
