css:
@-moz-document domain("vogons.org") {
html, :root {
    background: #333;
}

.root {
    background: transparent;
    border-color: rgba(255,255,255,0.1);
}

.table_head_label {
    background: transparent;
}

.post:nth-child(2n) {
    background: rgba(255,255,255,0.1);
}

.panel, .popup_content, .check-list_list, .alert_title, .table_row:nth-child(2n+1) {
    background-image: none;
}
.post_header {
    background: rgba(0,0,0,0.1);
}

.bb_quote, .bb_code, .bb_codeview {
    background: rgba(255,255,255,0.1) !important;
}

.post {
    border-width: .0625rem;
    border-color: #666;
}
}
