css:
@-moz-document url-prefix("https://wikipedia.org"), url-prefix("https://ru.wikipedia.org"), url-prefix("https://en.wikipedia.org") {

body {
    color: #f0f0f0;
    background-color: #282b30;
}


    a,
    .tocnumber {
        color: #6a8de3 !important;
        text-decoration: none;
    }

    a:hover {
        color: #526699 !important;
        text-decoration: underline;
    }

    #p-logo a {
        background-image: url("data:image/png;base64,") !important;
        background-color: #282b30 !important;
    }

#mw-head,.mw-body, .parsoid-body {
    background-color: #282b30;
    border: 0px;
}

.mw-parser-output .main-box, .mw-parser-output .main-top {
    background-image: none;
    background-color: #373b3e;
    border: 0px;
    border-radius: 6px;
    color: #f0f0f0;
}

.mw-ui-button {
    color: #f0f0f0;
    background-color: #212326;
    border: solid 1px #212326;
    border-radius: 5px;
}

.mw-ui-button:hover {
    color: #f0f0f0;
    background-color: #212326;
    border: solid 1px #939597;
    border-radius: 5px;
}

.mw-body-content h2 {
     color: #f0f0f0;
}

img {
    border: 0;
    vertical-align: middle;
    border-radius: 3px;
}

.mw-parser-output .main-top-left {
    background-image: linear-gradient(to right,#373b3e 0%,#373b3e 70%,rgba(248,249,250,0)100%);
}


.mw-body h1, .mw-body-content h1, .mw-parser-output .main-top-right .mw-ui-button.mw-ui-quiet {
    color: #f0f0f0;
}

.vectorTabs ul {
    display: none;
}

.vectorTabs, .vectorTabs a, #mw-head .vectorMenu h3 {
    background-image: none;
}

#simpleSearch {
    display: block;
    width: 13.2em;
    width: 20vw;
    min-width: 5em;
    max-width: 20em;
    height: 100%;
    margin-top: 0;
    position: relative;
}

#searchInput {
    background-color: #212326;
    color: #f0f0f0;
    width: 100%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border: 1px solid #212326;
    border-radius: 5px;
    padding: 0.4em 1.84615385em 0.4em 0.4em;
    -webkit-box-shadow: inset 0 0 0 1px transparent;
    box-shadow: inset 0 0 0 1px transparent;
    font-size: 0.8125em;
    direction: ltr;
    -webkit-transition: border-color 250ms,box-shadow 250ms;
    -moz-transition: border-color 250ms,box-shadow 250ms;
    transition: border-color 250ms,box-shadow 250ms;
    -webkit-appearance: textfield;
    outline:none;
}

#searchInput:hover {
    background-color: #212326;
    color: #f0f0f0;
    width: 100%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border: 1px solid #212326;
    border-radius: 5px;
    padding: 0.4em 1.84615385em 0.4em 0.4em;
    -webkit-box-shadow: inset 0 0 0 1px transparent;
    box-shadow: inset 0 0 0 1px transparent;
    font-size: 0.8125em;
    direction: ltr;
    -webkit-transition: border-color 250ms,box-shadow 250ms;
    -moz-transition: border-color 250ms,box-shadow 250ms;
    transition: border-color 250ms,box-shadow 250ms;
    -webkit-appearance: textfield;
    outline:none;
}

#searchInput:active {
    background-color: #212326;
    color: #f0f0f0;
    width: 100%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border: 1px solid #212326;
    border-radius: 5px;
    padding: 0.4em 1.84615385em 0.4em 0.4em;
    -webkit-box-shadow: inset 0 0 0 1px transparent;
    box-shadow: inset 0 0 0 1px transparent;
    font-size: 0.8125em;
    direction: ltr;
    -webkit-transition: border-color 250ms,box-shadow 250ms;
    -moz-transition: border-color 250ms,box-shadow 250ms;
    transition: border-color 250ms,box-shadow 250ms;
    -webkit-appearance: textfield;
    outline:none;
}

#searchInput:focus, #simpleSearch:hover #searchInput:focus {
    outline:none;
    border-color: #afb3b9;
    -webkit-box-shadow: none;
    box-shadow: none;
}

#mw-page-base {
    background-color: #282b30;
    background-image: none;
}

#p-personal li {
    text-decoration: none;
}

.mw-ui-button.mw-ui-quiet, .mw-ui-button.mw-ui-quiet.mw-ui-progressive, .mw-ui-button.mw-ui-quiet.mw-ui-destructive {
    color: #ccc;
}



.mw-body-content {
    color: #d9d9d9;
}

.mw-body-content h3, .mw-body-content h4 {
    color: #f0f0f0;
}

div.thumbinner {
    background-color: #373b3e;
    border: 1px #373b3e;
    border-radius: 4px;
}

.toc, .mw-warning, .toccolours {
    background-color: #373b3e;
    border: 1px #373b3e;
    border-radius: 4px;
}

.mw-editsection {
    display: none;
}

table.ambox {
    display: none;
}

.navbox, .navbox-subgroup, .navbox th, .navbox-title {
    background: #373b3e;
    border-color: #373b3e;
    border-radius: 4px;
}

tr + tr > .navbox-abovebelow, tr + tr > .navbox-group, tr + tr > .navbox-image, tr + tr > .navbox-list {
    border-top: 1px solid #282b30;
    background-color: #373b3e;
}

.navbox-list {
    border-color: #282b30;
}

.catlinks {
    border: 1px solid #373b3e;
    background-color: #373b3e;
    border-radius: 4px;
}

