css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("truuconfessions.com") {

div {background:none !important}
body div div * {font-size:13px !important}
p a {color:#999 !important}
li a {color:#000 !important}
a.permalink {background:none !important;text-align:left !important;display:inline !important}
a.active {font-weight:700 !important}

/* layout */
#container, #footer {max-width:999px !important;padding:0 !important;margin:0 auto !important}
#main {width:60% !important;padding:0 !important;margin:0 1em !important}
#right-col {width:30% !important;padding:0 !important;margin:0 1em !important}
#right-col * {border:0px !important}
#right-col textarea {width:90% !important}
#right-col div[style] {padding:0 !important}
#footer {letter-spacing:-0px !important;font-size:7px !important}

/* login */
#welcome-box {max-width:999px !important;padding:0 !important;margin:0 auto !important}
#welcome-box * {display:inline !important}
#welcome-box div {padding:0 !important;margin:0 !important}
#welcome-box p.dotted {display:none !important}
#welcome-box input[type=text] {border:1px inset #777 !important}


/* stuff */
span.flag,
#right-col-inner,
#blogroll,
div.dotted,
#super-footer,
#footer img,
#totd,
#logo,
#topad,
p.dotted,
#page-title,
p.confession-tools-right a img,
#potd,
div.commentmetoo-container img  {display:none !important}

p.confession-tools-right {width:90% !important;display:block !important}
p.confession-tools-right a,
p.confession-tools-right span,
div.commentmetoo-container a {font-weight:500 !important;font-size:11px !important}

/* featured */
#rotate * {background:none !important}
#rotate,
div.colors-Single,
div.colors-Bride,
div.colors-Wife,
div.colors-Mom,
div.colors-Body,
div.colors-Office,
div.colors-Military {display:none !important}

/* settings nav */
#top-nav {margin:0 !important}
#top-nav-sub *,
#top-nav-sub a.active {font-weight:500 !important;font-size:9px !important}

/* paging */
#paging {border:1px solid #b0c75c !important;border-width:1px 0 !important;margin-bottom:11px !important}



/*confession box*/
div.confession-info,
div.comment-info {padding:0 8px !important;color:#999 !important;float:none !important;width:100% !important;text-align:right !important;font-size:9px !important}
div.confession-info a,
div.confession-info span,
div.comment-info a,
div.comment-info span {padding:0 4px !important;color:#999 !important;font-size:9px !important}
div.confession-info img,
div.comment-info img {display:none !important}
div.confession-body,
div.comment-body {min-height:0 !important}
div.confessionbox {padding:12px 0 !important;border-color:#678e10 !important;border-style:dotted !important;border-width:1px 0 0 0 !important}
div.confessionbox br {display:inline !important;float:left !important}
p.confession-tools-right,
p.confession-tools-left {float:right !important;display:inline !important;margin:0 !important;text-align:left !important;display:inline !important;padding:2px 4px !important}
div.comment-box-bottom {display:none !important}
div.commentmetoo-container * {color:#999 !important}
div.commentmetoo-container {float:right !important;display:inline !important;
padding:0 !important;margin:0 !important}
div.comment-info {float:right !important;display:inline !important;
padding:0 !important;margin:0 !important}

div.confession-info a.permalink {font-size:10px !important;background:#678e10 !important;color:#fff !important;text-transform:uppercase;font-weight:700 !important;margin-left:4px !important}
div.confession-info a.permalink {-moz-border-radius:2px;border-radius:2px;}
div.confession-info a.permalink:hover {background:#90b000 !important;text-decoration:none !important}

/* confession form */
#confess {padding:0 !important;margin:0 !important;border:0 !important}
#confessionForm img {display:none !important}
#confess textarea,
#confess input[type=text] {border:1px inset #777 !important}
#confess input#submit,
#confess input.button {border:1px outset #777 !important;background:none !important;color:#000 !important;letter-spacing:-0em !important}

/* related tags */
div.related-tags * {color:#999 !important;font-size:10px !important}
div.related-tags {padding:6px 0 0 0 !important;margin:0 !important;text-align:right !important}


}
