css:
@-moz-document regexp("https?://.*(?<!jira.corp)(?<!confluence.corp)(?<!crucible.corp)(?<!teamcity.corp)(?<!eng)(?<!www).netsuite.com.*") {
/* Home Page     */

:root {
    color: white;
}

#ns-dashboard-page {
    background-color: #121212;
}

.ns-portlet-wrapper, .ns-loading-portlet-wrapper {
    background-color: #FFFFFF19;
}

.ns-portlet-wrapper div .ns-portlet-header-text {
    color: white;        
}

.ns-portlet-wrapper .ns-portlet-content > :not(.ns-dashboard-form) a {
    color: #8da1ec;        
}

.ns-portlet-wrapper, .ns-loading-portlet-wrapper {
    color: white;
}

.ns-bankrec-tile {
    color: white;
}

.ns-portlet-wrapper .ns-portlet-content .ns-dashboard-action, .ns-portlet-wrapper .ns-portlet-content .ns-portlet-action {
    color: #8da1ec;
}

.ns-portlet-wrapper[data-portlet-type='calendar'] .ns-agenda-view .ns-day-label  {
    background-color: #FFFFFF20;
}

.ns-portlet-wrapper[data-portlet-type='calendar'] .ns-calendar-view-switch li {

    background-color: #FFFFFF24;
}


/*   Header CSS   */
.ns-logo, .uir-logo {
     filter: invert(100%) hue-rotate(180deg) saturate(800%); 
}

#ns_header {
    background-color: #121212
}

#ns_header .ns-quick-menu>.ns-header>a, #ns_header .ns-user>.ns-header>a {
    background-color: #121212;
}

#ns_header .ns-help {
    color: white;
}

#ns_header .ns-user .ns-role {
    color: white;
}

/*   End of header   */

.gridview[name='ns-portlet-kpi-grid'] table thead .ns-portlet-action, .gridview[name='ns-portlet-kpi-grid'] table thead .ns-portlet-action:hover {
    color: white;
}

#div__footer .content {
    background-color : #121212;
    color : gray;
}

#body, #div__footer, body {
    background-color: #121212 !important;
}

/*   General   */

div#body {
    background: #121212;
}

#ns-dashboard-heading-panel h1, .uir-page-title .uir-record-type {
    color: white;
}

.page-title-menu > .ns-menu > .ns-menuitem > a
{
    color: white;
    background: #121212;
}

.page-title-menu > .ns-menu > .ns-menuitem.ns-active > a
{
    color: white !important;
    background: #333333 !important;
}

span.uir-label .smallgraytext, span.uir-label .smallgraytextnolink, span.uir-label .smallgraytextbold, span.uir-label .smalltextnolink, span.uir-label .smalltextul {
    color: #FFFFFFde !important;

}

.input, .inputreadonly, .inputtotalling, .totallingcurrency {
    color: white !important;
}

#ns_header .ns-search input {
    color: white;
/*         background-color: #FFFFFF19; */
}

input {
    color: white !important;
    background-color: #FFFFFF19 !important;
}

.dropdownDiv {
    background-color: #121212 !important;
}

.dropdownNotSelected  {
    color: white;
    background-color: #FFFFFF19 !important;
}


/* Machine */
.bdtabblock, .subtabblock, .nltabcontent {
    background-color: #121212 !important;
}

.uir-machine-button-row > td
{
    background-color: #121212 !important;
}

.uir-machine-table-container {
    border: solid #cccccc
}

.listtable > tbody > tr:not(.uir-machine-row-last):not(.uir-machine-row-focused) > td {
    border-bottom: none !important;
}

.uir-machine-headerrow > td {
    background: #121212 !important;
}

.listheader, .listheadertextb, .listheadertextbrt, .listheadertextbctr {
    background: #121212 !important
}

.listheadertextb {
    color: white;
}

.uir-list-row-tr > td, .uir-machine-row > td, .uir-machine-totals-row > td {
    color: white !important;
}

.uir-machine-row-even > td, .uir-list-row-even > td, .uir-list-additional-label-row > td {
    background-color: #121212 !important;
}

.uir-machine-row-odd > td, .uir-list-row-odd > td {
    background-color: #333333 !important;
}

tr.listfocusedrow td {
    background: #666666 !important;
}

.uir-machine-row:not(.uir-machine-row-focused):hover > td
{
    background-color: #666666 !important;
}

.uir-machine-button-row .machineButtonRow > table
{
    background: #333333;
}

.bntBg > .nlinlineeditbutton {
    color: black !important;
}

.bntBgB > .nlinlineeditbutton, .machineButtonRow td:first-child + td input[type="button"] {
    color: white !important;
}
  
input[type="text"]:disabled, input[type="password"]:disabled, select:disabled, textarea:disabled, .dropdownDiv:disabled, .dropdownInput:disabled {
    background-color: #FFFFFF61 !important;
}

#description {
    background-color: #FFFFFF19;
}

div.uir-listpage-body {
    background-color: #121212 !important;
}

.bg {
    background-color: #FFFFFF19;
    color: white ;
}

A.hoverlink:link, A.hoverlink:visited, A.hoverlink:active {
    color: #8da1ec !important;
}

#div__bodytab {
    background-color: #121212;
}

.uir-list-header-td {
    color: white;
    background-color: #666666 !important;
}

.uir_filters_header {
    background-color: #666666;
    color: white;
}

.uir-page-title .uir-list-name {
    color: white;
}


/* main log in */
#main, .headline {
	background-color: #FFFFFF19;
}

h1, h1 > a {
	color: white;
}


.description {
	color: white;
}

.description > a {
	color: #8da1ec;
}

.more-link > a {
	color: #8da1ec;
}

.partner-description {
	color: white;
}

.partner-description > a {
	color: #8da1ec;
}

#rememberMeLabel {
	color: #aaaaaa;
}

.privacy_statement_inline_block {
	color: white;
}

.privacy_statement_static a:visited, .privacy_statement_inline_block a:visited, .pwd_reset_inline_block a:visited, .portal_inline_block a:visited {
	color: #8da1ec !important;
}

.privacy_statement_static a:link, .privacy_statement_inline_block a:link, .pwd_reset_inline_block a:link, .portal_inline_block a:link {
	color: #8da1ec;
}
/* End of main log in  */

/*login*/
.page-message, .smalltextnolink, .smalltext {
    color: white;
}

.privacy_statement_static a:visited, .privacy_statement_inline_block a:visited, .pwd_reset_inline_block a:visited, .portal_inline_block a:visited, .bottom-links a, .bottom-links a:active, .bottom-links a:visited, .privacy_statement_static a:link, .privacy_statement_inline_block a:link, .pwd_reset_inline_block a:link, .portal_inline_block a:link {
	color: #8da1ec;
}

.n-w-heading.n-loginchallenge-heading--page-titles-shared, .n-widget.n-loginchallenge-custom-heading {
    color: white;
}

.n-w-label.style-checkbox, .n-w-field_label {
    color: #aaaaaa;
}

/* End of login */
/* role selection */
.n-chooserole-table--role-picker tbody tr:nth-child(odd) {
    background-color: #333333
}
/* End of role selection */

.listcontrol input[type="text"]:focus, .listcontrol input[type="text"]:active {
	background-color: #FFFFFF12 !important;
}

.dropdownNotSelected > a {
	color: white !important;
}

div.uir-field-wrapper span.uir-field .dottedlink {
	color: #8da1ec;
}

.uir-list-row-tr .dottedlink {
    color: #8da1ec !important;
}

.uir-list-header-td .listheader {
    color: white;
}



/* Price book  */
.uir-list-headerrow.noprint > .uir-list-header-td  > .listheader  {
	background: #666666 !important;
}

.uir-list-headerrow.noprint > .uir-list-header-td > .listheader > .listheadertextb > tbody  > tr td  {
	background: #666666 !important;
}

.uir-list-headerrow.noprint > .uir-list-header-td > .listheader > .listheadertextb > tbody  > tr td > a  {
	background: #666666 !important;
}


.listtext, .listtexthl, .xxlisttexthl, .listtexthlrt, .listtexthlctr, .listtextctr {
	background: #333333 !important;
    color: white;
}

div.uir-field-wrapper span.uir-field .dottedlink {
	color: #8da1ec;
}

.uir-page-title .uir-record-name {
	color: white;
}

.uir-list-row-tr.uir-list-row-even a {
	color: white !important;
}

.uir-list-row-tr.uir-list-row-even > td > .dottedlink, .uir-machine-row .dottedlink {
	color: #8da1ec !important;
}

/* End of Price book  */

.listheadertextbrt {
	color: white;
}


tr.uir-list-row-tr:hover td {
	background-color: #555 !important;
}

.pgm_action_menu {
	color: #8da1ec;
}

.ac_table {
	background-color: #333333 !important;
    color: white !important;
}

.ac_container > .pgm_bg {
	background: #333333 !important;
    box-shadow: 0 4px 0 0 #333333, 0 0 2px 2px rgba(0,0,0,0.2);
}


.x-window-mc {
	color: white !important;
	background-color: #333333;
}

#body_div, #body_div .text {
	color: white !important;
	background-color: #333333 !important;
}

.x-window-bl {
	background-color: #333333;
}

body[link="#000000"] {
	background-color: #333333 !important;
}

#fieldhelp_frame, #custstdflh_frame {
	background-color: #333333 !important;
}

#userLangText {
	background-color: #666666;
}

.body_2010.nl-pagetint {
	background-color: #121212 !important;
}


#memo {
	background-color: #FFFFFF19;
}

.ns-portlet-wrapper div .ns-portlet-controls {
	 background: linear-gradient(to right, rgba(122, 122, 1222, 0), rgba(66, 66, 66, 1) 20%) !important;
}

.uir-field.inputreadonly b, .uir-field-wrapper b, .uir-field-wrapper a {
	color: #777777;
}

textarea, #defaultaddress {
	background: #FFFFFF19 !important;
}


 .ext-strict, .body_2010.nl-pagetint.ext-webkit.ext-chrome, #childdrecord_frame, #childrecord_fame  {
	background-color: #333333 !important;
}

.nlsubtabcontent {
	background-color: #333333;
}

.textareainput, .textareainputreq, .inputreq {
	color: white !important;
}

 html[lang] > .body_2010.nl-pagetint.ext-webkit.ext-chrome {
	background-color: #121212 !important;
}

#loginForm + table a {
	color: white;
}

/* start issue record */
span[style="color:#888"] {
    color: #bbb !important;
}

a {
    color: white;
}
/* end issue record */
}
