css:
@-moz-document url-prefix("https://bsaber.com/") {
body, h1, h3, h4, .widget, .widget a, .small-12, .style5-container, .rwp-scores-sliders label{
	background-color: rgb(20, 20, 20) !important;
    color: grey !important
}

.youzer, .yz-settings-form, .yz-profile-picture h2, .yz-my-account-widget{
	background-color: rgb(40, 40, 40) !important;
}

.youzer-section-content .field-visibility-settings-toggle, .field-visibility-settings-notoggle{
	background-color: rgb(60, 60, 60) !important;
    border-left-color: rgb(60, 60, 60) !important;
}

h4 a, p strong, p a, .rwp-review div span:first-child, .article-tags a, .author-content a, .songs-filter a{
	color: rgb(180, 180, 166) !important;    
}

.post-row a{
    background-color: rgb(100, 100, 100);
    color: white !important;
    
}

.post-bottom-meta, .post-bottom-meta a, .post-row span, .filter-checkbox span{
	color: grey !important;
}

.bsaber-categories a{
	color: white !important;
}

input[type="text"]{
	background-color: white !important;
    color: black !important;
}

.rwp-users-score span{
	background-color: #e73f3f !important;
    color: white !important;
}

.songs-filter a:hover{
	color: #e73f3f !important;
}
}
