css:
@-moz-document url-prefix("https://wx.qq.com/") {
.dropdown_menu {
    background-color: rgb(89, 89, 89) !important;
    border: unset;
}
.main .copyright {
    display: none;
}
.dropdown_menu li a {
    color: white;
}
dropdown_menu li:hover a {
    display: none;
    background-color: rgba(255, 255, 255, .1) !important;
}
.header .avatar .img {
    border-radius: 50px;
}
.main_inner {
    border-radius: 20px;
    transition: border-radius 500ms;
}

@media screen and (max-width: 1000px) {
    .main_inner {
        border-radius: 0px;
        transition: border-radius 500ms;
    }
}
.btn_send {
    border-radius: 20px;
    transition: all .4s;
    border: none;
    background: #80969D;
    color: white;
    box-shadow: 0 4px 20px rgba(0, 0, 0, .29);
}
.btn_send:hover {
    border-radius: 20px;
    background: #475357;
    color: white;
    box-shadow: 0 4px 10px rgba(0, 0, 0, .29);
}
.chat .box_ft .desc {
    display: none;
}
.dropdown_menu li a {
    border: none;
}
.download_entry {
    display: none;
}
.panel.give_me .nav_view {
    top: 165px;
}
.box,
.box_hd .title_wrap {
    background-color: #2E3238;
    margin: unset;
}
.box_hd .title .title_name,
.box_hd .title,
.profile .nickname,
.profile_mini_bd .nickname {
    color: white;
}
.box_hd .title_wrap {
    border-bottom: unset;
}
.chat .box_ft {
    border-top: 2px solid rgba(255, 255, 255, .4);
}



.bubble.bubble_primary {
    background-color: #2b5366;
    color: white;
}
.bubble.bubble_primary.right.arrow_primary:before,
.bubble.bubble_primary.right:after {
    border-left-color: #2b5366;
}
.chat .box_ft .content .flex {
    color: white;
}
.content.ng-isolate-scope pre::after {
    content: "输入消息...";
    position: absolute;
    color: grey;
    transition: 300ms all;
}
pre:hover::after {
    opacity: 0;
    transform: translateX(30px);
}
.chat .box_ft .toolbar a {
    transition: 300ms all;
    border-radius: 10px;
}
.chat .box_ft .toolbar a:hover {
    background-color: rgba(255, 255, 255, .3);
}
body {
    backdrop-filter: opacity(20%);
}
.chat_item {
    border-radius: 30px;
}
.chat_item:hover {
    transition: all 300ms;
    background: #3a3f45;
}
.chat_item.active {
    transition: all 300ms;
    background: #4f555e;
}
.reader .box_bd {
    padding: unset;
}
.profile_mini_bd,
.mmpop {
    background: #2E3238;
}
.members {
    border: unset;
    background: #4d545e;
}
.member .avatar {
    border-radius: 10px;
}
.reader .box_bd {

    background: #2E3238;
}

.contact_list .active {
    transition: all 300ms;
    background: #4f555e;
}

.contact_item {
    border-radius: 20px;
    transition: all 300ms;
}
.contact_item:hover {
    background: #3a3f45;
}
.search_bar {
    border-radius: 20px;
}

.ng-isolate-scope .active {
    border-radius: 20px;
}
.dropdown_menu li a {
    transition: all 300ms;
}
.dropdown_menu li a:hover {
    background: #25272b;
}
.login_box .qrcode .img {
    filter: invert(90%);
}
.login_box {
    background: #33363c;
    box-shadow: unset;
}
.login_box .qrcode .sub_title {
    color: white;
}
}
