css:
@-moz-document domain("drawception.com") {
:root {
 --bg-accent-color:#1c1c1c;
 --bg-accent-highlight-color:#181818;
 --bg-color:#292929;
 --bg-disabled-color:#1E1525;
 --bg-hover-color:#363636;
 --bg-highlight-color:#ffe973;
 --bg-duck-option:#f7f7f7;
 --bg-duck-option-hover:#fff;
 --text-color:#dedede;
 --text-hover-color:#f8f8f8;
 --text-color-lighter:#f8f8f8;
 --text-color-darker:#c5c5c5;
 --store-btn-bg-color:#424242;
 --store-btn-bg-hover-color:#5c5c5c;
 --store-btn-text-color:#eee;
 --text-color-muted:#b9859a;
 --border-color:#444;
 --border-highlight-color:#777;
 --comment-highlight-color:#444;
 --colorpicker-border-color:#3b2a51;
 --popup-border-color:#E974BD;
 --progress-bar-bg-color:#36254C;
 --navbar-border-color:#412f6b;
 --link-color: #E974BD;
 --navbar-btn-text-shadow:0px 0px 2px #000;
 --link-color-hover:#FF0DAB;
 --navbar-btn-border-hover-color:#ffe973;
 --navbar-btn-border-color:#ff3fd2;
 --navbar-bg-color:#573981;
 --navbar-btn-text-color:#ffe0f3;
 --navbar-btn-text-hover-color:#ff8fce;
}
blockquote {
    border: 1px solid #ff8fce;
}

a:hover {
    text-decoration-line: none;
}

a:focus {
    text-decoration-line: none;
}

.icon {
   -webkit-filter: hue-rotate(-90deg); 
}

.profile-nav > li > a:focus, .profile-nav > li > a:hover {
    background-color: #FF3FC5;
}

.nav-pills>li.active>a,.nav-pills>li.active>a:focus,.nav-pills>li.active>a:hover {
 background-color:#b73380;
}

.profile-nav > li.active > a, .profile-nav > li.active > a:focus, .profile-nav > li.active > a:hover {
    background-color: #FF3FC5;
}

.btn-reaction.active {
    border: 1px solid #ff52c4;
}

.btn-primary:hover {
    background-color: #ff1d9c;
    border-color: transparent;
}

.btn-primary.focus,.btn-primary:focus {
 background-color:#90285D;
 border-color:#681C43;
}

#btn-notifications {
    color: #ffe0f3;
}

.btn-primary .badge {
 color:#ff40b4;
}

.btn-group > .btn:active {
    background: #b73373;
    border: #791e4a;
}

.btn-link {
 color:#b73373;
}

.btn-primary.active,
.btn-primary:active,
.btn-primary:hover,
.open>.dropdown-toggle.btn-primary {
    background: #b73373;
    border: #791e4a;
}

.btn-primary.disabled.focus,.btn-primary.disabled:focus,.btn-primary.disabled:hover,.btn-primary[disabled].focus,.btn-primary[disabled]:focus,.btn-primary[disabled]:hover,fieldset[disabled] .btn-primary.focus,fieldset[disabled] .btn-primary:focus,fieldset[disabled] .btn-primary:hover {
 background-color:#b73380;
 border-color:#a42e79;
}

.dropdown-menu>.active>a,.dropdown-menu>.active>a:focus,.dropdown-menu>.active>a:hover {
 background-color:#b73382;
}

.btn-primary.active.focus,.btn-primary.active:focus,.btn-primary.active:hover,.btn-primary:active.focus,.btn-primary:active:focus,.btn-primary:active:hover,.open>.dropdown-toggle.btn-primary.focus,.open>.dropdown-toggle.btn-primary:focus,.open>.dropdown-toggle.btn-primary:hover {
 background-color:#90285D;
 border-color:#681C43;
}

.breadcrumb > .active {
    color: #ebb7d7;
}

.nav-pills > li.active > a, .nav-pills > li.active > a:focus, .nav-pills > li.active > a:hover {
    background-color: #FF3FC5;
}

.btn-buy {
    background: #573981;
}

.btn-muted {
    color: #E974BD;
}

.btn-muted:hover {
    color: #ff4fbd;
}

.btn-default {
    border-color: #191122;
    background: #261e2f;
}

.btn-default:hover {
    background-color: #3c2e4c;
}

.h1 .small, .h1 small, .h2 .small, .h2 small, .h3 .small, .h3 small, .h4 .small, .h4 small, .h5 .small, .h5 small, .h6 .small, .h6 small, h1 .small, h1 small, h2 .small, h2 small, h3 .small, h3 small, h4 .small, h4 small, h5 .small, h5 small, h6 .small, h6 small {
    color: #e7a9ba;
}

.progressbar {
 background-color: #36254C;
}

.progressbar-bg {
 background-color:#ff4da7;
}

.v--modal-overlay .v--modal-box {
    background: rgba(87, 57, 129, .97);
}

.form-control {
    border: 1px solid #ff75c2;
}

.form-control:focus {
    border-color: #ff6e88;
}

.text-danger {
    color: #ff3680;
}

.btn-danger:focus {
    color: #fff;
    background-color: #e02957;
    border-color: #d43a5e;
}

.btn-danger {
    background-color: #ff366f;
}

.btn-danger:hover {
    background-color: #d62154;
    transition: background 0.2s;
}

.btn-primary {
    background-color: #ff3fc5;
}

.pagination > .active > a, .pagination > .active > a:focus, .pagination > .active > a:hover, .pagination > .active > span, .pagination > .active > span:focus, .pagination > .active > span:hover {
    background-color: #dc48cb;
    border-color: #d944be;
}

.pagination > li > a, .pagination > li > span {
    color: #ff7abc;
    background-color: #302936;
    border: 1px solid #1a151e;
}

.player-name-new {
    color: #9374e9;
}

.player-name-new:hover {
    color: #8723ff;
}

.player-name-new:focus {
    color: #5e338a;
}

.text-muted {
    color: #b9859a;
}

a:focus {
    color: #7c2357;
}

.success {
    background: #eab645;
}
}
