css:
@-moz-document domain("deviantart.com") {
/* those stubborn things when editing bio */
._3-gfP,
.theme-dark .forced-theme-light .R6Mv8,
.theme-light .R6Mv8{
	color: var(--D3);
    border-color: var(--D3);
}

._34XID {
    background: var(--L1);
}

/* white boxes fix */
[data-role="menu"],
[data-role="menu"] > span,
[data-role="menu"] > a,
[role="toolbar"]:not([data-hook="inlineToolbar"]){
	background: none !important;
}

.da-editor-theme-dark [data-hook="inlineToolbar"] {
	filter: invert(100%);
}

.da-editor-theme-dark [data-hook="inlineToolbar"] > * > * > * > *:hover {
	filter: invert(100%);
}

[data-hook="top_nav"] .no-theme-skin,
[data-hook="filter_nav"] .no-theme-skin,
#site-header-more-menu {
	background: var(--L1) !important;
}

.theme-dark .forced-theme-light>div {
	color: #f2f2f2;
    background: #06070d;
}
.theme-dark .forced-theme-light .user-link {
	color: #f2f2f2;
}

[data-hook="user_avatar_tooltip"] {
	background-image: linear-gradient(180deg, hsla(0,0%,100%,0),#06070d)
}

#site-header-more-menu {
	background: #06070d !important;
}
#site-header-more-menu li a{
    color: #f2f2f2;
}

.theme-dark .forced-theme-light {
	--D3: #f2f2f2;
    --L1: #06070d;
    --L2: #161a1f;
    --L8: #f2f2f2;
}

[data-hook="top_nav"] [type="button"] {
    --L2: #06070d;
    --L2-RGB: 6,7,13;
    --L8: #f2f2f2;
    --L8-RGB: 242,242,242;
    --G4: #06070d;
}

.theme-light.basic,
[data-hook="top_nav"] .theme-light {
	--G4: #dbdbdb;
    --L2: #06070d;
    --L2-RGB: 6,7,13;
    --L1: #f2f2f2;
    --L1-RGB: 242,242,242;
	--L8: #f2f2f2;
    --L8-RGB: 242,242,242;
    --L9: #292f34;
    --D1: #b1b1b9;
    --D1-RGB: 177,177,185;
}

[data-hook="gallection_folder"] {
	--L2: #292f34;
    --L8: #f2f2f2;
}

.no-theme-skin {
	--L1: #06070d;
	--L1-RGB: 6,7,13;
    --L2: #06070d;
    --L2-RGB: 6,7,13;
    --L3: #161a1f;
	--L3-RGB: 22,26,31;
    --L4: #06070d;
    --L4-RGB: 6,7,13;
    --D1: #b1b1b9;
	--D1-RGB: 177,177,185;
    --L8: #b1b1b9;
    --8-RGB: 177,177,185;
    --L19: #06070d;
    --L19-RGB: 6,7,13;
    --AS38: url(https://st.deviantart.net/eclipse/global/svg/p13-dark-desktop.svg);
}

button[tabindex="3"],
.da-editor-theme-bright svg:not(:hover) {
	color: var(--L8) !important;
}

.ReactModalPortal,
.ReactModalPortal .no-theme-skin{
	--L2: #161A1F;
    --L3: #292f34;
    --L8: #f2f2f2;
    --L19: #161A1F;
    --D1: #f2f2f2;
}

[placeholder="Add your title here"],
[aria-label="Plugin Toolbar"]:not(:hover) {
	color: var(--L8) !important;
}


/* deviation fullview */
div.ReactModal__Overlay.ReactModal__Overlay--after-open {
	background: hsla(0,0%,10%,.9);
}
div.ReactModal__Content.ReactModal__Content--after-open>div {
	width: 100%;
    height:100%;
}
div.ReactModal__Content.ReactModal__Content--after-open img {
    margin:auto;
}

*:not(.stash-submit-page) label, body:not(.sisu) label+div>input {
	color: #fff !important;
}

/* submission */
#ile-container, #autocomplete-placebo, #output #ile-contents .bubbleview {
	background: #06070d;
    color: #f2f2f2;
}

#ile-container .ile-section-preview {
    border: 1px solid #161a1f;
	background: #161a1f;
	box-shadow: 0 0 3px #161a1f inset;
}

#ile-contents input.text,
#ile-contents .ile-description .ile-description-writer {
	background: #161a1f;
}

#ile-contents input.ile-title-input,
#ile-contents .ile-description .ile-description-writer{
    border: 1px solid #161a1f;
	box-shadow: 0 0 3px 3px #161a1f inset;
	color: #f2f2f2;
}

.ile-eclipse #ile-contents .ile-description div.writer.empty {
    background-image: none;
}

#ile-container .ile-heading, #ile-container h1,
.active .submit-tab-title,
.tags-widget.eclipse .placeholder,
.tags .tag.tag-eclipse-new *{
    color: #f2f2f2 !important;
}

#ile-contents .ile-facets-list label:not(:hover) {
    box-shadow: inset 0 0 0 1px #292f34;
}

#output > .submit-tabbar,
#not-overhead #overhead-collect,
body.no-overhead,
body {
    background: #06070d !important;
}

.submit-tab.active,
body.no-overhead #overhead,
.ile-publish-section .ile-publish-bubble,
.ile-options-popdown,
#ile-container .ile-section-options a.ile-options-button {
    background: #161a1f;
    border: 1px solid #161a1f;
    box-shadow: none;
}

#ile-contents .ec-tag-fields li.ec-tag-field select,
#ile-contents .ec-tag-fields li.ec-tag-field div.ec-tag-value,
#ile-contents .ec-tag-fields ~ input.ec-tag-value,
.tags-widget.eclipse {
	background: #161a1f;
    color: #f2f2f2;
    box-shadow: inset 0 0 0 1px #161a1f;
    border: 1px solid #161a1f; 
}
#ile-contents .ile-tags .tags-widget {
    border: 1px solid #161a1f;
	box-shadow: 0 0 3px 3px #161a1f inset;
    color: #f2f2f2;
}
.smbutton-white, .smbutton-white.disabledbutton:hover, .smbutton-white.disabledbutton:active {
    border-color: #292f34;
    background: #292f34;
	border: 1px solid #292f34;
    color: #f2f2f2;
}
.smbutton-white:hover {
    background: #393f44;
    color: #f2f2f2;
}

td.oh-ile-submit-nav-buttons .smbutton-white span,
#ile-container .ile-section-footer-buttons a.ile-save-exit-button span, #ile-container .ile-section-footer-buttons a.ile-go-back-button span, #ile-container .ile-section-footer-buttons a.ile-submit-button.smbutton-white span, #ile-container .ile-section-footer-buttons a.ile-continue-button.smbutton-white span,
.schedule-modal-buttons > .smbutton > span{
    color: #f2f2f2;
}

#ile-container .ile-section-footer-buttons a.ile-save-exit-button, #ile-container .ile-section-footer-buttons a.ile-go-back-button, #ile-container .ile-section-footer-buttons a.ile-submit-button.smbutton-white, #ile-container .ile-section-footer-buttons a.ile-continue-button.smbutton-white {
	border: 1px solid #292f34;
}
.smbutton > span {
    border-top: none;
}

#oh-stashbacktab, .stash-page #overhead-collect #overhead tbody tr td.f, #overhead tbody tr td.f #overhead-collect #overhead tbody tr td.oh-stashinactivetab,
.ile-eclipse #ile-contents .ile-title input.ile-title-input.default-text, .ile-eclipse #ile-contents .ile-description textarea.ile-description-input.default-text, .ile-eclipse #ile-contents .ile-description div.writer.empty{
	background-image: none;
}

.ile-title {
    border-bottom: 4px solid #292f34 !Important;
}
}
