:root {
    --color-background: #111;
    --color-text: #999;
    --color-primary: /*[[primary]]*/;
    --color-secondary: /*[[secondary]]*/;
    /*--color-primary: #e64415;
    --color-secondary: #ff9980;
    --color-primary: #3399ff;
    --color-secondary: #99ccff;
    --color-primary: #7ED321;
    --color-secondary: #B8E986;*/
}
body,
.relative,
div#root section,
section.widget header,
section.widget div,
div#root button,
article,
.flex,
.relative > div,
header > div,
.bg-headerLeftToRight,
.bg-sec2-lightest, .bg-primary-base, .OUTBRAIN, .bg-shade-lightest, .bg-white, .antialiased, input[type=email],
.one-newsletter {
    background-color: var(--color-background) !important;
}
.bg-leftToRight {
    background-image: none !important;
}
.bg-headerLeftToRight {
    background: none !important;
}

.bg-rightToLeft {
    display: none;
}

.text-primary-base, .RichText--dropletter p:first-of-type:first-letter, .post-vote-subtitle, .ob-rec-description:before, .fill-current, a, button[data-theme-color=true] {
    color: var(--color-primary) !important;
    border-bottom: 0px;
}
.voting circle, .PulseCircle circle, circle.userDotPulse, rect:not(.highcharts-background):not(.highcharts-plot-background):not(.highcharts-legend-box):not(.highcharts-plot-border), .RichText--iconLinks span svg path {
    fill: var(--color-primary) !important;
    stroke: var(--color-primary) !important;
}
circle.pulse-beacon, button#btnCollapse svg path, button#btnClose svg path {
    stroke: var(--color-primary) !important;
}

