.RichText a, .border-primary-base {
    border-color: var(--color-primary) !important;
    transition:
    border-color 0.5s ease-in-out 0s, 
    color 0.5s ease-in-out 0s;
}
/*.text-black .align-middle,*/
 .border-primary-base:hover, a:hover, button[data-theme-color=true]:hover {
    border-color: var(--color-secondary) !important;
    color: var(--color-secondary) !important;
    transition:
    border-color 0.5s ease-in-out 0s,
    color 0.5s ease-in-out 0s;
}
.hover\:opacity-moderate, .focus\:opacity-moderate {
    opacity: 1.0 !important;
}
.text-black, .tik3-event-item-meta-timestamp, .stroke-current, .poll-title, .text-sm, section.widget header h1 {
    color: #fff !important;
}
.OffsetContainer > div.relative > div.relative {
    border-radius: 30px 30px;
    box-shadow: 0 10px 25px 10px var(--color-primary);
    margin-top: 20px;
    padding-bottom: 30px;
}
.RichText, h3, h2 > a, li > a, section > span, .ml-8, .font-extrabold > span, .ob-unit, .tik3-event-item, .bg-sec2-lightest p {
    font-family: verdana !important;
    color: var(--color-text) !important;
    width: 100% !important;
    font-size: 20px !important;
}
.at-element-marker p, .answers-section, h1 span.chart-title, .match, table tbody tr td, .legende .label, .LiveInfo, .antialiased, .timer, button[data-theme-color=true] div, span.talk-plugin-comment-content-line, .talk-plugin-infobox-info {
    color: var(--color-text) !important;
}
.font-slabcdUI, .font-serifUI, .italic {
    font-family: verdana !important;
}
.bg-whitePaywall {
    background-image: linear-gradient(turn, #fff 3.31%, hsla(0, 0%, 100%, 0));
}
.jw-display-icon-container, .oneListFormButton, .rangeslider__fill, .bg-attention {
    background-color: var(--color-primary) !important;
}

.tik3-ticker .tickaroo-web-embed-url-title, .tik3-ticker .tickaroo-web-embed-url-description, .tik3-ticker .tickaroo-web-embed-url-link {
    color: var(--color-primary) !important;
}

.one-newsletter, input[type=email] {
    border: 1px solid var(--color-primary);
}

/*anoying CPU burning advertisement*/
div[data-advertisement] {
    display: none !important;
}


section.clear-both {
    transform: scale(0.3);
    height: 110px;
    overflow: scroll;
	transition: all .5s ease-in-out;
}

section.clear-both:hover {
    transform: scale(1.0);
    height: 700px;
    overflow: scroll;
    transition: all .5s ease-in-out;
}
}
