css:
@-moz-document domain("bilibili.com") {

    /* 主页--起 */
    .b-wrap {
        width: 76.5% !important;
        max-width: 2400px !important;
    }

    .gg-carousel {
        width: 650px !important;
        height: 286px !important;
        margin-top: 36px !important;
    }

    /* 轮播图 */
    .focus-carousel.home-slide,
    .van-slide.ggc {
        width: 655.45px !important;
        height: 288.4px !important;
    }

    /* 推荐盒子 */
    .recommend-box {
        width: 1276px !important;
        height: 288.4px !important;
    }

    /* 推荐盒子广告 */
    .video-card-reco {
        width: 247.2px !important;
        height: 139.2px !important;
    }

    .video-card-reco .info-box .info {
        padding-top: 30px !important;
    }

    /* 推广 */
    .extension,
    /* 漫画 */
    .manga-panel {
        width: 1607.5px !important;
    }

    .manga-list-box {
        width: auto !important;
    }

    .manga-card {
        margin: 0 20px !important;
    }

    /* 分类主体 */
    .zone-list-box {
        width: 1607.5px !important;
        height: 448px !important;
    }

    /* 番剧 */
    .time-line>.zone-list-box {
        height: 376px !important;
    }

    /* 直播 */
    .live-card,
    .video-card-common {
        width: 257.5px !important;
    }

    /* 直播 */
    .live-card .up img {
        width: 64px !important;
        height: 64px !important;
    }

    /* 直播 */
    .live-card .up .txt {
        padding-left: 16px !important;
        width: 193.5px !important;
    }

    /* 直播 */
    .live-card>a>.pic,
    .video-card-common>.card-pic,
    .video-card-common>.match-card-pic {
        height: 145px !important;
    }

    /* 页面内广告 */
    .banner-card.b-wrap {
        display: none !important;
    }

    /* 底部特别推荐 */
    .space-between.s-rec.b-wrap {
        width: auto !important;
    }

    /* 右侧悬浮导航 */
    .elevator {
        left: 58% !important;
    }

    /* 主页--止 */

    /* 播放页面--起 */
    .v-wrap,
    .main-container {
        width: 90% !important;
        max-width: 2400px !important;
        margin-top: 10px !important;
    }

    #bilibili-player{
        width:1915.390px !important;
        height:1150px !important;
    }

    .v-wrap .l-con {
        width: 85% !important;
    }

    .main-container .plp-l {
        width: auto !important;
    }

    .v-wrap .l-con .player-wrap,
    .main-container .plp-l .player-module {
        height: auto !important;
    }

    /* 解决会员专属视频非会员无高度的问题 */
    .player-module {
        min-height: 766px;
    }

    .v-wrap .l-con #bofqi,
    .main-container .plp-l #bofqi {
        width: 100% !important;
        height: auto !important;
    }

    .bilibili-player-area {
        height: auto !important;
        max-height: 1150px !important;
    }

    .bilibili-player.mode-miniscreen.bilibili-player .bilibili-player-area .bilibili-player-video-wrap .bilibili-player-video-danmaku div,
    .bilibili-player.mode-miniscreen[class*=mode-] .bilibili-player-area .bilibili-player-video-wrap .bilibili-player-video-danmaku div {
        font-size: 26px !important;
    }

    /* 弹幕栏 */
    .bilibili-player-video-bottom-area {
        height: 46px !important;
    }

    /* 弹幕栏 */
    .bilibili-player-video-sendbar,
    /* 进度条 */
    .bilibili-player-video-control {
        display: flex !important;
    }

    .v-wrap .r-con {
        width: 15% !important;
    }

    /* 全屏和网页全屏状态时的播放窗口 */
    .bilibili-player.mode-fullscreen .bilibili-player-area,
    .bilibili-player.mode-webfullscreen .bilibili-player-area {
        height: auto !important;
        max-height: 9999px !important;
    }

    .bilibili-player.mode-fullscreen .bilibili-player-video-bottom-area,
    .bilibili-player.mode-webfullscreen .bilibili-player-video-bottom-area {
        height: 0 !important;
    }

    /* 播放页面--止 */

}

@-moz-document domain("live.bilibili.com") {
    .app-body {
        max-width: 2400px !important;
    }

    .room-bg {
        max-width: 2560px !important;
    }
}
