@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netafull.net") {
  body {
    background-color: white!important;
  }
  div#WRAPPER {
    margin-top: -140px;
  }
  #AREA-CENTER,
  #AREA-RIGHT,
  #FOOTER-LEFT,
  #FOOTER-CENTER,
  #FOOTER-RIGHT,
  #HEADER-AMN,
  #HEADER-MOREENTRY,
  #AREA-LEFT-FOOTER-MOREENTRY,
  #banner div.right,
  #navi div,
  #sub,
  #right,
  #footer {
    display: none!important;
    width: 0px!important;
  }
  #adsense,
  #adsense1,
  #adsense2,
  #adsense3,
  #adsense4,
  #ADSENCE6,
  #ADSENCE3 {
    display: none!important;
    width: 0px!important;
  }
  div#navi {
    height: 0px!important;
    border-top: 0px!important;
  }
  div#AREA-LEFT,
  div#wrapper,
  div#center,
  div#center div.content {
    width: 840px!important;
  }
  div#AREA-LEFT blockquote {
    width: 740px!important;
  }
}
